@-moz-document domain("duckduckgo.com") {
/* ANIMATIONS */

/* BACKGROUND */

#content_wrapper {
  
  /* FALLBACK */
  background-image : url("http://img820.imageshack.us/img820/1793/nuage3.png"),  url("http://img827.imageshack.us/img827/9818/dgradbleu.png") !important;
  
  /* DEFAUT */
  background-image: url("http://img820.imageshack.us/img820/1793/nuage3.png"), url("http://img820.imageshack.us/img820/1793/nuage3.png"), url("http://img820.imageshack.us/img820/1793/nuage3.png"), linear-gradient(top, #453d80 0%, #a9a3e4 50%) !important;
  
  /* CHROME/SAFARI */
  background-image: url("http://img820.imageshack.us/img820/1793/nuage3.png"), url("http://img820.imageshack.us/img820/1793/nuage3.png"), url("http://img820.imageshack.us/img820/1793/nuage3.png"), -webkit-linear-gradient(top, #453d80 0%, #a9a3e4 50%) !important;
  background-image: url("http://img820.imageshack.us/img820/1793/nuage3.png"), url("http://img820.imageshack.us/img820/1793/nuage3.png"), url("http://img820.imageshack.us/img820/1793/nuage3.png"), -webkit-gradient(linear, 0% 0%, 0% 50%, from(#453d80), to(#a9a3e4)) !important;
  
  /* FF */
  background-image: url("http://img820.imageshack.us/img820/1793/nuage3.png"), url("http://img820.imageshack.us/img820/1793/nuage3.png"), url("http://img820.imageshack.us/img820/1793/nuage3.png"), -moz-linear-gradient(top, #453d80 0%, #a9a3e4 50%) !important;
  
  /* OPERA */
  background-image: url("http://img820.imageshack.us/img820/1793/nuage3.png"), url("http://img820.imageshack.us/img820/1793/nuage3.png"), url("http://img820.imageshack.us/img820/1793/nuage3.png"), -o-linear-gradient(top, #453d80 0%, #a9a3e4 50%) !important;
  
  
  background-repeat : repeat-x, no-repeat, no-repeat, no-repeat !important;
  background-color : #aaa3e5 !important;
  background-position : 200px 120px, 10% 250px, 70% 40px, 0px 55px;
  background-attachment : fixed !important;
  
  /*[[bgAnimation]]*/
}


/* HEADER */

div#header {
  /* DEFAUT */
  background-image: linear-gradient(top, rgb(219,0,47) 0%, rgb(255,153,28) 15%, rgb(248,255,56) 33%, rgb(84,255,90) 44%, rgb(117,255,237) 57%, rgb(95,84,247) 77%, rgb(244,25,255) 94%) !important;
  /* OPERA */
  background-image: -o-linear-gradient(top, rgb(219,0,47) 0%, rgb(255,153,28) 15%, rgb(248,255,56) 33%, rgb(84,255,90) 44%, rgb(117,255,237) 57%, rgb(95,84,247) 77%, rgb(244,25,255) 94%) !important;
  
  /* FF */
  background-image: -moz-linear-gradient(top, rgb(219,0,47) 0%, rgb(255,153,28) 15%, rgb(248,255,56) 33%, rgb(84,255,90) 44%, rgb(117,255,237) 57%, rgb(95,84,247) 77%, rgb(244,25,255) 94%) !important;
  
  /* CHROME-SAFARI */
  background-image: -webkit-linear-gradient(top, rgb(219,0,47) 0%, rgb(255,153,28) 15%, rgb(248,255,56) 33%, rgb(84,255,90) 44%, rgb(117,255,237) 57%, rgb(95,84,247) 77%, rgb(244,25,255) 94%) !important;
  background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0,rgb(219,0,47)),color-stop(0.15, rgb(255,153,28)),color-stop(0.33, rgb(248,255,56)),color-stop(0.44, rgb(84,255,90)),color-stop(0.57, rgb(117,255,237)), color-stop(0.77, rgb(95,84,247)), color-stop(0.94, rgb(244,25,255))
) !important;
  
  /* IE (lol) */
  background-image: -ms-linear-gradient(top, rgb(219,0,47) 0%, rgb(255,153,28) 15%, rgb(248,255,56) 33%, rgb(84,255,90) 44%, rgb(117,255,237) 57%, rgb(95,84,247) 77%, rgb(244,25,255) 94%) !important;

}

#search_form, #header_button_wrapper {
  border : none !important;
  
  border-radius : 5px !important;
}
 
/* LINKS */
a { color : #339; }

div#links > div.results_links_deep, div#links > div#did_you_mean {
  border-radius : 5px !important;
  border : 2px dotted #000 !important;
  padding-right : 2px !important;
  margin-bottom : 10px !important;
  
  transition: background-color 1s !important;
  -webkit-transition: background-color 1s !important;
  -moz-transition: background-color 1s !important;
  -o-transition: background-color 1s !important;
}

div#links > div:not(#content_internal).highlight {
  border : 2px solid #000 !important;
}

/* RED */
div#links > div:not(#content_internal):nth-child(7n+1) {
  background-color : rgba(220, 80, 80, 0.7) !important;
}

div#links > div:not(#content_internal):nth-child(7n+1).highlight {
  background-color : rgba(220, 80, 80, 1) !important;
}

/* ORANGE */
div#links > div:not(#content_internal):nth-child(7n+2) {
  background-color : rgba(220, 120, 80, 0.7) !important;
}

div#links > div:not(#content_internal):nth-child(7n+2).highlight {
  background-color : rgba(220, 120, 80, 1) !important;
}

/* YELLOW */
div#links > div:not(#content_internal):nth-child(7n+3) {
  background-color : rgba(220, 220, 120, 0.7) !important;
}

div#links > div:not(#content_internal):nth-child(7n+3).highlight {
  background-color : rgba(220, 220, 160, 1) !important;
}

/* GREEN */
div#links > div:not(#content_internal):nth-child(7n+4) {
  background-color : rgba(160, 220, 160, 0.7) !important;
}

div#links > div:not(#content_internal):nth-child(7n+4).highlight {
  background-color : rgba(160, 220, 160, 1) !important;
}

/* LIGHT BLUE */
div#links > div:not(#content_internal):nth-child(7n+5) {
  background-color : rgba(120, 220, 220, 0.7) !important;
}

div#links > div:not(#content_internal):nth-child(7n+5).highlight {
  background-color : rgba(120, 220, 220, 1) !important;
}


/* DARK BLUE */
div#links > div:not(#content_internal):nth-child(7n+6) {
  background-color : rgba(140, 140, 220, 0.7) !important;
}

div#links > div:not(#content_internal):nth-child(7n+6).highlight {
  background-color : rgba(140, 140, 220, 1) !important;
}

/* VIOLET */
div#links > div:not(#content_internal):nth-child(7n+7) {
  background-color : rgba(220, 140, 220, 0.7) !important;
}

div#links > div:not(#content_internal):nth-child(7n+7).highlight {
  background-color : rgba(220, 140, 220, 1) !important;
}


/* BOXES */

#side {
  width : 175px !important;
  border-radius : 5px !important;
  padding : 2px !important;
  background-color : rgba(255,255,255, 0.6) !important;
  
  transition: background-color 1s !important;
  -webkit-transition: background-color 1s !important;
  -moz-transition: background-color 1s !important;
  -o-transition: background-color 1s !important;
}

#side div.button {
  opacity : 0.7;
}

#side:hover div.button {
  opacity : 1;
}

#zero_click {
  background-color :rgba(255,255,255, 0.6) !important;
  
  transition: background-color 1s !important;
  -webkit-transition: background-color 1s !important;
  -moz-transition: background-color 1s !important;
  -o-transition: background-color 1s !important;
}

#side:hover, #zero_click:hover {
  background-color : rgba(255,255,255, 0.9) !important;
}
}
