.fbDockChatTabFlyout .fbNubFlyoutBody {
background-color: #fff!important;
}
._50kd ._kso:after,._50dw .profileLink:after {
display:none!important
}
._50dw .profileLink .profilePhoto {
border-radius:100%!important;
}
._50kd ._kso {
color:#222!important;
background-color: #E9E9E9!important;
background-image: none!important;
}
._kso:after, ._kso:before {
background-color: transparent!important;
clip: initial!important;
margin-top: -18px;
top: 100%!important;
width: 0px!important;
height: 0px!important;
}
._kso:after {
display:none !important;
}
._50kd ._kso:before {
border-style: solid!important;
border-width:5px 8px 5px 0!important;
border-color: transparent #e9e9e9 transparent transparent!important;
}
._kso {
font-family: 'Droid Sans', Arial, sans-serif!important;
font-size: 12px!important;
padding: 6px 5px 4px 5px!important;
text-shadow: none!important;
color: #fff!important;
background: #0084ff!important;
border: 0!important;
-webkit-border-radius: 3px!important;
border-radius: 3px!important;
-webkit-box-shadow: none!important;
}
._kso:before {
border-style: solid!important;
border-width: 5px 0 5px 8px !important;
border-color: transparent transparent transparent #0084ff!important;
}
._kso:before {
background: transparent!important;
}
._ksh {
-webkit-border-radius: 0!important;
border-radius: 0!important;
}
._ksh::after {
border: 0!important;
}
div._511n {
background-color: #fff!important;
color: #999ca5!important;
font-size: 10px!important;
font-weight: normal!important;
}
._511m {
background-image: none!important;
background: #e9e9e9!important;
height: 1px!important;
margin: 16px 0 15px 5px!important;
}
._50dw .profileLink {
   top: auto!important;
  bottom: 0px!important;
margin-bottom: 5px!important;
}
}
