additional_info:
/*源scss*/



$background: 'https://lh6.googleusercontent.com/-KJ3twXWwsWM/UNBgXgTaBbI/AAAAAAAAT18/AwkZ-W-6Myk/s0/%E7%94%9F%E5%BE%92%E4%BC%9A%E5%BD%B9%E5%93%A1%E5%85%B1+%E7%AC%AC%E5%8D%81%E4%B8%89%E8%A9%B1+10%2757%27%27.png';

body {
    background: url($background) !important; 
    background-position:50% 50% !important;
    background-repeat:no-repeat !important;
    background-attachment:fixed !important;
    background-color: transparent !important;
}

#header {
	#header-inner {
		background-color: rgba(247,245,245,0.5) !important;

		#divider-header-bottom {
			display: none !important;
		}

		#block-tool-footer {
			background-color: rgba(255,255,255,0.5) !important;
		}
	}
}

#mainer {
	#mainer-inner {

		background-color: rgba(247,245,245,0.6) !important;

		#area-title {
			padding: 5px 5px 0 5px !important;

			#subtitle-article {
				width: 980px !important;
			}

			#area-tool {
				display: none !important;
			}
		}

		#area-player {
			padding: 0 8px !important;
			font-size: 17px !important;

		  p, div, span {
			  font-size: 17px !important;
		  }

		  div {
		      margin: 2px 8px !important;
		      background-color: rgba(255,255,255,0.5) !important;
		      padding: 5px !important;
		  }

		  p, span {
		  	background-color: transparent !important;
		  }
		}

		#area-desc, #area-tag {
			display: none !important;
		}

		/*other*/
		#area-video-other {
			display: none !important;
		}

		/*share*/
		#share-article {
			display: none !important;
		}

		#area-article-bottom {

			.area-right {
				#block-author-card {
					background-color: rgba(255,255,255,0.5) !important;
				}
			}

			#area-editor, .alert.alert-warning {
			    display: none !important;
			}

			#area-comment {



				background-color: transparent !important;

				.banner {
					background-color: rgba(250,250,250,0.5) !important;
				}

				#area-comment-inner {
					.item-comment-divider {
						background-color: transparent !important;
					}

					.item-comment {
						border-bottom-color: transparent !important;
					}

					.item-comment-quote {

						border-color: rgba(221,221,221,0.2) !important;
						border-top-color: rgba(235,235,235,0.2) !important;
						border-bottom-color: rgba(183,183,183,0.2) !important;

						background-color: rgba(255,255,254,0.2) !important;

						/*&>.item-comment-quote {
							background-color: transparent !important;
						}*/
					}

					.item-comment-quote:hover {
						background-color: rgba(255,255,254,0.4) !important;
					}

					.item-quote-hidden {
						background-color: rgba(250,250,250,0.5) !important;
					}

					.area-comment-left {
						.thumb {
							background-color: transparent !important;

							border-color: rgba(221,221,221,0.5) !important;
							border-top-color: rgba(235,235,235,0.5) !important;
							border-bottom-color: rgba(183,183,183,0.5) !important;
						}
					}
					.area-comment-right {
						.author-comment.last {
							background-color: rgba(250,250,250,0.5) !important;
						}

						.content-comment {
							font-size: 15px !important;
						}
					}
				}
			}
		}


		/*home*/
		#area-mainer-left {
			#block-channel-content {
				/*background-color: transparent !important;

				.banner {
					border-top-color: rgba(255,255,255,0.5) !important;
					border-bottom-color: rgba(253,253,253,0.5) !important;
					background-color: rgba(250,250,250,0.5) !important;
				}

				.mainer.th-list {
					.item {
						border-bottom-color: rgba(253,253,253,0.5) !important;
					}
				}*/
			}
		}

		/*map*/
		#block-map {
			background-color: rgba(255,255,255,0.2);
		}


	}
}


#block-guide-footer {
	display: none !important;
}



#footer {
	background-color: rgba(3,3,3,0.5) !important;
	border-top-color: rgba(5,5,5,0.5) !important;

	#footer-inner {
		padding: 10px 0 !important;

		#info-footer-bottom {
			#info-footer-alert {
				display: none !important;
			}
		}
	}
}



/*ads*/
.ad.ad-top, #ad-comment, .ad.ad-bottom {
	display: none !important;
}
















/* Track & scroll thickness */
::-webkit-scrollbar { 
	width: 14px !important; 
}

/* Track color */
::-webkit-scrollbar-track { 
	background-color:#ddd !important;
}

/* Scroll color */
::-webkit-scrollbar-thumb { 
	background-color: rgba(0,0,0,.2) !important;
	min-height: 50px !important;
}

/* Scroll hover color */
::-webkit-scrollbar-thumb:hover { 
	background-color: #333 !important;
}

/* Scroll click & drag color */
::-webkit-scrollbar-thumb:active { 
	background-color: #333 !important;
}

