css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tenki.jp") {
#bd{margin-top:-65px}
#hd{position:absolute;
top:4px;
min-height: 0px!important;
margin-top: 0px!important;
margin-bottom: 0px!important;

}

#menu ul#menu-main li a{
padding-top: 0.01em!important;
padding-bottom: 0.1em!important;
}
#menu ul#menu-main li{
height: 1.3em!important;
}
/*トップバー調整*/
#login,
#help-btn,
#hd-search UL,
IMG[src*="/images/header/logo/"],
#delimiter,
/*メインコンテンツ調整*/
DIV#container:nth-child(1)>DIV.clearfix:nth-child(4),

#introductionBox,
.contentsBoxLong .wrap,
.titleBorder,

.google_mainpage_ppc_bottom,
#tripadvisor_module,

.wideBtn{display:none!important;}



#masthead,.social_buttons_wrap{display:none!important;}
/*フッター調整*/
#ft,
#ft-contents-menu-publicdata,
#ft-contents-menu-cgm,
#ft-contents-home,
#ft-contents-top-link
{display:none!important;}

#ft-contents-menu-forecast,
#ft-contents-menu-disaster,
#ft-contents-menu-weatherguide,
#ft-contents-menu-leisure,
#ft-contents-menu-indexes{width: 150px!important;}



#search-box{
top:0px!important;
border:0px!important;
background:none!important;
}

/*高さ調整*/
#menu{padding-top:0px!important;}
body{margin-top:-4px!important;}


/*サイドバー調整*/
#showcaseRecommend,.ad-large-lectangle
{display:none!important;}
#bd-sub{margin-top: -10px!important;}

/*色調統一*/
#container.yui-t6,.wrap,#ft-contents-menu,#weatherGuideTabs{
background-image:none!important;
background-color: #edede1!important;
}
#townLeftOneBox{background-color: white!important;}


/*雨雲レーダーサイズ調整*/
#guideFlash02{height:485px!important;}
}


@-moz-document 
url-prefix("http://bousai.tenki.jp/"),
url-prefix("http://season.tenki.jp/"){
#hd{top:20px;
left:559px;}

#menu-sub1{margin-top: 13px!important;}
#menu-main{margin-top: 0px!important;}
#earthquake_reload,#wave_reload,
.earthquake_top_social_buttons,
#ft-contents-menu,.more_link_right_wrap{display:none!important;}
#bd-sub{margin-top:0px!important;}


}
