@-moz-document domain("alfalfalfa.com") {
* {font-family:inherit !important;}
body {
  font-size:14px !important;
  background-color: #f5f5db !important;
}
div.blogbody {
  padding:30px;
  border:0;
}
#blogcontainer>*{display:none !important;}
		#blogcontainer>#banner{display:block !important;}

		.google-user-ad,
		#leftbody>.sidewrapper:nth-of-type(4),
		#leftbody>.sidewrapper:nth-of-type(8),
		#leftbody>.sidewrapper:nth-of-type(9),
		#leftbody>.sidewrapper:nth-of-type(10),
		#leftbody>.sidewrapper:nth-of-type(11),
		#leftbody>.sidewrapper:nth-of-type(12),
		#leftbody>.sidewrapper:nth-of-type(13),
		#leftbody>a,
		#leftbody>dl{
			display:none !important;
		}
#left,
.container_r,
#blogcontainer,
#fixfooter,
div.description,
.social_buttons,
.comment,
iframe,
.fix_ent_ad_button,
.posted4
{
  			display:none !important;
}

#content {
  float:none !important;
}
#container,
.posted4,
#content,
#wrapper,
div.right_first,
div.article_list,
body {
   width:100% !important;
}
div.right_first {
  float:none !important;
}
}

@-moz-document url("http://alfalfalfa.com/") {
#blog>*{display:none !important;}
		#blog>.content{display:block !important;}
}

@-moz-document url-prefix("http://alfalfalfa.com/archives/") {
#blog>*{display:none !important;}
		#blog>#articlebody{display:block !important;}

		#articlebody>.blogbody>*{display:none !important;}
		#articlebody>.blogbody>.article-body{display:block !important;}

		#content>*{display:none !important;}
		#content>#blog{display:block !important;}

		#articlebody>.blogbody>.article-body a[href^="http://www.amazon.co.jp"]{display:none !important;}
}

