css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("02ch.net") {
/* GLOBAL */
html, body {
    background: #fff !important;
    margin: 0 !important;
    padding: 0 !important;
    font-family: Verdana, sans !important;
    font-size: 17px !important;
    color: #40454b !important;
}
a:link, a:visited {
    color: #3366cc !important;
    text-decoration: underline !important;
}
a:hover {
    color: #ff5a00 !important;
    text-decoration: none !important;
}
table {
    border-style: none !important;
}
table td {
    border-style: none !important;
}

/* HEADER */
.adminbar {
    float: none !important;
    margin-left: auto !important; 
    margin-right: auto !important;
    text-align: center !important;
    z-index: 1 !important;
    position: relative !important;
    width: 678px !important;
    font-size: 0.6em !important;
    font-weight: normal !important;
    background: #eaf4ff !important;
    text-shadow: 1px 1px 2px #ddd !important;
    border-left: 1px solid #ddd !important;
    border-right: 1px solid #ddd !important;
    border-bottom: 1px solid #ddd !important;
    -webkit-border-radius: 0 0 5px 5px !important;
    border-radius: 0 0 5px 5px !important;
}
.adminbar a {
    font-weight: normal !important;
}
.logo {
    z-index: 1 !important;
    position: relative !important;
    width: 678px !important;
    height: 120px !important;
    margin: 0 auto !important;
    background: url('data:image/png;base64,') bottom center no-repeat !important; 
    clear:both !important;
    text-align:left !important;
    line-height: 110px !important;
    font-size:1em !important;
    font-weight: bold !important;
    color:#FF6600 !important;
}

/* POSTFORM */
.postarea, div[id="de-parea"] {
    z-index: 1 !important;
    background: #fff !important;
    position: relative !important;
    width: 100% !important;
    width: 678px !important;
    padding-bottom: 30px !important;
    border: 1px solid #ddd !important;
    -webkit-border-radius: 5px !important;
    border-radius: 5px !important;
    margin: 0 auto !important;
}
#de-qarea {
    z-index: 1 !important;
    background: #fff !important;
    position: absolute !important;
    width: 678px !important;
    padding-bottom: 30px !important;
    border: 1px solid #ddd !important;
    -webkit-border-radius: 5px !important;
    border-radius: 5px !important;
    margin: 0 auto !important;
    font-size: 1.3em !important;
}
#postform {
    width: 560px !important;
    margin: 10px auto 5px auto !important;
    padding: 10px !important;
    border: 1px solid #ccc !important;
    -webkit-border-radius: 5px !important;
    border-radius: 5px !important;
    font-size: 0.7em !important;
}
textarea {
    clear: both !important;
    width: 100% !important;
    margin: 0px 0 6px 0 !important;
    border: 1px solid #999 !important;
    background: none !important;
}
input {
    background: none !important;
    border: 1px solid #999 !important;
}
.postblock {
    background:transparent !important;
    color:#002244 !important;
    font-weight:bold !important;
}
.captcha {
    color:#404040;
}

/* THREAD */
form[action$="wakaba.pl"] {
    width: 678px;
    padding-bottom: 40px;
    border: 1px solid #ddd;
    border-radius: 5px;
    margin: 0 auto;
    font-size: 0.7em;
}
form[action$="wakaba.pl"] table {
    position: relative !important;
    top: 10px;
    width: 560px !important;
    margin: 0 auto !important;
}
.omittedposts {
    float: left !important;
    display: block !important;
    margin: 0 5px 0 5px !important; 
    position: relative !important;
    background: #ffffff !important;
    padding: 3px 10px 4px 10px !important;
    border: 1px solid #ddd !important;
    -webkit-border-radius: 5px !important;
    border-radius: 5px !important;
    top: 5px !important;
    color: #aaa !important;
}

/* IMAGE */
.image, .nothumb { 
    float: left !important;
    margin: 3px !important;
    margin-left: 59px !important;
    padding-right: 5px !important;
}
table .image, table .nothumb { 
    float: left !important;
    margin: 3px !important;
    padding-right: 5px !important;
}
.nothumb {
    background-color: #FFFFFF !important;
    border-style: dotted !important;
    margin: .3em .5em !important;
}

/* POST INFO */
.filetitle {
    background:inherit !important;
    font-size:1.2em !important;
    color:#002244 !important;
    font-weight:bold !important;
}
.reflink {
    font-size: .8em !important;
    font-weight: normal !important;
}
.abbrev {
    color:#666666 !important;
}

/* POST */
.reply {
    background: #EAF4FF !important;
    border-style: none !important;
}
form div.reply, td[class="reply de-pview"] {
    display: block;
    position:absolute !important; 
    z-index:950 !important; 
    border-radius: 8px !important;
    border-style: none !important;
    resize: none;
    transform-origin: 0px 0px;
    -moz-box-sizing: content-box !important;
    background-attachment: scroll !important;
    background-clip: border-box !important;
    background-color: #E3EDFE !important;
    background-origin: padding-box !important;
    background-position: 0% 0% !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    box-shadow: 0px 0px 10px #666666 !important;
}

td.reply {
    width: 560px !important;
    margin: 2px auto !important;
    padding: 5px 10px 0 10px !important;
    background: #EAF4FF !important;
    border-radius: 8px !important;
}
.reply blockquote {
    margin: .5em !important;
}
blockquote {
    margin: .5em .5em .5em 1em !important;
}
div[class="watchers watchers-fixed"] + blockquote {
    margin-left: 59px !important;
    left: 59px;
    padding-left: 59px;
}
.highlight {
    background: #EAF4FF !important;
    border: 1px solid #bbb !important;
    border-radius: 8px !important;
}
.unkfunc {
    padding: 2px 0 !important;
    margin: 0 0 0 10px !important;
    font-style: italic !important;
    color: #789922 !important;
}
.underline {
    text-decoration: underline !important;
} 
.red {
    color:#ff0000 !important;
}
.oldpost, .notabene {
    color:#CC1105 !important;
    font-weight:bold !important;
}

/* SPOILER */
.spoiler {
    background: #ddd !important;
    color: #ddd !important;
}
.spoiler blockquote {
    background: #ddd !important;
    color: #ddd !important;
}
.spoiler:hover {
    color: #40454b !important;
}
.spoiler blockquote:hover {
    color:#789922 !important;
}
.spoiler blockquote blockquote:hover {
    color:#406010 !important;
}
.spoiler blockquote blockquote blockquote:hover {
    color:#204010 !important;
}

/* HIDDEN */
.theader, .passvalid, .rules, .doubledash, dl.menu dd.hidden, .filesize, hr, .postername, .postertrip, .replytitle, .commentpostername, .thumbnailmsg, .userdelete br, .watchers-relative img {
    display: none !important;
}

/* FOOTER */
table[border="1"] {
    margin-left: auto !important; 
    margin-right: auto !important;
    border: 1px solid #ddd !important;
    border-radius: 5px !important;
    top: 5px !important;
    width: 610px !important;
    height: 25px !important;
    position: relative !important;
    text-shadow: 1px 1px 2px #ddd !important;
    text-align: center !important;
    font-size: 0.7em !important;
    word-spacing: 20px !important;
}
.footer {
    font-family: inherit !important;
    font-size: inherit !important;
    text-align:center !important;
    margin: 1em 0 0 0 !important;
    color: #999 !important;
    position: relative !important;
    height: 265px !important;
    padding-top: 10px !important;
}
.footer a {
    color: #999 !important;
}

/* WATCHERS */
.watchers {
    margin: 0 !important;
    padding: 0 !important;
    text-align:center !important;
    font-size: .8em !important;
    font-weight: bold !important;
    color: #FF6600 !important;
}
.watchers-board {
    margin: 0 !important;
    padding: 0 !important;
    text-align:center !important;
    font-size: .8em !important;
    font-weight: bold !important;
    color: #FF6600 !important;
}
.watchers-relative {
    position:relative !important;
    float:right !important;
    top:0 !important;
    right:0 !important;
}
.watchers-fixed {
    position:fixed !important;
    float:right !important;
    bottom:40px !important;
    right:10px !important;
}

/* FRAME */
h1 { 
    width: 170px !important;
    float: right !important;
    clear: both !important;
    border-bottom: 1px solid #bbb !important;
    text-align: center !important;
    font-weight: normal !important;
    font-size: 1.2em !important;
    padding: 50px 0 11px 0 !important;
    color: #999 !important;
}
hr+h1 { 
    width: 100% !important;
    float: none !important;
    clear: both !important;
    border: none !important;
    text-align: center !important;
    font: normal normal 700 34px/normal Verdana !important;
    padding: 0px !important;
    color: #40454B !important;
}
h1 a[target="main"] {
    color: #999999 !important;
    text-decoration: none !important;
    cursor: default !important;
}
.menu {
    clear: right !important;
    width: 170px !important;
    float: right !important;
    min-height: 70px !important;
    font-size: 0.7em !important;
    text-align: center !important;
}
dl.menu dt {
    padding: 8px 0 !important;
    border-style: none !important;
    border-radius: 12px 0 0 12px !important;
    background: #EAF4FF !important;
    color: #3366cc !important;
    text-decoration: underline !important;
}
dl.menu dd {
    padding: 8px 0 !important;
    border-radius: 12px 0 0 12px !important;
    border-style: none !important;
    margin: 0 !important;
}
dl.menu dd:hover {
    background: #f2f2f2 !important;
}
center img { 
    margin-top: 20px !important;
}

/* ??? */
.managehead {
    background: #EAF4FF !important;
    color:#002244;
    padding:0px;
}
.row1 {
    background: #EAF4FF !important;
}

/* DOLLSCRIPT BUTTONS */
#de-btn-logo { background: url('data:image/png;base64,') no-repeat center !important; }
#de-btn-settings { background: url('data:image/png;base64,') no-repeat center !important; }
#de-btn-hidden { background: url('data:image/gif;base64,R0lGODlhEAAQALMOAP8zAMopAJMAAP/M//+DIP8pAP86Av9MDP9sFP9zHv9aC/9gFf9+HJsAAP///wAAACH5BAEAAA4ALAAAAAAQABAAAARU0MlJKw3B4hrGyFP3hQNBjE5nooLJMF/3msIkJAmCeDpeU4LFQkFUCH8VwWHJRHIM0CiIMwBYryhS4XotZDuFLUAg6LLC1l/5imykgW+gU0K22C0RADs=') no-repeat center !important; }
#de-btn-refresh { background: url('data:image/png;base64,') no-repeat center !important; }
#de-btn-goup { background: url('data:image/gif;base64,R0lGODlhCQALAIQQAOpiOoXgYfWShsXwlIHaXuZfN+dgOITeYILbX+lhOdhXMHXGVi2HCbKysqkMAZzLy////////////////////////////////////////////////////////////////yH+EUNyZWF0ZWQgd2l0aCBHSU1QACH5BAEAAA8ALAAAAAAJAAsAAAUZ4CM+zWiW5TmmpNmyZyOvcyvCLL6qdyynIQA7') no-repeat center !important; }
#de-btn-expimg { background: url('data:image/png;base64,') no-repeat center !important; }
#de-btn-maskimg { background: url('data:image/png;base64,') no-repeat center !important; }
#de-btn-favor { background: url('data:image/png;base64,') no-repeat center !important; }

.de-btn-fav-sel { background: url('data:image/png;base64,') no-repeat center !important; }
.de-btn-fav { background: url('data:image/png;base64,') no-repeat center !important; }
#de-btn-godown, .de-btn-sage { background: url('data:image/gif;base64,R0lGODlhCQALAIQQAOpiOoXgYfWShsXwlIHaXuZfN+dgOITeYILbX+lhOdhXMHXGVi2HCbKysqkMAZzLy////////////////////////////////////////////////////////////////yH+EUNyZWF0ZWQgd2l0aCBHSU1QACH5BAEAAA8ALAAAAAAJAAsAAAUZ4PM0JCmKzZmqbDueL4qaMw2/q5vnMu8+IQA7') no-repeat center !important; }
#de-btn-newthr, .de-btn-rep { background: url('data:image/png;base64,') no-repeat center !important; }
.de-btn-hide-user, .de-btn-hide { background: url('data:image/png;base64,') no-repeat center !important; }
.de-post-hid .de-btn-hide-user, .de-post-hid .de-btn-hide { background: url('data:image/png;base64,') no-repeat center !important; }
.de-btn-expthr { background: url('data:image/png;base64,') no-repeat center !important; }

#de-main TEXTAREA, #de-main INPUT {
    background: #FFFFFF !important;
}
}
