css:
/* @name forest.impress.co.jp 窓の杜 ja jp 2013/01 */
@-moz-document domain("forest.impress.co.jp"){
/* generic unwanted contents --*/
.header, /* header */
.watch-color, /* side bar backround */
div[class^="ad_content"],
div[id^="ad-"]
 {display:none !important;}

/* top page layout */
body {min-height: inherit !important;}
#center-wrap {width: inherit !important;}
#center-wrap>div {width: 100% !important;}
.sm-wrap {width: 70% !important; min-width: 560px; margin: 0 !important;}
 .main {width: 75% !important; min-width: 420px; margin: 0 !important; padding: 0 !important;}
  .sfs-search, .title img[width="468"] {width: 100% !important;}
 .sub{width: 25% !important; min-width: 140px; margin: 0 !important;}
.extra {width: 30% !important; min-width: 192px; margin: 0 !important;}

}

@-moz-document url-prefix("http://www.forest.impress.co.jp/docs/"),
url-prefix("http://www.forest.impress.co.jp/backno/"){
/*--  main articles --*/

.content {min-width: inherit !important;}
.main-wrap {float: right !important; width: 75% !important;}
.main {
 width: 100% !important;
 min-width: inherit !important; 
 margin: 0 !important;
 padding: 0 !important;
 overflow: inherit !important;
 }
.contents-section {padding-left: 0.5em;}
.sub, .extra {
 width: 25% !important;
 margin: 0 !important;
 padding: 0 !important;
 background-color: #339900;
 }
/*-- social (twitter/facebook) info box relayout --*/
#social_tab,  .twtr-doc, .fb_iframe_widget>span {width: inherit !important;}
#social_tab2 *[width="538"], #social_tab2 iframe {width: 100% !important;}
}

@-moz-document url-prefix("http://www.forest.impress.co.jp/library/") {
/*-- monthly software libraly --*/
.lyt-smx div.content, .lyt-smx div.footer {width: 100% !important;} /* main contents and footer */
.lyt-smx div.main-wrap {width: 75% !important; min-width: 600px;} /* main article on left (article) */
.lyt-smx div.extra {width: 25% !important; min-width: 200px;} /* sub on right (search box) */
.lyt-smx div.main {width: 100% !important;} /* ariticle wrapper */
#common-hd {width: 100% !important;} /* article header */
#wraper {width: 100% !important; margin: 0 !important; border: none !important;} /* article body wrapper */
#all-genre, #each-genre {width: 100% !important; padding: 0 !important;} /* software genre */
#each-genre>div,
#each-genre div.soft-intro-field ul.lists
 {width: auto !important;}

}
