css:
@-moz-document url-prefix("http://china.nikkeibp.com.cn/") {
body { /*替换字体，去掉本样式引起的横向滚动条*/
  font-family: arial, sans-serif !important;
  overflow-x: hidden;
}

div#page { /*拉宽页面至100%*/
  width: 100%;
  position: absolute;
}

div.main {
  position: relative;
}

div.main,
div.mainbg,
div.mainbg .thebox3 .thebox3_box,
div.mainbg .thebox3 .thebox3_box .left {/*拉宽文章外的div以便内部可以拉宽*/
  width: 100%;
  padding: 0px;
}

div.mainbg .thebox3 .thebox3_box .left .viewbox {/*拉宽左侧的文章主体*/
  width: auto;
}

div.mainbg .thebox3 .thebox3_box .left .viewbox > div {/*为文章主体右侧的相关链接留出位置*/
  padding-right:320px;
  overflow: visible !important;
}

div.mainbg .thebox3 .thebox3_box .left .viewbox #contentDiv {
  overflow: visible !important;
  height: auto !important;
}

div.mainbg .thebox3 .thebox3_box .left .viewbox #contentDiv #content {
  overflow: visible !important;
}

div.mainbg .thebox3 .thebox3_box .right {/*将相关链接定位*/
  position: absolute;
  right: 10px;
}

div.mainbg .abthebox2 .thebox2bg {/*将下方的“更多新闻”拉宽*/
  width: 100%;
  padding: 0px;
}

div.mainbg .abthebox2 .thebox2bg .newbox {/*本周特辑*/
  width: 48%;
}

div.mainbg .abthebox2 .thebox2bg .newbox.newbox_r {/*其他栏目*/
  width: 48%;
}

div#rightad_a,
div.thebox { /*去掉一些广告位*/
  display: none;
}

div#coverDiv {
  display: none;
}
}

@-moz-document domain("a.deteql.net") {
body {
  font-family:arial, sans-serif;
}
}
