css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("zhihu.com"){

a {outline:none!important;text-decoration:none!important}
body { background:#EEE }

/* ---------------- header ---------------- */

.zu-top {
    background:-webkit-gradient(linear,left top,left bottom,from(#1995de),to(#0771c1));
    background:-moz-linear-gradient(top,#1995de,#0771c1)!important;
    box-shadow: 0 0 7px #777;
    height:46px!important
}

#zh-top-inner{
    margin:0 30px!important;
    width:-webkit-calc(100% - 60px)!important;
    width:-moz-calc(100% - 60px)!important;
    width:calc(100% - 60px)!important
}
.zu-top-nav {
    position:absolute!important;
    right:150px;
}

@media screen and (min-width:1160px) {
    .zu-top-search-form{ width:500px!important }
    .zu-top-search-input{ width:480px!important }
}

/* ---------------- general ---------------- */

@media screen and (min-width:1400px){
    .zu-main.zg-wrap{width:1392px!important}
    .zu-main-content{width:1032px!important;margin-left:360px}
    .zu-main-content-inner{margin-right:360px!important}
    .zu-main-sidebar{width:312px!important;margin-left:-312px!important}
}
@media screen and (min-width:1200px) and (max-width:1399px){
    .zu-main.zg-wrap{width:1152px!important}
    .zu-main-content{width:912px!important;margin-left:240px}
    .zu-main-content-inner{margin-right:240px!important}
    .zu-main-sidebar{width:192px!important;margin-left:-192px!important}
}

.zu-main-sidebar{opacity:0!important;-moz-transition:.2s}
.zu-main-sidebar:hover{opacity:1!important}

.feed-item .avatar{display:none!important}
.feed-item .feed-main{margin-left:0!important}
.feed-item .answer_wrap{margin-left:30px!important}
.zu-main-feed-con,
.zg-section-title,
.zm-topic-topbar,
.feed-switcher { border:none!important }

.zm-common-nav-bar-link{ background:#EEE!important }

.zm-search3-tabs a { border:none!important }

.feed-item,
#zh-explore-list .zm-item,
#zh-favlist-following-wrap .zm-item,
#zh-single-list-page-wrap .zm-item,
#zh-draft-item-list .zm-item,
.zh-question-answer-summary-wrap,
.zm-search3-item { 
    background:#FFF!important;
    border:none!important;
    box-shadow:0 2px 2px #DDD;
    margin-bottom:22px!important;
    padding:22px!important;
    margin:22px 0;
    -webkit-transition: .2s;
    transition: .2s
}

.zu-main-feed-fresh-button,#zh-load-more {
    border-radius:0!important;
    background:#FFF!important;
    border:none!important;
    box-shadow:0 2px 2px #DDD;
    margin-bottom:22px!important;
    outline:none!important;
    -webkit-transition: .2s;
    transition: .2s
}
.zu-main-feed-fresh-button:hover,#zh-load-more:hover { box-shadow:0 2px 2px #BBB!important }
.zu-main-feed-fresh-button:active,#zh-load-more:active {
    box-shadow:0 2px 2px #EEE!important;
    position:relative;
    top:1px;
    -webkit-transiton:0;
    transition:0
}

.combine { margin:0!important }
.first-combine { margin-top:22px!important }
.combine:not(.first-combine) { padding-top:0!important }

.zh-general-list>div:first-of-type { margin-top:0!important }

.feed-main { margin-left:0!important }

.answer_wrap { margin-left:30px!important }

h2 a {
    color:#000!important;
    -webkit-transition: .2s;
    transition: .2s
}
h2 a:hover { color:#259!important }
.feed-item h2 { margin-right:0!important }

.zm-item-vote-info {
    font-size: 12px!important;
    line-height:18px!important;
    max-height: 45px;
    overflow-y:auto
}

.zm-item-vote-count,
.zm-votebar button {
    box-shadow: 0 1px 0 #DDD;
    -webkit-transition:.2s;
    transition:.2s
}

.feed-item:hover .zm-item-vote-count,
.feed-item:hover .zm-votebar button,
.zm-item:hover .zm-item-vote-count,
.zm-item:hover .zm-votebar button {
    box-shadow:0 1px 0 #BBB
}

.zm-item-vote-count:hover,
.zm-votebar button:hover {
    background:#a6ce56!important;
    color:#3E5E00 !important
}

.zm-item-vote-count:active,
.zm-votebar button:active {
    box-shadow:none;
    position:relative;
    top:1px
}

.zu-autohide { visibility:visible!important }

.source,.meta {
    -webkit-transition: .2s;
    transition: .2s
}

.meta { opacity:0.5 }

.ignore {
    top:0!important;
    opacity:0;
    -webkit-transition:opacity .2s;
    transition:opacity .2s
}

.feed-item:hover,
.zm-item-expanded .feed-item { box-shadow:0 2px 2px #BBB }
.zm-item-expanded .source {opacity:0.5}
.zm-item-expanded:hover .source,
.feed-item:hover .ignore,
.feed-item:hover .meta,
.zm-item-expanded .meta {opacity:1}

.feed-item:hover .zg-follow { 
    color:#259!important;
    -webkit-transition:.2s;
    transition:.2s
}

.time { display:none!important }

.feed-item h2 {
    font-size: 16px!important;
    line-height:24px!important
}

.feed-item .source,
.feed-item h3,
.feed-item .zm-item-answer-detail,
.feed-item .meta {
    font-size:14px!important;
    line-height:24px!important
}

.zm-comment-box {
    border:none!important;
    border-radius:0!important;
    box-shadow:0 2px 2px #DDD;
    margin:5px auto!important;
    padding:0;
    max-width:100%!important
}
#zh-question-meta-wrap .zm-comment-box { width:575px!important }
.zm-comment-bubble { display:none!important }
.zm-comment-form .zm-comment-textarea, .zm-comment-form .zm-comment-editable {
    box-shadow:none!important;
    border-radius:0!important
}
.zm-item-comment { margin:0!important }
.zm-comment-form { padding:0!important }

.feed-separator {
    margin-bottom:36px!important;
    text-shadow:0 1px 0 rgba(255,255,255,0.8)
}
.feed-separator span { background:#EEE!important }

.zh-backtotop {
    margin-left:270px!important;
    box-shadow:0 2px 0 #BFBFBF;
    -webkit-transition:.1s;
    transition:.1s
}

@media screen and (min-width:1200px){.zh-backtotop{margin-left:400px!important}}

.zh-backtotop:hover {
    box-shadow:0 2px 0 #AAA;
    background:#DDD!important
}
.zh-backtotop:active {
    box-shadow:0 1px 0 #BFBFBF;
    bottom:112px!important;
}

/* ---------------- single question/answer page ---------------- */

.zm-item-meta { text-shadow:0 1px 1px rgba(255,255,255,0.8) }
.zg-num { text-shadow:0 -0.5px 0.5px rgba(255,255,255,0.5) }

#zh-single-question h2 {
    font-size: 18px!important;
    line-height:24px!important;
    text-shadow:0 1px 1px rgba(255,255,255,0.8)
}
#zh-single-question #zh-question-detail {
    font-size: 14px!important;
    line-height:24px!important;
    text-shadow:0 1px 1px rgba(255,255,255,0.8)
}

#zh-single-question .panel-container,
#zh-single-question .zm-item-answer { 
    background:#FFF!important;
    border:none!important;
    margin: 22px 0;
    box-shadow:0 2px 2px #DDD;
    -moz-transition:.2s;
    transition:.2s
}
#zh-single-question .zm-comment-box { padding:22px!important }
#zh-single-question .zm-item-answer { padding:22px 22px 22px 52px!important }
#zh-single-question .zm-item-answer:hover { box-shadow:0 2px 2px #BBB }
.question-invite-panel {
    background:#FFF!important;
    border:none!important;
    border-radius:0!important;
    box-shadow:0 2px 2px #DDD;
    padding:22px!important
}

.zh-answers-title { border:none!important }

.zh-answers-filter,.zh-answers-filter-popup {
    background:#FFF!important;
    border:none!important;
    border-radius:0!important
}

.selectable:hover { background:#FFF!important }
.selectable:hover span { color:#222!important }

#zh-single-question-page .zm-votebar,
#zh-single-question-page .zm-item-vote,
#zh-single-answer-page .zm-votebar,
#zh-single-answer-page .zm-item-vote { left:22px!important }

.zm-item-answer .answer-head,
.zm-item-answer .zm-item-rich-text {
    font-size:14px!important;
    line-height:24px!important
}

/* ---------------- personal page ---------------- */


#zh-pm-page-wrap .zm-profile-header,
#zh-pm-page-wrap .zm-profile-section-wrap,
#zh-pm-page-wrap .zm-profile-progress,
#zh-pm-page-wrap .zm-profile-details { 
    background:#FFF!important;
    border:none!important;
    border-radius:0!important;
    box-shadow:0 2px 2px #DDD!important;
}

.zm-profile-module { border:none!important }

.zm-profile-header-img { z-index:0!important }

/* ---------------- sidebar ---------------- */

.zu-main-sidebar li{
    -webkit-transition:.2s;
    transition:.2s;
    box-shadow:0 2px 2px rgba(255,255,255,0)
}
.zu-main-sidebar li:hover,
.zu-main-sidebar li a:hover{background:#FFF!important;box-shadow:0 2px 2px #DDD;}

#zh-profile-search-form-mirro,
#zh-profile-search-query-mirro { width:226px!important }

.zm-list-avatar-medium.zm-profile-side-topic-avatar {
    border-radius:0!important;
    margin:0!important
}

.zm-side-section { text-shadow:0 1px 1px rgba(255,255,255,0.8) }
.zm-side-section a:hover { text-shadow:0 1px 1px #FFF }
.zm-side-section-inner { border:none!important }
.zm-side-section-inner div:not(#zh-question-operation-menu) { border:none!important }

/*
.zu-small-avatar-list,
.zm-side-pinned-topics ul,
.zh-trendings ul,
.zh-question-related-questions,
.zh-question-webshare-links,
#zh-fav-list-side-related {
    opacity:0.5;
    -webkit-transtion:.2s;
    transition:.2s
}
.zm-side-section:hover .zu-small-avatar-list,
.zm-side-section:hover ul,
.zm-side-section:hover .zm-side-trend-row,
.zm-side-section:hover .zh-question-related-questions,
.zm-side-section:hover .zh-question-webshare-links,
.zm-side-section:hover #zh-fav-list-side-related {
    opacity:1
}
*/

.zm-side-pinned-topics .zm-side-nav-li {
    padding-right:30px!important;
    float:left;
}

.zm-side-section-inner>div { margin:12px 0 }

.zm-common-nav-bar { border:none!important }

.zu-settings-page .zm-common-nav-bar-item {
    text-align:left!important;
    width:220px!important;
    margin-bottom:5px!important
}

.zm-common-nav-bar-link,.zm-common-nav-bar-current-light {
    border:none!important;
    padding:0!important;
    float:none!important
}

.zg-info-message {
    opacity:.3;
    -webkit-transition:.3s;
    transition:.3s
}
.zg-info-message:hover { opacity:1 }

/* ---------------- footer ----------------*/

.zu-footer-inner {
    border:none!important;
    padding:15px 0!important;
    text-shadow:0 1px 1px rgba(255,255,255,0.8)
}

@media screen and (min-width:1025px) {
    .zu-footer .zg-left {
        margin-left: 100px;
        -webkit-transition: .2s;
        transition: .2s
    }
}

/* ---------------- other fixes ----------------*/

h3{ line-height:25px }
.zm-side-list-content{clear:both}
.zm-item-tag,.zm-tag-editor-edit-item {
    background:rgba(34,85,153,0.8)!important;
    border-radius:3px!important;
    display:inline-block;
    position:relative;
    font-size:14px!important;
    text-shadow:0 -1px 0 rgba(0,0,0,0.1)
}

.zm-item-tag,.zm-tag-editor-edit-item a{ color:#FFF!important }

.zm-item-tag:hover,
.zm-tag-editor-edit-item:hover {
    background:rgba(34,85,153,0.75)!important
}
.zm-item-tag:active,
.zm-tag-editor-edit-item:active {
    background:rgba(34,85,153,0.8)!important
}

/* ---------------- iZhihu ---------------- */

.izh_fav {
    border:none!important;
    box-shadow:0 0 7px #BBB;
}
.izh_fav .fav {
    -webkit-transition:.2s;
    transition:.2s
}
.izh_fav .fav:hover {
    background:#DDD;
    text-shadow:0 1px 1px rgba(255,255,255,0.8)
}

}
