css:
@namespace url(http://www.w3.org/1999/xhtml);
/** created by Quelbs - Version 1.0.1 **/
@-moz-document domain("fritz.box") {
html,body,div.fw_bg_body {
    background:#fff !important;
}
#uiViewPassword, .Pushbutton {
    font-size:20px !important;
}
#intro_bar_box,div.fw_header,div.fw_footer {
    display:none;
}
#main_page_all,#uiPass,#uiSubmitLogin {
    font-size:15px !important
}
div {
    background-image:none !important;
    background-color:transparent !important;
}
td,tr,table {
    background-color:transparent !important;
}
.tborder {
    border-radius:8px;
}
#menucontent div {
    background:none !important;
    border:none !important;
    margin:0 !important;
}
#menucontent li,#menucontent ul li ul li {
    background-color:#eee;
    border:1px solid #ddd;
    margin:1px 0;
    font-size:18px !important;
    border-radius:5px;
}
#menucontent ul li ul li,#menucontent ul li ul li.selected {
    font-size:14px !important;
    width:140px !important;
    padding:5px !important
}
#menucontent li.explain {
    background:none !important;
    border:none !important;
    font-size:11px !important;
}
#menucontent li.selected,#menucontent li.submenu_last ul, #menucontent li.submenu ul, #contentTitle {
    background-image:none !important;
    background-color:#adf !important;
}
#menucontent li.selected, #menucontent li {
    padding:3px 5px !important;
    margin:3px 0 !important;
}
#menucontent li ul li.selected {
    width:auto !important;
    left:0 !important;
    border-color:#003365;
}
#page_content, .backframe, .backdialog,.forebuttons {
    border:none !important;
}
#contentTitle {
    margin:0 !important;
    border-radius:5px;
}
/* some adjustments for FRITZ!Box 7113 */
.LMenu > li > img, .LMenubottom, .LMenutop {
    display:none;
}
.LMenu > li {
    background-image:none !important;
}
.LMenu > li > a, .LMenu > li > span {
    font-size:13px !important;
    top:0;
}
}
