css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("kickass.to"), domain("katproxy.com") {




/*Homepage Changes*/


#tagcloud {
display: none !important;
}

#wrapperInner > a {
display: none !important; 
}

a.advancedSearchLink.ajaxLink {
font-weight:bold !important;
font-size: 12px !important;
}



/*Site-Wide Changes*/


h2 {
margin-bottom: 5px !important;
}


div.headmenu.darkbrownbg a:hover {
color: #f8c017 !important;
text-decoration: underline !important;
}


.sidebarCell,
footer,
.blank,
a.rsssign,
#feedback {
display: none !important;
}


#navstyle {
margin-bottom: 3px !important; 
}



/*Profile Changes*/


h1.nickname {
margin-top: 3px !important;
}


#wrapperInner > div.mainpart > table > tbody > tr > td:nth-child(1) > div.lightgrey.font12px > span {
color: #FFF !important;
}


/*Search / Table Changes*/


.tabs {
margin-top: -32px !important;
min-height: 35px !important;
}


.doublecelltable {
margin-left: -14px !important;
margin-bottom: -20px !important;
margin-top: -2px !important;
width: 100.8% !important;
}


tr.even {
background-color: #eeecd7 !important;
}


tr.odd {
background-color: #f5f3e8 !important;
}


tr.odd:hover {
background-color: #e8e3c3 !important;
text-decoration: underline !important;
}


tr.even:hover {
background-color: #e8e3c3 !important;
text-decoration: underline !important;
}


.normalgrey.font12px.plain.bold {
color: #545454 !important;
}


.normalgrey.font12px.plain.bold:visited {
color: #929292 !important;
}


td.font12px.torrentnameCell:hover .normalgrey.font12px.plain.bold {
color: #000 !important;
}


.font11px.lightgrey.block {
color: #585858 !important;
}


table.data {
margin-bottom: 15px !important;
}


.pages.botmarg5px.floatright {
margin-bottom: 8px !important;
}



/*Blog / Forum Changes*/


.blogcol,
.comments {
min-width: 100% !important;
}


#communityBlock {
width: 100% !important;
}


.commentbody {
background-color: #eae6d2 !important;
min-height: 124px !important;
}


.showmore.folded {
margin-bottom: 12px !important;
}


.line50perc.showmore.botmarg0.ajaxLink {
display: none !important;
}


#pollinfo.pollresultsblock {
width: 100% !important;
margin-top: 14px !important;
margin-bottom: 22px !important;
padding: 0px !important;
}


#pollresults {
width: 100% !important; 
}


.pollbarwrapper {
width: 100% !important;
}



/*Browse Changes*/


.margauto.width900px.textcontent {
width: 100% !important;
margin-left: -10px !important;
margin-top: -12px !important;
}




/*Ad Removal / Sponsored Listings*/


.vuze,
.slotbox,
.advertising.horizontalAdvert,
.partner1Button.idownload.icon16,
#_2bffc94164dd9984ae4826e8bc988721,
HTML > BODY > DIV:nth-child(5) > TABLE > TBODY > TR > TD:first-child > .tabs:nth-child(2) {
display: none !important;
}
}



