css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("slo-tech.com") {
    body { font-family: "Liberation Sans",sans-serif !important; font-size: .91em !important; }
    #panels, #head, #menu, #poll, #panels a, #menu a, #poll a, #panels h2 a, #menu h2 a, #poll h2 a  { font-size: .8em !important; }
    #head { display: none !important; }

.post .signature, .post .history, .post .history a, .post .zgodovina, .post .zgodovina a, .messages, .views { font-size: .8em !important; color: #777 !important; line-height: 1.1em !important; }

.content blockquote, .content i { font-style: normal !important; color:#555 !important; font-size: .89em !important; font-family: "Liberation Sans",sans-serif !important; }

.avatar, .avatar img { width: 32px !important; height: 32px !important; border-color: #eee !important; }

.post { -moz-border-radius: 6px !important; border-radius: 6px !important; margin-bottom: 8px !important; }

}
