css:
@-moz-document domain("riverport.s27.xrea.com") {
* {
	line-height: 1.5;
}

body {
	margin: 0;
	padding: 0 32px 32px 0;
	background: #57A;
	color: #000;
	background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAHgAAAB4AQMAAAADqqSRAAAABlBMVEVEZjNmd6rfZTZCAAAAh0lEQVR4Xu2TAQYAIRBFhwGIrjIHiK7eDfYqEUC0didCsz4Flj6ZHr4xTZ+QIucHOMdFvpRrs5kvrSHbTKLMNDH04/7782Fxn0L0PjElPY70TCzDJxa74UsGY1X/+nyqa9yicvlgH7SWZDP16mhi4Ef9t+dDQu+P9of2j/7Pf/N78nvye/J7Az7K4x2OfZcZAAAAAElFTkSuQmCC);
}

a[href] {
	text-shadow: 0 1px 1px #00C;
	padding: 0 2px;
}

a:link {
	color: #66F !important;
}

a:visited {
	color: #969 !important;
}

div#header {
	padding: 0;
	z-index: 2;
	position: relative;
	top: 0;
	left: 0;
	height: 80px;
	line-height: 0;
	width: 100%;
	overflow: hidden;
}
div#header object, div#header img {
	position: absolute;
	border: 2px solid #888;
	background-color: #FFF;
}
#logo {
	top: 8px;
	left: 8px;
	background-image: url(data:image/png;base64,);
}

#ad {
	top: 8px;
	left: 80px;
}

div#header img {
	top: -2px;
	left: -2px;
}

hr {
	display: none;
}

div#chapter {
	background: -webkit-gradient(linear, left top, right top, from(#ADE), color-stop(0.9, #650), to(#650));
	background: -moz-linear-gradient(left, #ADE, #650 90%);
	background: -o-linear-gradient(left, #ADE, #650 90%);
	margin: 0;
	border-radius: 0 32px 0 0;
	border-width: 4px 4px 0 0;
}

div#chapter, div#footer {
	border-style: solid;
	border-color: #221;
}

div#footer {
	border-width: 0 4px 4px 0;
	padding: 0 32px 0 0;
	margin: 0;
	background: #650;
	text-shadow: 1px 0px 1px #a97;
	font-family: verdana, sans-serif;
	border-radius: 0 0 32px 0;
}

div#footer p, div#footer address {
	background: -webkit-gradient(linear, left top, right top, from(#ADE), color-stop(0.9, #650), to(#650));
	background: -moz-linear-gradient(left, #ADE, #650 90%);
	background: -o-linear-gradient(left, #ADE, #650 90%);
	padding: 1em;
	margin: 0;
}
div#footer p#breadcrumbs {
	margin-bottom: -1em;
}
div#footer address {
	font-style: normal;
}
div#footer a {
	text-shadow: 0px 1px 1px #66f;
}

div#footer a:link {
	color: #00C !important;
}

div#footer a:visited {
	color: #A48 !important;
}

h1 {
	margin: 0;
	text-align: center;
	letter-spacing: .1em;
	font-size: 245%;
	font-family: Verdana, Arial, sans-serif;
	padding: 32px 0 8px;
	color: #BDB;
	line-height: 1;
	text-shadow: 0px .08em 0px #B9A;
}

.section {
	background-color: #221;
	color: #987;
	margin: 32px 32px 0 0;
	padding: 0 16px 1px 16px;
	border-radius: 0 32px 32px 0;
	border-width: 4px 4px 4px 0;
	border-style: solid;
	border-color: #A56;
	background-image:url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAgAAAAIAQMAAAD+wSzIAAAABlBMVEUiIhEqKhmCffayAAAAFElEQVQI12OwZ7BnOAOEn4EQSAMAJXgFlYC5C58AAAAASUVORK5CYII=);
}

h2 {
	margin: 0 0 .67em 0;
	padding: 16px 0 0 0;
	line-height: 1;
	font-size: 150%;
	font-family: Verdana, Arial, sans-serif;
	color: #96C;
}

h2:first-letter {
	color: #F60;
	font-size: 120%;
}

h3 {
	font-size: 125%;
	font-family: Verdana, Arial, sans-serif;
	color: #D7A;
	margin:  .77em 0;
}

ul dl, ol dl, dd ul, dd ul, dd ol, dd dl {
	margin: 0;
}

ul p, ol p {
	margin: .5em 0;
}

dt {
	display: inline-block;
	border: 2px solid #765;
	padding: 0 .5em;
	border-radius: .7em;
	min-width: 5.5em;
	margin-bottom: -1px;
	background: #332;
}

dd {
	border-left: 8px double #432;
	padding-left: .3em;
	margin-bottom: .5em;
	margin-left: 1.88em;
	border-radius: 0 0 0 13px;
}

dt + dd {
	padding-top: .5em;
}

blockquote {
	background-color: #332;
	padding: 1px 1em;
	border: 1px dashed #987;
	margin: 1em 3em;
	border-radius: 10px;
}

pre, pre * {
	line-height: 1;
}
pre a[href] {
	padding: 0;
}

form p {
	margin: 0;
}

form {
	margin: 0 0 1em;
}

dl#linebbs dt, dl#linebbs dd {
	border-width: 1px 0 0;
	border-style: dashed;
	border-color: #443;
	border-radius: 0;
}

dl#linebbs dt {
	width: 18em;
	padding: 0;
	background: transparent;
}

dl#linebbs dd {
	padding: 0;
	margin: -1.5em 0 0 18em;
	position: relative;
	top: -1px;
}

label {
	font-family: "MS Gothic", monospace;
}

input[type="text"],
input[type="password"],
textarea {
	padding: 2px;
	font-size: 100%;
	color: #221;
	line-height: 1;
	border-width: 2px;
	border-style: inset;
	border-color: #a98 #987 #987 #a98;
	background-color: #ba9;
	vertical-align: top;
	border-radius: 6px;
	font-family: monospace;
}

input[type="submit"],
input[type="reset"],
input[type="button"],
button {
	border-width: 2px;
	border-style: ridge;
	border-color: #fed #654 #654 #fed;
	background-color: #987;
	color: #221;
	font-size: 96.12%;
	line-height: 1;
	padding: 2px;
}

table {
	border: none;
	border-collapse: collapse;
	margin: 0 0 2em;
}

caption {
	font-weight: bold;
	letter-spacing: .07em;
}

th, td {
	padding: 1px 3px;
	border: 1px solid #987;
}

th {
	background-color: #332;
}

em {
	font-weight: bold;
	font-style: normal;
	color: #e94;
	margin: 0 2px;
}

code {
	font-family: courier, monospace;
}

var {
	font-style: normal;
	color: #DDD;
	background: #3C3C3C;
	font-family: monospace;
	padding: 2px;
	border-radius: 4px;
	border: 1px solid #555;
	position: relative;
	top: -2px;
	margin: 0 2px;
}

code var {
	border: none;
	padding: 0;
	position: static;
	background: transparent;
	margin: 0;
	font-family: inherit;
	font-size: inherit;
	text-shadow: 1px 0 1px;
}

dfn {
	font-style: italic;
	margin-right: 3px;
}

div.section img {
	vertical-align: top;
	box-shadow: 8px 8px 8px rgba(0,0,0,0.6);
	margin: 8px 8px 8px 0;
}

div.section div img {
	box-shadow: none;
}
div.section a img {
	vertical-align: bottom;
}

@media screen and (min-width: 1200px) {
	div#headline {
		position: absolute;
		border-radius: 0 32px 32px 0;
		width: 17.5em;
	}
	div.section + div.section {
		margin-left: 21.5em;
		border-radius: 32px;
		border-width: 4px;
	}
}
}
