css:
@-moz-document domain('reddit.com') {
html {
background-color:#F9F9F9;
}
.thing .title {
   color:#0060C1;
}
.sr-bar a {
   text-shadow: 0px 1px 0px #fff;
}
.sr-bar a:hover {
   text-shadow: none;
   background-color:#666;
color:#fff;
}
#sr-header-area {
   background: rgb(215,215,215);
background: -moz-linear-gradient(top,  rgba(215,215,215,1) 0%, rgba(229,229,229,1) 100%);
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(215,215,215,1)), color-stop(100%,rgba(229,229,229,1)));
background: -webkit-linear-gradient(top,  rgba(215,215,215,1) 0%,rgba(229,229,229,1) 100%);
background: -o-linear-gradient(top,  rgba(215,215,215,1) 0%,rgba(229,229,229,1) 100%);
background: -ms-linear-gradient(top,  rgba(215,215,215,1) 0%,rgba(229,229,229,1) 100%);
background: linear-gradient(to bottom,  rgba(215,215,215,1) 0%,rgba(229,229,229,1) 100%);
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#d7d7d7', endColorstr='#e5e5e5',GradientType=0 );
}
#header {
background: rgb(129,162,196);
background: -moz-linear-gradient(top,  rgba(129,162,196,1) 0%, rgba(168,205,234,1) 99%);
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(129,162,196,1)), color-stop(99%,rgba(168,205,234,1)));
background: -webkit-linear-gradient(top,  rgba(129,162,196,1) 0%,rgba(168,205,234,1) 99%);
background: -o-linear-gradient(top,  rgba(129,162,196,1) 0%,rgba(168,205,234,1) 99%);
background: -ms-linear-gradient(top,  rgba(129,162,196,1) 0%,rgba(168,205,234,1) 99%);
background: linear-gradient(to bottom,  rgba(129,162,196,1) 0%,rgba(168,205,234,1) 99%);
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#81a2c4', endColorstr='#a8cdea',GradientType=0 );
}
}
