css:
@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')   {
 
body {
 background: #FFFF33
 url('http://i202.photobucket.com/albums/aa282/MikilofSouthern/yosukebg_zps057af12e.png')no-repeat fixed !important;
 background-attachment: fixed !important;
 background-position: 0% 0% !important;
 background-size: 100% 100%  !important;
}
 
 
 
ol#posts li.notification {
 background-color: #339933   !important;
 border-top: 3px solid #66FF66 !important;
 border-left: 3px solid #66FF66 !important;
 border-bottom: 3px solid #66FF66 !important;
 border-right: 3px solid #66FF66 !important;
 color: #66FF66 !important;
 text-decoration: none !important;
}

 
#posts .notification a:not(.avatar_frame) {
 color: rgba(00, 00, 00, 1) !important;
 font-weight: bold;
 text-decoration: none !important;
 border-bottom: 0px solid #66FF66 !important;
 -webkit-background-clip: padding-box;
}
 
 
#posts .notification blockquote {
 border-left: solid 3px #66FF66 !important;
}
 

 
#posts .notification .avatar_frame {
 background-color: #66FF66 !important;
 border: 1px solid #66FF66 !important;
 position: absolute;
 top: -1px;
 left: -55px;
 overflow: hidden;
 display: inline-block;
}
 
 
#posts .post.new_post .new_post_label {
 -webkit-font-smoothing: antialiased;
 opacity: .9;
 font-size: 14px;
 font-weight: bold;
 color: #000000;
 text-decoration: none;
 text-transform: uppercase;
 text-align: center;
 width: 74px;
 position: absolute;
}
 
 
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
 background: rgba(242, 164, 0, .0) !important;
}
 
*{text-shadow:none !important; color:#626262!important;}
 
 
.selection_nipple:not([class="selection_nipple white"]) {
 
 
border-bottom: solid 11px rgba(00,00,00, 0.50)  !important;}
 
 
#content_top {
 width: 0px !important;
 height: 0px !important;
}
 
 
.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
 background: none !important;
 width: 0px !important;
 height: 0px !important;
 margin-top: 0px !important;
 border-bottom: solid 11px rgba(00,00,00, 0.50) !important;
 border-right: 11px solid transparent !important;
 border-left: 11px solid transparent !important;
}
 
 
#content_bottom {
 width: 0px !important;
 height: 0px !important;
}
 
 
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
 padding-top: 25px !important;
 padding-bottom: 25px !important;
 -moz-border-radius: 20px !important;
}

 
 
LABEL {
color: rgba(0,0,0, 1.0) !important;
}

 
#footer {
 display: none !important;
}
 
 
.radar {
 display: none !important;
}
 
.selected .following,
 
.selected .followers,
 
.selected .settings,
 
.selected .posts,
 
.selected .messages,
 
.selected .drafts,
 
.selected .queue,
 
.selected .likes  {
 color: rgba(0, 0, 0, 0.50) !important;
 background: rgba(0, 0, 0, 0.0) !important;
} 
 
 
#new_post .arrow {
 opacity: 0.50 !important;
 margin-left: -1px !important;
}
 
}
