css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document url(http://eksisozluk.com/), url-prefix(http://eksisozluk.com/), url-prefix(https://eksisozluk.com/) {
body > header {background-color: #640000 !important;}
body > header a{color: #ffffff !important;}
body > header a:hover {color: #bbbbbb !important;}
body {background-color:#111111 !important;width:100% !important;}
body a {color:#fda5a5 !important;}
body a:hover{text-decoration:none !important; color:#fc6464 !important;}

#container {float:left !important;margin-left: 10px !important;width:97% !important;max-width:none !important;max-width:1400px !important;color:white !important;}
#content-body {margin:0 !important; width:100% !important; }
#content {margin-right:17px !important; margin-left:17px !important;}
#main {margin-left: 250px!important;background-color:#222222 !important; 
border-width:1px !important;
border-style: solid !important;
border-color: #ffffff !important;}
.sub-title-menu a, #entry-list footer div.feedback a {color:#bbbbbb !important;}
.sub-title-menu a:hover, #entry-list footer div.feedback a:hover {color:#fd3838 !important;}

#sub-navigation a:hover {color:#bbbbbb !important;}
#entry-list > li:before {border-bottom-color: #555555 !important;}
#sub-navigation {background-color: #640000 !important;}
#top-navigation > ul .new-update, #sub-navigation > ul .new-update {
    background-color: rgba(60, 255, 0, 0.8) !important;}
#top-navigation > ul > li > a { 
background-color: #444444 !important;
border-radius:8px !important;
border-width:1px !important;
border-style: solid !important;
border-color: #333333 !important;
}
#threads li article.unread h2 {color:#fda5a5 !important;}
#threads li article.unread h2 small {color: #ffffff !important;}
#threads li article.unread p {color: #ababaa !important;}

#index-section {width:250px !important; background-color:#111111 !important;}
#partial-index .topic-list.partial li > a {background-color:#111111 !important;padding-top:0px !important; padding-bottom:0px !important;}
.topic-list li {border-top-width:1px !important; border-top-color:#444444 !important;}

#aside{display: none !important;}
#message-thread {max-width:860px !important;}
html.no-touch .topic-list li > a {color:#fda5a5 !important;}
html.no-touch .topic-list li > a:hover {
background-color:#000000 !important;
color:#fb3c3c !important;
}
fieldset legend {color:#999999 !important;}

html.no-touch #feed-refresh-link:hover {
color:#000000 !important;
background-color: #fd3838 !important;}
html.no-touch .dropdown .dropdown-menu li a {color:#000000 !important;}
html.no-touch .dropdown .dropdown-menu li:hover {
    background-color: #fd3838 !important;
    }
.topic-list.partial li.numbered > a > small {
    position: static !important;
    color:#ffffff !important;}
h1, h2, h3, h4, h5, h6 {color:#ffffff !important;}

label, input, select, textarea {color: #515151 !important;}

#user-badges a {background-color:#ffffff !important;}
.tabs {background-color: #222222 !important;}
.tabs a:hover {background-color:#fd3838 !important; color:#000000 !important;}
html.no-touch #threads li article:hover {background-color: #444444 !important; }
#message-thread .incoming, #message-thread .outgoing {background-color: #3a3a3a !important;
color:#ffffff !important;}
#threads li article p {color: #ffffff !important;}

button.primary, input.primary[type="submit"], input.primary[type="button"] {
background-color:#008aff !important;}
h2 {font-size: 15px !important;}
}
