css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document  url-prefix("http://elearn.guilan.ac.ir/amozesh") {
/**
*elearn.guilan.ac.ir.amozesh.stylish
*Created by Ali Seddighi
*version 0.8.1
**/
/* clear pre css */
#header-bt, #header-bb,
#content-bt, #content-bb,
#footer-bt, #footer-bb,
.bt,.bb,
#chooselang
{display:none!important}
#header-i1, #header-i2,
#content-i1, #content-i2,
#footer-i1, #footer-i2,
.i1,.i2,.i3,
.navbar,
div.sideblock div.content,
#header-home-i3,#header-i3,#content-i3,#footer-i3,
table.forumpost div.commands
{background:none!important;padding:0!important;margin:0!important;border:0!important}
#header-home-i3 > img, #header-i3 > img,
#header-home-i3 img[src="/amozesh/theme/custom_corners/logo.png"],
#header-i3 img[src="/amozesh/theme/custom_corners/logo.png"]{display:none!important}
#header-home-i3, #header-i3 {
min-height:37px;
background:url(data:image/png;base64,)right top no-repeat!important
}
/* set new css */
body{ font: 400 11pt/20pt  tahoma, arial!important; background:#C3C39B!important;color:#2D3742!important }
input, select,textarea, button{font: 400 9pt/14pt  tahoma, arial!important;}
h1,h2,h3,h4,h5,h6{text-shadow:0px 1px 0px #ddd;font-family:"b yekan", yekan, tahoma!important;}
a:link, a:visited {color: #9C8628!important;text-shadow: 0 1px 0 #E3E3B3;}
a:visited {color: #868760!important;}
a:hover {color: #C2682B!important;}
a[href^="http://elearn.guilan.ac.ir/amozesh/login/logout.php"]:link{color:red!important}
.breadcrumb li{font: 400 10pt/24pt "b yekan", yekan, tahoma!important; }
.breadcrumb li > a {color:#fff!important;text-shadow: none!important}
.navbar{border-radius:5px!important;background:#A3AAA6!important;}
.headermenu{margin-bottom:6px!important}
#header, #header-home{}
#content{background:#EDF0EE!important;margin:5px 0!important;border-radius:5px}
.rsslink {padding:5px 6px!important;display:block}
div.breadcrumb span.sep {color: #B8BFBB!important; font-family: arial,sans-serif!important;text-shadow:0 1px 0px #E1EBE5}
#intro{padding:8px!important;border-radius:5px!important}
.loginbox.twocolumns{margin:auto!important; border:0!important}
.loginpanel{border:0!important; border-left:1px solid #ddd!important}
td#left-column, td#right-column {
padding:10px!important;
}
td#middle-column {
padding:10px 0px!important;
}
div.sideblock{border:1px solid #999!important;background:#DDDDDD!important;border-radius:3px}
div.sideblock div.content{padding:3px!important}
div.sideblock .title{padding:0 2px!important;background:#A3AAA6!important;}
div.sideblock .title h2{color:#fff!important;text-shadow:0 1px 0 #666!important}
div.sideblock .title input{margin:8pt 4px 0 6px!important}
div.sideblock div.column.c0{margin:0!important;float:right!important}
.sideblock img.userpicture {margin:0 0 0 4px!important;}
.block_online_users .listentry div.user, .block_messages .listentry div.user {
float: right!important;
}
div.sideblock div.title h2 {font-weight:400!important;font-size: 10pt!important;line-height:24pt!important}
#footer-i3 p:after{ content: '  :: Style and Design by Ali Seddighi'; }
table.forumpost{width:100%}
table.forumpost div.commands{line-height:2em!important;background:#ddd!important}
table.forumpost tr.header td.topic, table.forumpost tr.header td.picture{background:none!important;vertical-align: middle!important;}
.forumpost .content, .forumpost .side{border:0!important;background:#fff!important}
.forumpost{border-bottom:2px solid #B9BBBA!important}
div.coursebox{font-size:1.15em!important;line-height:1.8em!important;color:#666!important}
th.header{line-height:3.4em!important}
#footer{line-height:22px!important}
.weekdays th{text-align:center!important}
.loginbox .loginform .form-input input { width: auto!important;}
.loginbox .loginform .form-input input[text] { width: 9em!important;}
td#middle-column tr.section{background:#fff!important;border:1px solid #ddd!important}
td#middle-column tr.separator{background:none!important;border:0!important}
#course-view .section td.side{background:none!important}
#course-view .section td{border:0!important}
.tabrow0 {padding-bottom: 50px!important;}
.tabrow0 LI, UL.tabrow1{line-height:30px!important;}
.tabrow0 div, .tabrow0 ul { top: 30px!important;}
.sideblock .content .list li {
clear: both!important;
}
div.sideblock div.column.c0 {
margin-left: 0!important;
margin-top:3px!important;
}
#content-i3>iframe{width:100%!important;height:100%!important;position:absolute;top:95px;left:0;border:0!important}

font[size='1']{font-size:9px!important}
font[size='2']{font-size:10px!important}
font[size='3']{font-size:12px!important}
font[size='4']{font-size:14px!important}
font[size='5']{font-size:18px!important;line-height:2em;font-family:"b yekan",yekan,tahoma!important}
font[size='6']{font-size:24px!important;line-height:2em;font-family:"b yekan",yekan,tahoma!important}
font[size='7']{font-size:36px!important;line-height:2em;font-family:"b yekan",yekan,tahoma!important}
}
