css:
/* @name dic.yahoo.co.jp ヤフー辞書 ja jp 2013/02 */
@-moz-document domain("dic.yahoo.co.jp"){
/*= dic.yahoo.co.jp , dic.yahoo.co.jp/dsearch =*/
/*- unwanted contents -*/
#header-d>div[style^="margin:10px 2px;padding:5px;text-align:center;"],
#sub-contents-d>#pos-lrec,
#dic-sult,
.yj-guid
 {display: none !important;}

/*- fit to window -*/
#wrapper-d, #contents-d, .navi-dic, #select-dic
 {width: inherit !important; margin: 0 !important; padding: 0 !important;}

/*- 2 column relayout -*/
#searchbox-dic #SaC{width: auto !important;}
#main-d, #main-r-d, #select-dic dl {
 width:70% !important; min-width: 560px;
 margin: 0 !important; padding: 0 !important;
 }
#sub-left-d, #sub-contents-d, #select-dic .set-dic {
 width:30% !important; min-width:240px;
 margin: 0 !important; padding: 0 !important;
 }
/*- fit to column -*/
#main-d * {width: auto !important; max-width: 100% !important;}
#sub-contents-d * {width: auto !important; max-width: 100% !important;}

}

@-moz-document domain("dic.search.yahoo.co.jp"){
/*= dic.search.yahoo.co.jp/search -*/
/*- unwanted contents -*/
.yj-guid, .yj-skip, #sIn>#So3.ss
 {display: none !important;}

/*- fit to window -*/
#wrapper {width: inherit !important;}
/*- yahoo jisho logo -*/
#SaB {
 display: inline-block !important;
 float: none !important;
 position: relative !important;
 margin: 0 !important;
 }
/*- dic search field -*/
#SaC fieldset {height: auto !important;}
/*- side bar alignment -*/
.unit.size1of3.mod {float: none !important;}
}
