css:
@-moz-document url-prefix(http://sky9games.com/community/) {
/*

v. b.e.E.3.c.kd

BBBBBBBBB
 BB     BB
 BB     BB
 BBBBBBBB
 BB     BB
 BB      BB
 BB     BB
BBBBBBBBB

Featuring gradients and colors and permission post styles and some image changes. The editor cannot be dealt with directly. A blue alternative - like the dark skin beta that this is built around - might be made if people would prefer it.

Ktnxbai ~

- Jeol

*/

html, *{
	margin: 0px;
}

.block {
	background-color: #111;
	background: rgba(4, 2, 2, 0.4);
	display: block;
	padding: 5px 20px;
	border: solid 1px #444;
}

.title {
	font: 140% "Lucida Sans Unicode", "Bitstream Vera Sans", "Trebuchet Unicode MS", "Lucida Grande", Verdana, Helvetica, sans-serif;
	font-weight: bold;
	color: #555;
}

body {
	background-color: #000;
}

/* Menu */

#menu {
	float: left;
	list-style: none;
	margin: 0;
	padding: 0;
	width: 100%;
	border-top: 1px solid #333;
	border-bottom: 1px solid #333;
	font: 100% "Lucida Sans Unicode", "Bitstream Vera Sans", "Trebuchet Unicode MS", "Lucida Grande", Verdana, Helvetica, sans-serif;
}
#menu li {
	float: left;
	margin: 0;
	padding: 0;
}
#menu a {
	color: #555;
	display: block;
	float: left;
	margin: 0;
	padding: 8px 20px;
	text-decoration: none;
}
#menu a:hover {
	background: #212121;
	background: rgba(162, 52, 52, 0.7);
	color: #aaa;
}
#menu .selected {
	background: #212121;
	background: rgba(180, 52, 52, 0.4);
	color: #888;
	display: block;
	float: left;
	margin: 0;
	padding: 8px 20px;
	text-decoration: none;
}

/* End Menu */

.copywrite {
	width: 800px;
	position: relative;
	left : 50%;
	margin-left: -400px;
	font-size: 10px;
	font-family: 'Helvetica Neue', Arial, Helvetica, sans-serif;
	color: #555;
	padding: 8px 0px;
}

.topmenus {
	width: 800px;
	position: relative;
	left: 50%;
	margin-left: -400px;
}

#wrapper {
	background: url(http://sky9games.com/images/background_dark.png) top center no-repeat !important;
	background-color: #000;
}

.header {
	height: 33px;
	background: url(http://sky9games.com/images/header_dark.jpg) top repeat-x !important;
}
.footer {
	height: 33px;
	background: url(http://sky9games.com/images/footer_dark.jpg) top repeat-x !important;
	text-align: center;
	margin-bottom: 0px;
}

a:link, a:visited {
	color: #822;
	text-decoration: none;
}
a:hover, a:active {
	color: #922;
	text-decoration: none;
}

#container {
	width: 90%;
	min-width: 800px;
	max-width: 1400px;
	color: #555 !important;
	margin: auto auto;
	padding: 20px;
	text-align: left;
	font-family: Verdana, Arial, Sans-Serif;
	font-size: 13px;
	line-height: 1.4;
}

#content {
	/* FIX: Make internet explorer wrap correctly */
	width: auto !important;
}

.menu ul {
	color: #2D69B9;
	font-weight: bold;
	text-align: right;
	padding: 4px;
	font-size: 13px;
}
.menu ul a:link {
	color: #2D69B9;
	text-decoration: none;
}
.menu ul a:visited {
	color: #2D69B9;
	text-decoration: none;
}
.menu ul a:hover, .menu ul a:active {
	color: #5694E7;
	text-decoration: none;
}

#panel {
	/* background: #fff; */
	color: #555;
	text-align: left;
	font-size: 90%;
	/* border: 3px solid #90B7EE; */	
	padding: 8px;
}

table {
	font-family: Verdana, Arial, Sans-Serif;
	font-size: 13px;
}

.tborder {
	background: #333;
	width: 100%;
	margin: auto auto;
	/*border: 2px solid #90B7EE;*/
	border: 1px solid #333;
}

.thead {
	background: #3e1919 url(http://kevin.sublimism.com/css/head_bg.png) top repeat-x !important;
	color: #a19b9c;
}
.thead a:link {
	color: #a19b9c;
	text-decoration: none;
}
.thead a:visited {
	color: #a19b9c;
	text-decoration: none;
}
.thead a:hover, .thead a:active {
	color: #bfb4b5;
	text-decoration: underline;
}

.tcat {
	/*background: url(http://sky9games.com/community/images/default/tcat_bg.gif);*/
	background-color: #040404;
	color: #666;
	font-size: 12px;
}
.tcat a:link {
	color: #a99;
}
.tcat a:visited {
	color: #a99;
}
.tcat a:hover, .tcat a:active {
	color: #bfafaf;
}

.trow0 {
	background: #1c1c1c;
	color: #5a5a5a;
}
.trow1 {
	background: #171717;
}
.trow2 {
	background: #111;
}
.trow_shaded {
	background: #111;
}
.trow_selected td {
	background: #FFFBD9;
}
.trow_sep {
	background: #555 url(http://kevin.sublimism.com/css/headcat_bg.png);
	color: #222;
	font-size: 12px;
	font-weight: bold;
}

.tfoot {
	background: #190a0a url(http://kevin.sublimism.com/css/bottom_bg.png) top repeat-x !important;
	color: #e2e3df;
}
.tfoot a:link {
	color: #e2e3df;
	text-decoration: none;
}
.tfoot a:visited {
	color: #e2e3df;
	text-decoration: none;
}
.tfoot a:hover, .tfoot a:active {
	color: #e2e3df;
	text-decoration: underline;
}

.bottommenu {
	background: #fff;
	color: #000;
	font-size: 10px;
	border: 2px solid #90B7EE;
	padding: 8px;
}

.navigation {
	color: #555;
	font-size: 11px;
	font-weight: bold;
}
.navigation a:link {
	text-decoration: none;
}
.navigation a:visited {
	text-decoration: none;
}
.navigation a:hover, .navigation a:active {
	text-decoration: none;
	color: #922;
}
.navigation .active {
    color: #666;
	font-size: small;
	font-weight: bold;
}

.smalltext {
	font-size: 11px;
}
.largetext {
	font-size: 16px;
	font-weight: bold;
}

input.textbox {
	background: #e4e4e4;
	color: #4b4b4b;
	border: 1px solid #776;
	padding: 1px;
}
textarea {
	background: #e4e4e4;
	color: #4b4b4b;
	border: 1px solid #777;
	padding: 2px;
	font-family: verdana, geneva, lucida, lucida grande, arial, helvetica, sans-serif;
	line-height: 1.4;
	font-size: 11px;
	width: 500px;
}
select {
	background: #232323;
	border: 1px solid #e2e3df;
}

.editor {
	background: #e4e4e4;
	border: 1px solid #ccc;
}
.editor_control_bar {
	background: #333;
	border: 1px solid #0f5c8e;
}
.autocomplete {
	text-align: left;
	background: #333;
	border: 1px solid #000;
	color: white;
}
.autocomplete_selected {
	background: #adcee7;
	color: #333;
}

.popup_menu {
	background: #ccc;
	border: 1px solid #000;
}
.popup_menu .popup_item {
	background: #fff;
	color: #4e4e4e;
}
.popup_menu .popup_item:hover {
	background: #C7DBEE;
	color: #fff;
}

.trow_reputation_positive {
	background: #ccffcc;
}
.trow_reputation_negative {
	background: #ffcccc;
}
.reputation_positive {
	color: green;
}
.reputation_neutral {
	color: #444;
}
.reputation_negative {
	color: red;
}

.invalid_field {
	border: 1px solid #f30;
	color: #f30;
}
.valid_field {
	border: 1px solid #0c0;
}
.validation_error {
	background: url(http://sky9games.com/community/images/invalid.gif) no-repeat center left;
	color: #f30;
	margin: 5px 0;
	padding: 5px;
	font-weight: bold;
	font-size: 11px;
	padding-left: 22px;
}
.validation_success {
	background: url(http://sky9games.com/community/images/valid.gif) no-repeat center left;
	color: #00b200;
	margin: 5px 0;
	padding: 5px;
	font-weight: bold;
	font-size: 11px;
	padding-left: 22px;
}
.validation_loading {
	background: url(http://sky9games.com/community/images/spinner.gif) no-repeat center left;
	color: #555;
	margin: 5px 0;
	padding: 5px;
	font-weight: bold;
	font-size: 11px;
	padding-left: 22px;
}

/* Additional CSS (Master) */

img {
	border: none;
	max-width: 500px;
	max-height: 300px;
}

.clear {
	clear: both;
}

.hidden {
	display: none;
	float: none;
	width: 1%;
}
.hiddenrow {
	display: none;
}

.selectall {
	background-color: #FFFBD9;
	font-weight: bold;
	text-align: center;
}

.float_left {
	float: left;
}
.float_right {
	float: right;
}

.menu ul {
	list-style: none;
	margin: 0;
}
.menu li {
	display: inline;
	padding-left: 5px;
}
.menu img {
	padding-right: 5px;
	vertical-align: top;
}

#panel .links {
	margin: 0;
	float: right;
}

.expcolimage {
	float: right;
	width: auto;
	vertical-align: middle;
	margin-top: 3px;
}

img.attachment {
	border: 1px solid #141414 !important;
	padding: 2px;
}

hr {
	background-color: #999;
	color: #999;
	height: 1px;
	border: 0px;
}

#copyright {
	font: 11px Verdana, Arial, Sans-Serif;
	margin: 0;
	padding: 10px 0 0 0;
}
#debug {
	float: right;
	text-align: right;
	margin-top: 0;
}

blockquote {
	border: 1px solid #141414 !important;
	margin: 0;
	background: #040404;
	padding: 4px;
}
blockquote cite {
	font-weight: bold;
	border-bottom: 1px solid #141414 !important;
	font-style: normal;
	display: block;
	margin: 4px 0;
}
blockquote cite span {
	float: right;
	font-weight: normal;
}
blockquote cite span.highlight {
	float: none;
	font-weight: bold;
	padding-bottom: 0;
}

.codeblock {
	background: #fff;
	border: 1px solid #141414 !important;
	padding: 4px;
}
.codeblock .title {
	border-bottom: 1px solid #141414 !important;
	font-weight: bold;
	margin: 4px 0;
}
.codeblock code {
	overflow: auto;
	height: auto;
	max-height: 200px;
	display: block;
	font-family: Monaco, Consolas, Courier, monospace;
	font-size: 13px;
}

.subforumicon {
	border: 0;
	vertical-align: middle;
}

.separator {
	margin: 5px;
	padding: 0;
	height: 0px;
	font-size: 1px;
	list-style-type: none;
}

form {
	margin: 0;
	padding: 0;
}

.popup_menu .popup_item_container {
	margin: 1px;
	text-align: left;
}
.popup_menu .popup_item {
	display: block;
	padding: 3px;
	text-decoration: none;
	white-space: nowrap;
}
.popup_menu a.popup_item:hover {
	text-decoration: none;
}

.subject_new {
	font-weight: bold;
}

.highlight {
	background: #FFFFCC;
	padding-top: 3px;
	padding-bottom: 3px;
}

.pm_alert {
	background: #FFF6BF;
	border: 1px solid #FFD324;
	text-align: center;
	padding: 5px 20px;
	margin-bottom: 15px;
	font-size: 11px;
}
.red_alert {
	background: #FBE3E4;
	border: 1px solid #A5161A;
	color: #A5161A;
	text-align: center;
	padding: 5px 20px;
	margin-bottom: 15px;
	font-size: 11px;
}
.high_warning {
	color: #CC0000;
}
.moderate_warning {
	color: #F3611B;
}
.low_warning {
	color: #AE5700;
}

div.error {
	padding: 5px 10px;
	border-top: 2px solid #FFD324;
	border-bottom: 2px solid #FFD324;
	background: #FFF6BF;
	font-size: 12px;
}
div.error p {
	margin: 0;
	color: #000;
	font-weight: normal;
}
div.error p em {
	font-style: normal;
	font-weight: bold;
	padding-left: 24px;
	display: block;
	color: #C00;
	background: url(http://sky9games.com/community/images/error.gif) no-repeat 0;
}
div.error.ul {
	margin-left: 24px;
}

.online {
	color: #15A018;
}
.offline {
	color: #C7C7C7;
}

.pagination {
	font-size: 11px;
	padding-top: 10px;
	margin-bottom: 5px;
}
.tfoot .pagination, .tcat .pagination {
	padding-top: 0;
}
.pagination .pages {
	font-weight: bold;
}
.pagination .pagination_current, .pagination a {
	padding: 2px 6px;
	margin-bottom: 3px;
}
.pagination a {
	border: 1px solid #141414;
}
.pagination .pagination_current, .pagination_page, .pagination_last, .pagination_next, .pagination_first, .pagination_previous {
	background: #040404;
	color: #555;
	border: 1px solid #242424;
	font-weight: bold;
}
.pagination a:hover {
	background: #040404;
	border: 1px solid #2a2a2a;
	text-decoration: none;
}

.thread_legend, .thread_legend dd {
	margin: 0;
	padding: 0;
}
.thread_legend dd {
	padding-bottom: 4px;
	margin-right: 15px;
}
.thread_legend img {
	margin-right: 4px;
	vertical-align: bottom;
}
.forum_legend, .forum_legend dt, .forum_legend dd {
	margin: 0;
	padding: 0;
}
.forum_legend dd {
	float: left;
	margin-right: 10px;
}
.forum_legend dt {
	margin-right: 10px;
	float: left;
}

.success_message {
	color: #00b200;
	font-weight: bold;
	font-size: 10px;
	margin-bottom: 10px;
}
.error_message {
	color: #C00;
	font-weight: bold;
	font-size: 10px;
	margin-bottom: 10px;
}

.post_body {
	padding: 5px;
}
.post_content {
	padding: 5px 10px;
	color: #4D4D4D;
	font-family: verdana, geneva, lucida, lucida grande, arial, helvetica, sans-serif;
	font-size: 11px;
}

.quick_jump {
	background: url(http://sky9games.com/community/images/jump.gif) no-repeat 0;
	width: 13px;
	height: 13px;
	padding-left: 13px; /* amount of padding needed for image to fully show */
	vertical-align: middle;
	border: none;
}

.repbox {
	font-size: 16px;
	font-weight: bold;
	padding: 5px 7px 5px 7px;
}
._neutral {
	background-color: #FAFAFA;
	color: #999;
	border: 1px solid #ccc;
}
._minus {
	background-color: #FDD2D1;
	color: #CB0200;
	border: 1px solid #980201;
}
._plus {
	background-color: #E8FCDC;
	color: #080;
	border: 1px solid #080;
}

.pagination_breadcrumb {
	background-color: #f5f5f5;
	border: 1px solid #fff;
	outline: 1px solid #ccc;
	padding: 5px;
	margin-top: 5px;
	font-weight: normal;
}
.pagination_breadcrumb_link {
	vertical-align: middle;
	cursor: pointer;
}

/* Extra ghenny */

.rating_link_a {
	background-color: #111;
	margin-right: 2px;
}
.rating_link_a:hover {
	background-color: #2a2a2a;
}

img[src="http://sky9games.com/community/images/default/buddy_offline.gif"] {
	display: none;
}

::selection {
	background: #999;
	color: #222;
}

/* Image controls? */

a[id^="multiquote_link_"], a[href*="newreply.php?"], a[href^="javascript:Thread.reportPost"], a[href*="editpost.php?"] {
	background-color: #3a3939;
	height: 17px;
	display: block;
	float: left;
	color: #777;
	text-align: center;
	margin-bottom: 1px;
	border: 1px solid #3a3939;
	border-radius: 2px;
}
a[id^="multiquote_link_"]:hover, a[href^="newreply.php?"]:hover, a[href^="javascript:Thread.reportPost"]:hover, a[href*="editpost.php?"]:hover, a[href*="newthread.php?fid="]:hover {
	background-color: #535252;
	border: 1px solid #535252;
	color: #999;
}
a[id^="multiquote_link_"] {
	width: 41px;
	content: "MQuo";
}
a[href*="newreply.php?"][href*="&pid="] {
	width: 73px;
	content: "Quote";
}
a[href*="newreply.php?tid="] {
	width: 73px;
	content: "Reply";
}
a[href^="javascript:Thread.reportPost"] {
	width: 73px;
	content: "Report";
}
a[href*="editpost.php?"][id^="edit_post_"] {
	width: 73px;
	content: "Edit";
}
a[href*="editpost.php?"][id^="quick_delete_"] {
	width: 73px;
	content: "Delete";
}
a[href*="newthread.php?fid="] {
	background-color: #3a3939;
	width: 108px;
	height: 22px;
	padding-top: 3px;
	display: block;
	float: left;
	color: #777;
	text-align: center;
	margin-bottom: 1px;
	border: 1px solid #3a3939;
	border-radius: 2px;
	content: "New Thread";
}
td[class="trow1"][align="center"][valign="top"][width="1"] {
	vertical-align: middle;
}
td[class="trow2"][align="center"][valign="top"][width="1"] {
	vertical-align: middle;
}
img[src$="postbit_multiquote.gif"], img[src$="postbit_quote.gif"], img[src$="postbit_report.gif"], img[src$="newthread.gif"] {
	display: none;
}
img[src$="/on.gif"] {
	content: url('http://kevin.sublimism.com/css/unread.png');
}
img[src$="/off.gif"] {
	content: url('http://kevin.sublimism.com/css/read.png');
}
img[src$="collapse.gif"] {
	content: url('http://kevin.sublimism.com/css/collapse.png');
}
img[src$="collapsed.gif"] {
	content: url('http://kevin.sublimism.com/css/collapsed.png');
}
td[style^="background-color:#E1E9EF;"] {
	background-color: #1c1c32 !important;
}
td[style^="background-color:#FFD9D9;"] {
	background-color: #321c1c !important;
}
td[style^="color:#13376B;"] {
	color: #4a4a7a !important;
}

/* Hidden Text! */

span[style^="color: #E6E6FA;"] {
	color: #1b1b1b !important;
}
}
