    border: none !important;
    background: none !important;
  }

/* Destroy borders only */
 img[src*="cleardot.gif"],
 #cust_link_tbl,
 #footer,
 .boxitem,
  /* Gmail Beta 1 */
 #bc_popup > tbody > tr > td,
 #co > .fs,
 #fbw > #fb,
 .nc > tbody > tr > td > .s,
