css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("picknick.name") {
    body{
        background: #000000 !important;
    }
    
    div{
        /*background: url("") !important;*/
    }
    
    a:hover{
        color: #BF860A !important;
    }
    
    #container{
        width: 1500px !important;
    }
    
    #important{
        background: rgba(100,100,100,0.1) !important;
        box-shadow: 0px 0px 10px orange !important;
        
        margin-left: auto !important;
        margin-right: auto !important;
        margin-top : 5px;
        width: 1002px !important;
        border-radius: 5px;
    }
    
    #memberbox{
        background: none !important;
    }
    
    #footer{
        background: rgba(100,100,100,0.1) !important;
        box-shadow: 0px 0px 10px orange !important;
        
        margin-left: auto !important;
        margin-right: auto !important;
        width: 1002px !important;
        border-radius: 5px;
    }
    
    .box{
        background:none !important;
        border: 0px !important;
        box-shadow: 0px 0px 5px orange !important;
        margin: auto;
        margin-top: 5px;
        margin-bottom: 25px !important;
    }
    
    .box p , a{
        color: #F0F0F0 !important;
    }
    
    .game{
        background: none !important;
        box-shadow: 0px 0px 5px orange !important;
        border: 0px !important;
    }
    #fpage_news_small , #fpage_news_top{
        background: none !important;
        box-shadow: 0px 0px 5px orange !important;
    }
    #fpage_news_top h3 , #fpage_news_top a , #fpage_news_top p , #fpage_news_small h3 , #fpage_news_small p{
        color: #999999 !important;
    }
    #fpage_news_top a:hover{
        color: #BF860A !important;
        text-decoration: none !important;
    }
    #sidebar .box h3, #fpage_left .box h3, #sidebar .box .nolink a, #sidebar .box .nolink a:visited, #sidebar .box .nolink a:active, #sidebar .box .nolink a:hover {
        color: #0A00D1 !important;
    }
    #header #sup{
        color: red !important;
    }
    #header #logotype{
        background:  url("http://static.kottnet.net/picknick.name/logotype_small.png") !important; 
    }
    #sup{
        margin: 5px; 
    }
    #currentavatar{
        box-shadow: 0px 0px 7px orange !important;
        border-radius: 5px;
        margin:5px;

    }
    #memberbox{
        width:312px !important;
    }
    #logotype{
        margin-left: 0px !important;
    }
    #content{
        color:#999999 !important;
    }
    #header #menubox{
        background: none !important;
    }
    #menutop1 , #menutop2 , #menutop3{
        background: #1C8EFF !important;
        margin-top: 20px;
        box-shadow: 0px 0px 7px orange !important;
        border-top-right-radius: 10px;
        border-top-left-radius: 10px;
    }
    #popup1 , #popup2 , #popup3{
        background: #1C8EFF !important;
        box-shadow: 0px 0px 7px orange !important;
        border-bottom-left-radius: 10px !important;
        border-bottom-right-radius: 10px !important;
    }
    #newforum  , #newpm , #newfriend{
        background: #1C8EFF !important;
        height: 20px !important;
        width: 90%;
        margin-left: auto;
        margin-right: auto;
    }
    #newforum{
        border-top-right-radius: 10px;
        border-top-left-radius: 10px;
        box-shadow: 0px 0px 7px orange;
        margin-top: 4px;
    }
    #newpm{
        box-shadow: 0px 0px 7px orange;
    }
    #newfriend{
        border-bottom-right-radius: 10px;
        border-bottom-left-radius: 10px;
        box-shadow: 0px 0px 7px orange;
    }
    #box1 , #box2 , #box3{
        background: none !important;
        border: 1px dotted orange;
    }
    #profile_left p{
        color: red !important;
    }
    .memberinfo p{
        color: red !important;
    }
    .middle{
        border: 1px dashed orange;
    }
    .image{
        border: 2px solid orange !important;
        box-shadow: 1px 1px 5px orange !important;
    }
    #profile_menu a{
        background: gray !important;
    }
    .middle{
        background: none !important;
    }
    .bottom{
        background: none !important;
    }
    .top{
        background: none !important;
    }
    td , tr{
        background: none !important;
        border-bottom: 1px dashed orange;
        box-shadow: 0px 2px 3px orange !important;
    }
    .forumheader{
        background: none !important;
        border: 1px solid orange;
    }
    .cat_forumcontent{
        background: none !important;
        border: 1px solid orange !important;
        border-top: 0px !important;
    }
    .cat_first , .cat_second , .cat_third , .cat_fourth{
        background: none !important;
    }
    
}
