css:
@-moz-document domain('news.ycombinator.com') {

/* Reset all current rules */
/* Colours */
/* grey - #828282 */
body, td, .subtext td, a:visited, .default, .title, .adtitle, .subtext, .yclinks,
.comhead, .subtext a:link, .subtext a:visited, .comhead a:link, .subtext a:visited
{
  color: black !important;
}
/* black - #000000 */
.admin td, input, textarea, a:link, .admin, .pagetop a:visited {
  color: black !important;
}
/* white - #FFFFFF */
.topsel a:link, .topsel a:visited {
  color: black !important;
}
/* dark grey - #22222 */
.pagetop {
  color: black !important;
}
/* faded grey - #DDDDDD */
.dead, .dead a:link, .dead a:visited {
  color: black !important;
}

/* Font */
/* Family */
/* Veranda */
body, td, .admin td, .subtext td, input[type="\"submit\""], .default, .admin,
.title, .adtitle, .subtext, .yclinks, .pagetop, .comhead, .comment, .dead {
  font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Roboto, Arial, sans-serif !important;
}
/* Courier */
input, textarea {
  font-family: "Andale Mono", AndaleMono, Consolas, monaco, monospace;
}
/* Size - everything to 16pt */
/* 10pt */
body, td, input, textarea, .default, .title, .pagetop {
  font-size: 16pt !important;
  line-height: 1.2 !important;
}
/* 9pt */
.adtitle, .comment, .dead {
  font-size: 16pt !important;
  line-height: 1.2 !important;
}
/* 8.5pt */
.admin td, .admin {
  font-size: 16pt !important;
  line-height: 1.2 !important;
}
/* 8pt */
.yclinks, .comhead {
  font-size: 16pt !important;
  line-height: 1.2 !important;
}
/* 7pt */
.subtext td, .subtext {
  font-size: 16pt !important;
  line-height: 1.2 !important;
}
/* Get rid of html font overrides */
font {
  font-family: Calibri, Candara, Segoe, "Segoe UI", Optima, Roboto, Arial, sans-serif !important;
  font-size: 16pt !important;
  line-height: 1.2 !important;
}

/* Text decoration - remove all underline */
/* Underline */
.comment a:link, .comment a:visited, .subtext a:hover, .comhead a:hover {
  text-decoration: none !important;
}
/* None */
a:link, a:visited, u {
  text-decoration: none !important;
}

/* Other rules, repeated here for reference */
.default p {
    margin-bottom: 0;
    margin-top: 8px;
}
.pagebreak {
    page-break-before: always;
}
pre {
    max-width: 100% !important; /* Was 600px */
    overflow: auto;
    padding: 2px;
}
pre:hover {
    overflow: auto;
}



/* New rules */
/* Get rid of table layout */
body {
  margin: 0;
  min-width: 320px;
}
table, tr, td, tbody {
  display: block !important;
  width: auto !important;
}
/* Colours */
table {
  background-color: white !important;
}
/* Links to match new colour scheme */
a:link, a:visited, .topsel a:link, .topsel a:visited,
.subtext a:link, .subtext a:visited, .comhead a:link, .subtext a:visited {
  color: #f67636 !important;
}
.pagetop a:visited {
  color: white !important;
}


/* Heading - all pages */
/* General */
td[bgcolor="#ff6600"] > table {
  background-color: #f67636 !important;
  padding: 8px !important;
}
/* Logo, replace current with css one */
td[bgcolor="#ff6600"] > table > tbody > tr > td:first-of-type {
  display: none !important;
}
td[bgcolor="#ff6600"] > table > tbody > tr > td:nth-child(2) .pagetop b a:after {
  content: "Y";
  margin-right: 0.75em;
  border: 2px solid white;
  color: white;
  background-color: #f67636;
  width: 24px;
  height: 24px;
  font-size: 20px !important;
  display: inline-block;
  text-align: center;
  font-family: "Arial Narrow", sans-serif;
}
/* Link bar */
td[bgcolor="#ff6600"] > table > tbody > tr > td:nth-child(2) {
  line-height: 1.2 !important;
  height: auto !important;
  margin-right: 8em !important;
}
td[bgcolor="#ff6600"] > table > tbody > tr > td:nth-child(2) .pagetop {
  color: #f67636 !important;
}
td[bgcolor="#ff6600"] > table > tbody > tr > td:nth-child(2) .pagetop > b > a {
  font-size: 0 !important;
}
td[bgcolor="#ff6600"] > table > tbody > tr > td:nth-child(2) .pagetop a {
  color: white !important;
  padding: 0 0.5em 0 0;
}
/* Login bar */
td[bgcolor="#ff6600"] > table > tbody > tr > td:nth-child(3) {
  position: absolute !important;
  right: 8px !important;
  top: 8px !important;
  background-color: #f67636 !important;
}
td[bgcolor="#ff6600"] > table > tbody > tr > td:nth-child(3) .pagetop {
  color: #f6ba9b !important;
}
td[bgcolor="#ff6600"] > table > tbody > tr > td:nth-child(3) .pagetop a {
  color: white !important;
  padding: 0 0.5em 0 0;
}



/* Footer */
body > center > table > tbody > tr:last-of-type span.yclinks {
  color: white !important;
}
body > center > table > tbody > tr:last-of-type span.yclinks a {
  color: #f67636 !important;
  margin: 0 0.5em !important;
}


/* Main body */
body > center > table > tbody > tr:nth-child(3) {
  margin: 1em;
}




/* News page */
@-moz-document regexp('http.*news\\.ycombinator\\.com/'),
regexp('http.*news\\.ycombinator\\.com/news'),
regexp('http.*news\\.ycombinator\\.com/news\\d*'),
regexp('http.*news\\.ycombinator\\.com/x\\?.*') {
/* List items */
/* Hide numbers and spacers */
body > center > table > tbody > tr:nth-child(3) > td > table > tbody > tr:nth-child(3n+1) > td:nth-child(1) {
  display: none !important;
}
body > center > table > tbody > tr:nth-child(3) > td > table > tbody > tr:nth-child(3n) {
  display: none !important;
}
/* Upvote */
body > center > table > tbody > tr:nth-child(3) > td > table > tbody > tr:nth-child(3n+1) > td:nth-child(2) {
  float: left;
  padding-top: 3px;
}
body > center > table > tbody > tr:nth-child(3) > td > table > tbody > tr:nth-child(3n+1) > td:nth-child(2) > center > a:before {
  content: "▲";
  padding-right: 0.5em;
}
body > center > table > tbody > tr:nth-child(3) > td > table > tbody > tr:nth-child(3n+1) > td:nth-child(2) > center {
  padding-right: 0.5em;
}
body > center > table > tbody > tr:nth-child(3) > td > table > tbody > tr:nth-child(3n+1) > td:nth-child(2) center > a > img {
  display: none !important;
}
/* Title line and link */
body > center > table > tbody > tr:nth-child(3) > td > table > tbody > tr:nth-child(3n+1) > td:nth-child(3) {
  padding-right: 6em !important;
  border-bottom: 1px dotted #ccc;
  margin-left: 3em !important;
  text-indent: -1em !important;
}
body > center > table > tbody > tr:nth-child(3) > td > table > tbody > tr:nth-child(3n+1) > td:nth-child(3) a {
  color: #f67636 !important;
}
/* Comments line */
body > center > table > tbody > tr:nth-child(3) > td > table > tbody > tr:nth-child(3n+2) {
  margin-bottom: 2em;
}
body > center > table > tbody > tr:nth-child(3) > td > table > tbody > tr:nth-child(3n+2) > td,
body > center > table > tbody > tr:nth-child(3) > td > table > tbody > tr:nth-child(3n+2) > td font {
  font-size: 0 !important;
}
body > center > table > tbody > tr:nth-child(3) > td > table > tbody > tr:nth-child(3n+2) > td > a:last-of-type {
  font-size: 16pt !important;
  color: #f67636 !important;
  float: right !important;
  position: relative;
  top: -1.2em;
  background-color: white !important;
}
}



/* Comments page */
@-moz-document regexp('http.*news\\.ycombinator\\.com/item\\?.*') {
/* Submit comment */
body > center > table > tbody > tr:nth-child(3) > td > table:first-of-type {

}
/* Comments */
body > center > table > tbody > tr:nth-child(3) > td > table:last-of-type {

}
/* Each comment */
body > center > table > tbody > tr:nth-child(3) > td > table:last-of-type > tbody > tr {
  border-bottom: 1px dotted #ccc;
  padding-bottom: 0.8em !important;
}
/* Indent */
body > center > table > tbody > tr:nth-child(3) > td > table:last-of-type > tbody > tr > td > table > tbody > tr:first-of-type > td > img {
  height: 0 !important;
  width: 0 !important;
  margin-right: 0.5em !important;
  padding-right: 64px !important;
  padding-bottom: 32px !important;
  background-repeat: no-repeat;
  float: left !important;
  background-image: url(data:image/png;base64,);
}
body > center > table > tbody > tr:nth-child(3) > td > table:last-of-type > tbody > tr > td > table > tbody > tr:first-of-type > td > img[width="40"] {
  background-image: url(data:image/png;base64,);
}
body > center > table > tbody > tr:nth-child(3) > td > table:last-of-type > tbody > tr > td > table > tbody > tr:first-of-type > td > img[width="80"] {
  background-image: url(data:image/png;base64,);
}
body > center > table > tbody > tr:nth-child(3) > td > table:last-of-type > tbody > tr > td > table > tbody > tr:first-of-type > td > img[width="120"] {
  background-image: url(data:image/png;base64,);
}
body > center > table > tbody > tr:nth-child(3) > td > table:last-of-type > tbody > tr > td > table > tbody > tr:first-of-type > td > img[width="160"] {
  background-image: url(data:image/png;base64,);
}
body > center > table > tbody > tr:nth-child(3) > td > table:last-of-type > tbody > tr > td > table > tbody > tr:first-of-type > td > img[width="0"] {
  background-image: none !important;
}
/* Upvote */
body > center > table > tbody > tr:nth-child(3) > td > table:last-of-type > tbody > tr td[valign="top"] {
  float: left;
  padding-top: 3px;
}
body > center > table > tbody > tr:nth-child(3) > td > table:last-of-type > tbody > tr td[valign="top"] > center > a:before {
  content: "▲";
  padding-right: 0.5em;
}
body > center > table > tbody > tr:nth-child(3) > td > table:last-of-type > tbody > tr td[valign="top"] > center {
  padding-right: 0.5em;
}
body > center > table > tbody > tr:nth-child(3) > td > table:last-of-type > tbody > tr td[valign="top"] center > a > img {
  display: none !important;
}
/* Central comment */
body > center > table > tbody > tr:nth-child(3) > td > table:last-of-type > tbody > tr td.default {
  margin-right: 2em;
}
body > center > table > tbody > tr:nth-child(3) > td > table:last-of-type > tbody > tr td.default > div {
  margin-bottom: -0.8em !important;
}
/* Reply */
body > center > table > tbody > tr:nth-child(3) > td > table:last-of-type > tbody > tr td.default p font u a {
  float: right !important;
  position: relative;
  top: -0.8em;
  right: -2em;
  background-color: white !important;
}
}



}
