css:
@-moz-document url("about:home") {

body{
    background:url('http://i1339.photobucket.com/albums/o712/kalxd/Stylish/sample_0a600ae9219ae93e183cdeeb37ba3e7f4c4cbe82_zps2aac0a73.jpg') !important;
    background-position:center !important;
    background-size:100% !important;
}

#brandLogo{
    display:none !important;
}

#searchForm{
    background:rgba(120,200,200,.5) !important;
    border-radius:5px !important;
    border:7px rgba(255,255,255,.2) double !important;
    padding:30px 20px !important;
    box-shadow:0 0 10px 5px rgba(0,255,255,.5) !important;
    -moz-transition:all .5s ease-in-out;
}
#searchForm:hover{
    background:rgba(255,255,255,.3) !important;
    box-shadow:0 0 20px 5px rgba(0,255,225,.8) !important;
}

#searchText{
    -moz-appearance:none !important;
    background:rgba(255,255,255,.3) !important;
    border-radius:5px !important;
    border:none !important;
    border-bottom:2px #0ff solid !important;
    margin-right:20px !important;
    box-shadow:0 0 10px 2px rgba(0,255,200,.2) inset !important;
    -moz-transition:all 1s ease-in-out;
}
#searchText:hover,
#searchText:focus{
    background:rgba(255,255,255,.7) !important;
    box-shadow:1px 1px 10px 2px rgba(0,255,255,.3) !important;
    text-shadow:0 0 3px rgba(255,255,255,.5) !important;
}

#searchSubmit{
    -moz-appearance:none !important;
    background:linear-gradient(rgba(255, 255, 255, 0.8), rgba(255, 255, 255, 0.1)) !important;
    border-radius:5px !important;
    border:1px #0ff solid !important;
    color:black !important;
}
#searchSubmit:hover{
    background:linear-gradient(rgba(255, 255, 255, 0.8), rgba(255, 255, 255, 0.1)) !important;
    box-shadow:0 0 5px 2px #0ff !important;
    color:black !important;
    text-shadow:0 0 1px gray !important;
}

#snippetContainer{
    display:none !important;
}

.launchButton{
    border-radius:10px !important;
    -moz-transition:all .5s ease-in-out;
}

.launchButton:hover{
    background:-moz-linear-gradient(top,rgba(200,200,200,.8),rgba(255,255,255,.2)) !important;
    border-bottom:1px #0ff solid !important;
    box-shadow:0 0 5px 3px rgba(0,255,255,.7) !important;
}
}
