css:
@-moz-document url-prefix("https://www.facebook.com/"), url-prefix("http://www.facebook.com/") {
body {
}
a {
  color:#555555;
}
/* LOGIN PAGE BACKGROUND */
.fbIndex.UIPage_LoggedOut,
.login_page.UIPage_LoggedOut,
.registration.UIPage_LoggedOut {
  background: url("http://i.imgur.com/rgL7AT5.jpg") !important;
  background-position:center center!important;
  background-repeat:no-repeat !important;
  background-attachment:fixed !important;
  background-color: transparent !important;
  background-size:100% 100%!important;
}
/* MAINPAGE BACKGROUND */
.hasLeftCol.home.composerExpanded.fbx {
  background: url("http://i.imgur.com/rgL7AT5.jpg") !important; 
  background-position: center center!important;
  background-repeat:no-repeat !important;
  background-attachment:fixed !important;
  background-color: transparent !important;
  background-size:100% 100%!important;
}
/* STATUS PAGE BACKGROUND */
.permalinkBody.adunitRedesign,
.ego_page.fbx {
  background: url("http://i.imgur.com/rgL7AT5.jpg") !important; 
  background-position: center center!important;
  background-repeat:no-repeat !important;
  background-attachment:fixed !important;
  background-color: transparent !important;
  background-size:100% 100%!important;
}
/* START FB LOGIN */
.fbIndex.UIPage_LoggedOut .inputtext{
  background: rgba(255,255,255,0.8);
  border-radius:3px;
  width: 150px !important;
  text-align: center !important;
}
.fbIndex.UIPage_LoggedOut .inputtext:focus {
  background: rgba(255,255,255,0.8);
  border-radius:3px;
  border-color: rgba(51, 166, 255, 0.6) rgba(39, 145, 225, 0.6) rgba(0, 122, 204, 0.6) !important;
}
.loggedout_menubar_container { 
  background: url('http://fiakka.com/images/blank_transparent.png') !important;
}

.fbIndex.UIPage_LoggedOut #globalContainer #dropmenu_container,
.fbIndex.UIPage_LoggedOut #globalContainer #content,
.fbIndex.UIPage_LoggedOut #globalContainer #pageFooter ,
.login_page.UIPage_LoggedOut #globalContainer #dropmenu_container,
.login_page.UIPage_LoggedOut #globalContainer #pageFooter {
  display: none !important;
}
.fbIndex.UIPage_LoggedOut .loggedout_menubar_container {
  position: fixed !important;
  width:450px !important;
  height: 82px !important;
  min-width: 0px !important;
  top: 40% !important;
  left: 48% !important;
  margin-top: -17px !important;
  margin-left: -210px !important;
  z-index: -1 !important;
  text-align: center !important;
}
.fbIndex.UIPage_LoggedOut .loggedout_menubar ,
.login_page.UIPage_LoggedOut .login_page_interstitial{
  background: rgba(0,0,0,0.3) !important;
  border-radius: 7px !important;
  padding:15px 25px 25px 15px !important;
  width: auto !important;
  box-shadow: rgba(0,0,0,0.35) 5px 5px 5px; 
}
.fbIndex.UIPage_LoggedOut .loggedout_menubar_container .lfloat,
.fbIndex.UIPage_LoggedOut .loggedout_menubar_container .rfloat {
  float: none !important 
}
.fbIndex.UIPage_LoggedOut .loggedout_menubar_container .lfloat img,
.fbIndex.UIPage_LoggedOut .loggedout_menubar_container .rfloat #login_form table {
  display: block !important;
  margin: 0 auto !important;
}
.fbIndex .loggedout_menubar_container .lfloat img {
  display: block; 
  margin: -60px auto 20px !important; 
}
#SetAsHomepage_Callout {
  display: none;
}
.login_page.UIPage_LoggedOut .loggedout_menubar_container {
  display: none!important;
}
/* END FB LOGIN */

/* START AD BLOCK */
#pagelet_ego_pane_w,
.ego_column,
.UIStandardFrame_SidebarAds {
  display:none!important;
}
/* END AD BLOCK */

/* START BLUE BAR */
#blueBar.fixed_elem {
  width:100%;
  margin:0 auto !important;
  background: -webkit-linear-gradient(top, rgba(50,50,50,1),rgba(50,50,50,0))!important;
  border:none!important;
  box-shadow:none!important;
  transition: padding-bottom .3s linear;
  -moz-transition: padding-bottom .3s linear;
  -webkit-transition: padding-bottom .3s linear;
}
#blueBar.fixed_elem:hover {
  padding-bottom:5px;
}
#pageHead a {
  border-radius: 5px !important;
  transition: background-color .3s linear;
  -moz-transition: background-color .3s linear;
  -webkit-transition: background-color .3s linear;
}
#pageHead a:hover  {
  background-color: rgba(255,255,255,0.5)!important;
}

.notifNegativeBase .fbJewelFlyout, #tinymanNavWrapper .fbJewelFlyout {
  background-color: rgba(255,255,255,0.8)!important;
}
.notifNegativeBase .fbJewelFlyout .jewelFooter a {
  background-color: rgba(255,255,255,0)!important;
}
.fbJewelFlyout .jewelFooter a {
  background-color: rgba(255,255,255,0)!important;
}
div.pagesVoiceBar {
  background: rgba(0,0,0,0.5) !important;
  box-shadow: 0px 0px 10px rgba(0,0,0,0.8) !important;
  width:500px !important;
  margin-left:600px !important;
}
div.pagesVoiceBarText  {
  width: 500px !important;
  margin-left: 50px !important;
  position: fixed !important;
}
span.headerTinymanName , a.navLink.bigPadding {
  color:rgb(255, 255, 255)!important;
}
ul#userNavigation.navigation {
  opacity:0.8;
}
ul.fbTimelineScrubber.fixed_elem {
  margin-top:10px !important;
}
/* END BLUE BAR */

/* START LEFT BAR */

.clearfix.fbxWelcomeBox {
  background: -webkit-linear-gradient(left, rgba(255,255,255,1),rgba(255,255,255,1),rgba(255,255,255,0.5), rgba(255,255,255,0))!important;
  margin: 5px !important;
}
.uiSideNav .item,
.uiSideNav .subitem,
.uiSideNav .highlighted .item,
.uiSideNav ul .highlighted .subitem{
  border:none!important;
  -webkit-transition: all .3s ease !important;
  -moz-transition: all .3s ease !important;
  transition: all .3s ease !important;
}
.uiSideNav .selectedItem .item,
.uiSideNav .selectedItem .item:hover,
.uiSideNav ul .selectedItem .subitem,
.uiSideNav ul .selectedItem .subitem:hover {
  background-color:rgba(255,255,255,0.9)!important;
}
.uiSideNav .item:hover,
.uiSideNav .item:active,
.uiSideNav .item:focus,
.uiSideNav .subitem:hover,
.uiSideNav .subitem:active,
.uiSideNav .subitem:focus,
.uiSideNav .highlighted .item:hover,
.uiSideNav ul .highlighted .subitem:hover {
  border-left-style:solid;
  border-left-width:1px;
  border-left-color:rgb(180,180,180);
  background-color:rgba(255,255,255,0.8)!important;
}
.homeSideNav {
  border-style:solid;
  border-right:none;
  border-width:2px;
  border-radius: 5px 0 0 5px;
  border-color:rgb(255,255,255);
  background: -webkit-linear-gradient(right, rgba(255,255,255,0),rgba(255,255,255,0.75), rgba(255,255,255,1))!important;
  margin:5px 0 5px;
}
#pagelet_friends_online {
  display:none!important;
}
.navHeader, .navHeader a {
  margin-left:10px;
  margin-top:2px;
}
/* END LEFT BAR */

/* START RIGHT BAR */

#pagelet_reminders , .mtl {
  border-style:solid;
  border-left:none;
  border-width:2px;
  border-radius:0 5px 5px 0;
  border-color:rgb(255,255,255);
  background: -webkit-linear-gradient(left, rgba(255,255,255,0),rgba(255,255,255,0.75), rgba(255,255,255,1))!important;
}
.rhcFooterBorder {
  display:none!important;
}
/* END RIGHT BAR */

/* START HOME STATUS BOX */
.hasLeftCol.home.composerExpanded.fbx #pagelet_composer,
.hasLeftCol.home.composerExpanded.fbx #pagelet_group_composer {
  background-color:rgba(255, 255, 255, 0.5);
  margin: 5px !important;
  box-shadow: 2px 2px 10px rgba(0,0,0,0.8) !important;
  border-radius: 10px;
}
.hasLeftCol.home.composerExpanded.fbx ._1dsl, {
  padding: 10px 0 0 10px!important;
}
.hasLeftCol.home.composerExpanded.fbx ._118 {
  background:none!important;
  padding-bottom:5px!important;
}
.hasLeftCol.home.composerExpanded.fbx ._1dsp {
  background:rgba(255,255,255,0.4)!important;
}
.hasLeftCol.home.composerExpanded.fbx ._4n ._2wr {
  display:none!important;
}
.hasLeftCol.home.composerExpanded.fbx ._2yg, 
.hasLeftCol.home.composerExpanded.fbx ._1dsm
{
  margin:5px 5px 2px 5px;
  border-color:#FFFFFF;
  border-radius:10px;
}
.hasLeftCol.home.composerExpanded.fbx ._42g-,
.hasLeftCol.home.composerExpanded.fbx ._42fu{
  background-image:none!important;
  background-color:rgba(150,150,150,0.4)!important;
  border:none!important;
  color:#000!important;
}
                    
/* END STATUS BOX */

/* START NEWS FEED STREAM */
.hasLeftCol.home.composerExpanded.fbx #contentCol {
  background:none !important;
}
.hasLeftCol.home.composerExpanded.fbx ._4ks > li {
  border:none !important;
}
.hasLeftCol.home.composerExpanded.fbx #contentCol {
  border:none !important;
}
.hasLeftCol.home.composerExpanded.fbx .uiUnifiedStory {
  background-color:rgba(255, 255, 255, 0.5);
  margin: 5px !important;
  box-shadow: 2px 2px 10px rgba(0,0,0,0.8) !important;
  border-radius: 10px;
  -webkit-transition: background-color .3s ease !important;
  -moz-transition: background-color .3s ease !important;
  transition: background-color .3s ease !important;
}
.hasLeftCol.home.composerExpanded.fbx .uiUnifiedStory:hover {
  background-color:rgba(255, 255, 255, 0.9);
}
.hasLeftCol.home.composerExpanded.fbx ul.uiStream {
  margin: 15px 0 15px !important;
}
.hasLeftCol.home.composerExpanded.fbx .uiStream .uiStreamHeader {
  background:none!important;
}
.hasLeftCol.home.composerExpanded.fbx .uiStream .uiStreamHeader .uiStreamHeaderChronologicalForm,
.hasLeftCol.home.composerExpanded.fbx .uiStream .uiStreamHeader .uiStreamHeaderText{
  background:none!important;
}
.hasLeftCol.home.composerExpanded.fbx .uiStreamRedesign .uiStreamStory .actorPhoto ,
.hasLeftCol.home.composerExpanded .uiStreamRedesign .uiStreamStory .substoryActorPic {
  margin-left:5px!important;
}
.hasLeftCol.home.composerExpanded .uiStreamRedesign .uiStreamStory .actorPhoto .img ,
.hasLeftCol.home.composerExpanded ._rx.img{
  border-radius:50%!important;
}
.hasLeftCol.home.composerExpanded.fbx .uiStream .uiStreamStory .highlightSelector {
  margin: 10px 30px 0 0;
}
.hasLeftCol.home.composerExpanded.fbx .uiStreamStory {
  padding-left:0px !important;
}
.hasLeftCol.home.composerExpanded.fbx .UFIRow {
  background-color:rgba(255, 255, 255, 0.5);
}
.hasLeftCol.home.composerExpanded.fbx #mainContainer {
  border:none!important;
}
.hasLeftCol #pageFooter {
  display:none!important;
}
/* END NEWS FEED STREAM */

/* START PHOTO VIEWER */
.fbPhotoSnowlift .rhc {
  background: -webkit-linear-gradient(left, rgba(255,255,255,1),rgba(255,255,255,0.8), rgba(255,255,255,0.5))!important;
}
/* END PHOTO VIEWER */

/* START STATUS PAGE */
.permalinkBody.adunitRedesign .hasRightCol #contentArea {
  background-color:rgba(255, 255, 255, 0.5);
  box-shadow: 2px 2px 10px rgba(0,0,0,0.8) !important;
  border-radius: 10px;
  -webkit-transition: background-color .3s ease !important;
  -moz-transition: background-color .3s ease !important;
  transition: background-color .3s ease !important;
}
.permalinkBody.adunitRedesign .hasRightCol #contentArea:hover {
  background-color:rgba(255, 255, 255, 0.9);
}
.permalinkBody.adunitRedesign .UFIRow,
.permalinkBody.adunitRedesign .fbTimelineFeedbackActions{
  background-color:rgba(255, 255, 255, 0.5);
}
.permalinkBody.adunitRedesign.noFooter.fbx .fbTimelineUFI {
  background:none!important;
}
.permalinkBody.adunitRedesign .hasRightCol #rightCol {
  background:none!important;
  border:none!important;
}
.ego_page.fbx #fbPhotoPageHeader {
  background-color:rgba(255, 255, 255, 0.6);
  box-shadow: 2px 2px 10px rgba(0,0,0,0.8) !important;
  border-radius: 10px;
  padding:3px 20px 3px 20px;!important;
  margin-bottom:10px;!important;
}
.ego_page.fbx .fbxPhoto .fbPhotoImageStage .stageContainer {
  background-color: rgba(0,0,0,0)!important;
  height: 0px!important;
}
.ego_page.fbx .photoUfiContainer {
  background-color:rgba(255, 255, 255, 0.6);
  box-shadow: 2px 2px 10px rgba(0,0,0,0.8) !important;
  border-radius: 10px;
  margin-top:20px;
  padding-left:10px;
}
.ego_page.fbx .UFIRow {
  background-color:rgba(255, 255, 255, 0.5);
}
.ego_page.fbx .photoDetailsContainer {
  background-color:rgba(255, 255, 255, 0.6);
  box-shadow: 2px 2px 10px rgba(0,0,0,0.8) !important;
  border-radius: 10px;
  margin-top:20px;
}
.ego_page.fbx #pageFooter {
  display:none!important;
}
/* END STATUS PAGE */
}
