css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(tankafetast.com), domain(www.tankafetast.com) {
div#com_1 img, div#com_2 img, div#footer, ul#navi_top li b, iframe {
    display:none !important}
/*[href="https://pirateweb.net/Pages/Public/SE/NewMember/New.aspx"], [href="http://www.houseofshirts.se/10-tankafetast"] {
    display:none !important}*/
div.col_100>a img {
    display:none !important}
div#main_content, div#wrapper  {
    width: 1280px !important;
    margin-left: -80px !important;}
div#main_content {
    margin-left: -1px !important;}
table.list tbody td {
    line-height: 17px !important;
    font-weight: bold !important;}
p.pages {
    width: 62% !important;
    padding-top: 10px !important; 
    margin: 0 28.25% auto !important; position: relative !important;}
div.col_25  {
    margin-left:0px !important;
    width: 210px !important;}
div.col_75 {
    margin-left:10px !important;;
    width: 1030px !important;}
div.col_65 {
    width: 860px !important;}
div#comments.col_100, div.box2  {
    width: 1278px !important;}
/*body {transform: scale(1.2,1.1920);}*/
#search {
    margin-top: 100px !important;
    background-color:  transparent !important;
    background: -moz-linear-gradient(top,  rgba(206,220,231,1) 0%, rgba(206,220,231,0.5) 46%, rgba(206,220,231,0.51) 47%, rgba(89,106,114,1) 100%), 
    -moz-linear-gradient( left, rgba(0,9,9,.5), rgba(255,255,255,0) 10%, rgba(255,255,255,0) 90%, rgba(0,9,9,.5))!important;  
    background: -webkit-linear-gradient(top,  rgba(206,220,231,1) 0%, rgba(206,220,231,0.5) 46%, rgba(206,220,231,0.51) 47%, rgba(89,106,114,1) 100%), 
    -webkit-linear-gradient( left, rgba(0,9,9,.5), rgba(255,255,255,0) 10%, rgba(255,255,255,0) 90%, rgba(0,9,9,.5))!important;  
    box-shadow:  inset 1px 1px 1px #888, inset -1px 0px 1px #000 !important;
    border: transparent!important;
    border-radius: 3px 3px 3px 3px !important;}
body, div#wrapper, div#main_content, div#main_content div.col_100 td {
    margin-top: 0px !important;
    background: #ddd !important;  }
.submenu ul {
    border-top: none !important;
    border-bottom: 1px solid rgba(105,117,124,0.25) !important;
    border-left: 1px solid rgba(105,117,124,0.25)!important;
    border-right: 1px solid rgba(105,117,124,0.25) !important;
    border-bottom-left-radius: 3px !important;
    border-bottom-right-radius: 3px !important; 
    background-color:  transparent !important;
    background: -moz-linear-gradient(top, rgba(139,156,164,1) 0%,rgba(226,240,251,1) 100%), 
    -moz-linear-gradient( left, rgba(0,9,9,.5), rgba(255,255,255,0) 10%, rgba(255,255,255,0) 90%, rgba(0,9,9,.5))!important;
    background: -webkit-linear-gradient(top, rgba(139,156,164,1) 0%,rgba(226,240,251,1) 100%), 
    -webkit-linear-gradient( left, rgba(0,9,9,.5), rgba(255,255,255,0) 10%, rgba(255,255,255,0) 90%, rgba(0,9,9,.5))!important;
    box-shadow: 0px 0px 2px rgba(245,245,245,0.45), inset 0px 0px 5px rgba(0,0,0,0.10) !important;
    -moz-margin-start:-5px!important;
    -webkit-margin-start:-5px!important;}
.submenu ul li {
    padding: 8px 2px!important;}
span.btn{
    min-width: 22px !important;
    max-width: 22px !important;
    text-align: center !important;
    color:darkblue!important;
    font-size: 13px!important;
    font-weight: bold !important;
    text-shadow: 0px 0px 1px white !important;
    margin: 0px 0px 3px 0px!important;
    background: #f9f9f9 !important; 
    box-shadow:  0px 0px 2px #666, inset 1px 1px 5px #fff !important;
    border: 1px solid rgb(245, 245, 245)!important;
    border-radius: 3px !important;}
span.btn.blue{
    min-width: 72px !important; }
span.grey, span.btn.blue {
    color: #666 !important;
    font-size: 12px!important;
    background: #f0f0f0  !important;
    border: 1px solid rgb(230, 230, 230)!important;
    box-shadow:  0px 0px 1px #444, inset 1px 1px 5px #fff !important;}
span.grey:hover, span.btn.blue:hover{
    color: darkblue!important;
    background: #f9f9f9  !important; 
    box-shadow:  0px 0px 2px #666, inset 1px 1px 5px #fff !important;
    border: 1px solid rgb(245, 245, 245)!important;}
a.btn.blue, a.btn {
    line-height: 22px !important;
    text-align: center !important;
    color:#555!important;
    font-size: 13px!important;
    font-weight: bold !important;
    text-shadow: 0px 0px 1px white !important;
    margin: 0px 0px 3px 0px!important;
    background: #f9f9f9 !important; 
    box-shadow:  0px 0px 2px #666, inset 1px 1px 5px #fff !important;
    border: 1px solid rgb(245, 245, 245)!important;
    border-radius: 3px !important;}
ul#navi_top {
    font-weight: bold !important;
    width: 45% !important;
    margin-left: 390px !important;}
div#navi_top_wrapper {
    background-color:  transparent !important;
    background: -moz-linear-gradient(top,  rgba(206,220,231,1) 0%, rgba(206,220,231,0.5) 46%, rgba(206,220,231,0.51) 47%, rgba(89,106,114,1) 100%), 
    -moz-linear-gradient( left, rgba(0,9,9,.5), rgba(255,255,255,0) 10%, rgba(255,255,255,0) 90%, rgba(0,9,9,.5))!important;
    background: -webkit-linear-gradient(top,  rgba(206,220,231,1) 0%, rgba(206,220,231,0.5) 46%, rgba(206,220,231,0.51) 47%, rgba(89,106,114,1) 100%), 
    -webkit-linear-gradient( left, rgba(0,9,9,.5), rgba(255,255,255,0) 10%, rgba(255,255,255,0) 90%, rgba(0,9,9,.5))!important;
    box-shadow:  inset 1px 1px 1px #888, inset -1px 0px 1px #000 !important;
    border: transparent!important;
    border-radius: 3px 3px 3px 3px !important;}
div#logo a img{
    border-radius: 3px !important;
    box-shadow:   0px 0px 1px #777,  0px 1px 2px #000 !important;}
ul#top_nav li a, h6, a.browser_style{
    font-size: 13px !important;
    font-weight: bold !important;
    color: #888!important;}
div.col_100 a {
    font-weight: bold !important;
    color: #888!important;}
ul#top_nav li a:hover, a.browser_style:hover, div.col_100 tr a:hover, div.col_30 li a:hover, body a:hover {
    color: darkblue!important;}
body a.hover, body a.selected, ul#navi_top li a:hover{
    border-radius: 3px !important;
    background: transparent !important; 
    box-shadow:  inset 0px -1px 1px #222, inset 0px 0px 1px #fff !important;
    color: #fff!important; padding-top: 8px !important;padding-bottom: 8px !important;
    text-shadow: 1px 1px 1px #000 !important;}
div#main_content table.list thead {
    background-color:  transparent !important;
    background: -moz-linear-gradient(top,  rgba(206,220,231,1) 0%, rgba(206,220,231,0.5) 46%, rgba(206,220,231,0.51) 47%, rgba(89,106,114,1) 100%), 
    -moz-linear-gradient( left, rgba(0,9,9,.5), rgba(255,255,255,0) 10%, rgba(255,255,255,0) 90%, rgba(0,9,9,.25))!important;
    background: -webkit-linear-gradient(top,  rgba(206,220,231,1) 0%, rgba(206,220,231,0.5) 46%, rgba(206,220,231,0.51) 47%, rgba(89,106,114,1) 100%), 
    -webkit-linear-gradient( left, rgba(0,9,9,.5), rgba(255,255,255,0) 10%, rgba(255,255,255,0) 90%, rgba(0,9,9,.25))!important;  
    box-shadow:   1px 0px 1px #222,  -1px 0px 1px #222 !important;
    border-radius: 1px !important;
    font-weight: bold !important;}
ul.accordion li:first-child, ul.accordion li:last-child, ul.accordion li , a.tfbtn, .btn.tfgreen, .tfbtn.tfgreen {
    border-radius: 3px !important;
    margin-right:5px !important;
    font-size: 13px!important;
    font-weight: bold !important;
    text-shadow: 0px 0px 1px white !important;
    background: #f9f9f9 !important; 
    box-shadow:  0px 0px 1px #666, inset 1px 1px 5px #fff !important;
    border-radius: 3px !important}
ul.accordion li a, a.tfbtn, .btn.tfgreen, .tfbtn.tfgreen {
    min-width: 100px !important;
    border-radius: 3px !important;
    border: 1px solid rgb(245, 245, 245)!important;
    font-weight: bold !important;
    color: #555 !important;
    text-align: center !important;}
ul.accordion li a:hover, a.tfbtn:hover, .btn.tfgreen:hover, .tfbtn.tfgreen:hover {
    border-radius: 3px !important;
    font-weight: bold !important;
    color: darkblue!important;
    background: #f9f9f9  !important; 
    box-shadow:  0px 0px 2px #666, inset 1px 1px 5px #fff !important;
    border: 1px solid rgb(245, 245, 245)!important;}
div.box, div.box2, tbody, table.forum {
    background: rgb(230,233,241) !important;}
div#main_content tbody tr{
    background: none repeat scroll 0% 0% rgb(230,233,241) !important;
    border-left: 1px solid transparent !important;
    border-right: 1px solid transparent !important;}
table.list tbody tr:nth-child(2n) {
    background: none repeat scroll 0% 0% rgb(242,247,251) !important; }   
div#main_content table.list tbody tr:hover{
    border-left: 1px solid rgb(240,243,251) !important;
    border-right: 2px solid rgb(240,243,251) !important; 
    background: none repeat scroll 0% 0% rgb(240,243,251) !important;}
}
