css:
html {
	min-height:100%;
}

body.fbIndex {
	background: url("http://images.wikia.com/habbo/en/images/archive/2/26/20100710160458!Background.png")repeat !important;
	overflow:hidden;
}

.loggedout_menubar_container {
	background:none !important;
}
.loggedout_menubar {
	background: #ffffff;
    background: url(data:image/svg+xml;base64,);
    background: -moz-linear-gradient(top, #ffffff 0%, #f2f2f2 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#ffffff), color-stop(100%,#f2f2f2));
    background: -webkit-linear-gradient(top, #ffffff 0%,#f2f2f2 100%);
    background: -o-linear-gradient(top, #ffffff 0%,#f2f2f2 100%);
    background: -ms-linear-gradient(top, #ffffff 0%,#f2f2f2 100%);
    background: linear-gradient(to bottom, #ffffff 0%,#f2f2f2 100%);
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#ffffff', endColorstr='#f2f2f2',GradientType=0 );) !important;
	padding:0 10px 14px 10px;

	-webkit-border-radius: 4px;
	border-radius: 4px;

	-webkit-box-shadow: 0 0 0 3px rgba(51,51,51,.2);
	
	box-shadow: 0 0 0 3px rgba(51,51,51,.2);
	
	border: #999 1px solid;
}
.loggedout_menubar label,
.login_form_label_field a {
	color:#fff !important;
}
.fbIndex #globalContainer #dropmenu_container,
.fbIndex #globalContainer #content,
.fbIndex #globalContainer #pageFooter {
	display: none !important
}

.fbIndex .loggedout_menubar_container {
	position: fixed !important;
	width: 563px !important;
	height: 201px !important;
	min-width: 0 !important;
	min-height: 201px !important;
	top: 40% !important;
	left: 45% !important;
	margin: -60px 0 0 -210px !important;
	z-index: -1 !important;
}


.fbIndex .loggedout_menubar {
	width: auto !important;
}
.fbIndex .loggedout_menubar_container .lfloat,
.fbIndex .loggedout_menubar_container .rfloat {
	float: left !important;
}
.fbIndex .loggedout_menubar_container .lfloat img,
.fbIndex .loggedout_menubar_container .rfloat #login_form table {
	display: block !important;
	margin-top: -110px !important;
	margin-left: 120px !important;
	background: rgba(51,51,51,.3) !important;
	padding:10px !important;
	-webkit-border-radius: 4px !important;
	border-radius: 4px !important;
	
	text-shadow:#333 0 0 1px;
}

.fbIndex .loggedout_menubar_container .lfloat img {
	display: block;
	margin: -60px auto 20px !important;
        
}

#SetAsHomepage_Callout {
	  display: none;
}

.fb_logo {
	background: url("http://i48.tinypic.com/vo7dy1.png") no-repeat !important;
	width: 452px !important;
	height:228px !important;
	float:left !important;
	
	margin-top:-50px !important;
}

