css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.tv.nu"){

.nav__body * { color: white !important; }

.page-wrapper {
    max-width: 100%!important;
    width: calc((100% / /*[[antalkol]]*/)*(/*[[antalkol]]*/ - 1)) !important;}

.one-quarter, .three-twelfths, .two-eighths {
    width: calc(100% / /*[[antalkol]]*/) !important;}

.page {
    background: transparent !important;
    float: left !important;
    position: relative!important;
    width: 100%!important;
    max-width: 100%!important;
    height: 100%!important;}

.js-cookie-notice, .inset-top--medium.palm-content-hide img, li.grid__item.portable-content-hide, .ad-area, .sidebar, .newsTicker, .menu_right .serach_container, IFRAME, #adpanorama2.listing, #popup_advertise, #top_banner, .page_right, #footer, .bubble-container, span.stream, .play_container h3, .play_content_area.clear h2, #eas-panorama3, .site_container:before, .site_container div.adsplaceholder_label, #eas-panorama2, .app-banner, .icon.text-line-inherit.float-left.inset-right--tiny.color-brand, .box.box--flush.indent-top--flush.indent-bottom--large, .ad--mega-2, .ad-top-streamer, .play-provider-carousel__wrapper {
    display: none !important;}

.app-banner-root{
    margin-top: 0px !important;}


/*[[lullull]]*/
}

style_setting_options.value:
html{
    font-family: Calibri !important;
    font-size: 1.07em !important;}

body, html {
    background: url("/*[[bakgrund]]*/")                                                                                            
    center bottom no-repeat !important;     
    background-size: cover !important;      
    background-attachment: fixed !important;}

.page__header, .page__footer {
    background: rgba(253,181,31,.7) !important;
    border: 1px solid rgba(253, 181, 31, 0.7);}

.page-search{
    background-color: transparent !important;}

.color-brand-misc, .color-brand-misc .text-normal{
    color: #fff!important;
    font-weight: normal!important;}

.color-brand-secondary, .ui-select{
    text-decoration: none !important;
    color: blue !important;}

HTML > BODY > DIV:nth-child(4) > DIV:first-child > MAIN > DIV > DIV:first-child > DIV:nth-child(3) > DIV > DIV > DIV:nth-child(2) > DIV > DIV:nth-child(2) > NAV > DIV, 
HTML > BODY > DIV:nth-child(4) > DIV:first-child > MAIN > DIV > DIV:nth-child(n) > DIV:first-child > DIV > DIV > DIV:nth-child(2) > DIV > DIV:nth-child(2) > NAV > DIV,
HTML > BODY > DIV:nth-child(4) > DIV:first-child > MAIN > DIV > DIV:first-child > DIV:last-child > DIV > DIV > DIV:nth-child(2) > NAV > DIV,
HTML > BODY > DIV:nth-child(4) > DIV:first-child > MAIN > DIV > DIV:first-child > DIV > DIV > DIV:nth-child(2) > NAV > DIV{
    border: 2px solid;
    -moz-border-bottom-colors:  rgba(155,155,155,1) rgba(0,0,0,5)!important;
    -moz-border-left-colors: rgba(250,250,250,1) rgba(255,255,255,.65)!important;
    -moz-border-right-colors:  rgba(155,155,155,1) rgba(0,0,0,.5)!important;
    -moz-border-top-colors:  rgba(250,250,250,1) rgba(255,255,255,.7)!important;
    border-radius: 1px !important;background: rgba(225,225,225,1) !important;}

.pack--middle > .pack__item a{
    text-transform: none !important;
    text-decoration: none !important;
    font-weight: normal!important;
    opacity: 1 !important;}

.pack--middle > .pack__item a:hover {
    text-decoration: none !important;
    font-weight: normal!important;
    opacity: 1 !important;}

.page-nav__push a:hover, a:hover, .color-gentle:hover{
    text-decoration: none !important;
    transition: none !important; 
    color: blue !important;}

.epsilon, h5{
    text-decoration: none !important;
    color: white !important;}

.epsilon:hover, h5:hover{
    text-decoration: none !important;
    color: blue!important; 
    font-weight: normal!important;}

div.box.box--emphasize.box--medium.box--flush-vertical{ background: rgba(255,255,255,.8) !important;}

.broadcast-scrollable__item--inactive {
    color: #888!important;}

.broadcast-scrollable__item--inactive .icon {
    color: #aaa!important;}

li.broadcast-scrollable__item--active:nth-child(n) > a:nth-child(n) > div:nth-child(n) > div:nth-child(n) > time:nth-child(n){font-weight: bold!important;}

.grid--flush > .one-whole{ 
    border-bottom: 1px solid rgba(150, 150, 150, 0.3)!important;}

.broadcast-scrollable__item a, .broadcast-scrollable--has-active > .broadcast-scrollable__item{
    background: rgba(250,250,250,.01) !important; 
    border-top: 1px solid rgba(255, 255, 255, 0.5)!important;
    border-bottom: 1px solid rgba(0, 0, 0, 0.1)!important;
    border-left: 1px solid rgba(155,155,155, 0.4)!important;
    border-right: 1px solid rgba(155,155,155, 0.4)!important;}

.broadcast-scrollable__item--active a, .broadcast-scrollable__item a:hover, .button.button--submit.text-bold.js-channel-guide__button {
    background: linear-gradient(to top, rgba(255,255,255,.95) 0%,rgba(245,245,245,.8) 100%) !important;
    color: blue !important;}

.broadcast-scrollable__item{
    border-top: 1px solid transparent !important;
    border-bottom: 1px solid transparent !important;}

.box--tiny {
    padding: 1px !important;}

.box--tiny .text-ellipsis {
    padding-left: 5px !important;}

.box--emphasize-smooth, .box--emphasize--invert {
    background-color: transparent !important;
    background: rgba(0,0,0,.0) !important;}

span.text-bold {
    font-weight: normal !important;}

div.one-fifth{
    border: 1px solid #bbb!important;
    border-radius: 2px !important;
    background: rgba(255,255,255,.6) !important;
    height: 32px !important;}

div.one-fifth .normal{ 
    color: #0000FF !important;}

.dropdown.dropdown--alternative_accent .dropdown__trigger{
    padding-top: 5px !important;
    border: 1px solid #bbb!important;
    border-radius: 2px !important;
    background: rgba(255,255,255,.6) !important; 
    width: 100px !important;
    height: 32px !important;
    font-weight: normal !important; 
    color: #0000FF !important;}
 
.palm-switchable__top.indent-bottom--medium, .js-content .indent-bottom--medium, div.box--flush-vertical:nth-child(1), .ad--tws  {
    background: rgba(255,255,255,.8) !important; 
    border: rgba(232,232,232,1) 1px solid !important; }

.indent-bottom--medium a.color-gentle {
    color: #0000FF !important;}

a:focus {
    outline: none !important;}

.modal-overlay--view.animate--in, .modal-overlay.animate--in {
    background: rgba(0,0,0,.9) !important;}

.modal__window, .bg-page-bg {
    background: none!important;}

.dialog__overlay { 
    background: rgba(0,0,0,.5) !important;}

.broadcast-indent {
    margin-right: 0px !important;
    margin-left: 0px !important;}

.palm-switchable__top > * , .broadcast{
    background: linear-gradient(to bottom, rgba(0,0,0,0) 0%,rgba(0,0,0,0) 100%) !important;}

li.channel-schedule:nth-child(n) > div:nth-child(n){
    background: linear-gradient(to bottom, rgba(255,255,255,.8) 0%,rgba(255,255,255,.8) 100%) !important;
    border: rgba(232,232,232,1) 1px solid !important;
    padding-left: 1px !important; 
    padding-right: 1px !important;}

h1.delta, h4{
    color: white !important;}

.action-button--circle {
    border-radius: 2px !important;}

.page-search__input {
    border-radius: 2px !important;}

button.\[{
    background: blue !important; 
    border-radius: 1px !important;}

#newsletter-email {
    border-radius: 1px !important;}

.action-button--invert{
    color: blue !important;}

span.palm-content-hide.color-brand-secondary {
    font-weight: normal !important;}

.action-button--chevron {
    background: rgba(50, 50, 50, 0.8) none repeat scroll 0% 0%;
    border: 1px solid rgba(0, 0, 255, .7)!important;
    box-shadow: 0px 0px 3px rgba(50, 50, 50, 0.8)!important;}

.action-button--chevron:hover, .positioner--pull-bottom .positioner__body:hover {
    background: rgba(0,0,255,.8) !important;
    border: 1px solid rgba(40, 40, 40, 1)!important; 
    box-shadow: 0px 0px 2px blue!important;}

.ui-checkbox input + label {
    border: 1px solid blue !important;
    color: blue !important;
    border-radius: 2px !important;}

.ui-checkbox input:checked + label {
    background: blue !important;
    color: #FFF !important;}

.color-gentle-extreme{
    color: #FFF!important;
    font-weight: bold!important;}

.epsilon, .epsilon:hover, h3.zeta, .text-uppercase, .text-uppercase:hover, .color-gentle:hover{
    color: #666!important;
    font-weight: normal !important;}

h3.zeta.text-ellipsis, .box--medium .color-brand-secondary, .float-right .epsilon{
    color: #FFF !important;}

.float-right .epsilon:hover, .box--medium .color-brand-secondary:hover, .box--medium .bg-light {
    color: blue !important;}

section {
    background: rgba(0,0,0,.3) !important;}

.button--submit {
    background: blue !important;}

.button--facebook, .button--invert-text, .button--submit, .button--submit-invert, .form [type="submit"] {
    color: #EEE!important;}

.button--facebook:hover, .button--invert-text:hover, .button--submit:hover, .button--submit-invert:hover, .form [type="submit"]:hover {
    color: #FFF !important;} 

.text-uppercase .text-bold:hover {
    color: blue !important;}

.button {
    border-radius: 1px !important;}

.pack--auto .color-brand-secondary {
    color: #000 !important;
    font-weight: bold !important;
    table-layout: auto;}

span.palm-content-hide.color-brand-secondary.epsilon, .inset > a:nth-child(1) {
    color:#fff !important;}

span.palm-content-hide.color-brand-secondary.epsilon:hover, .inset > a:nth-child(1):hover {
    color:#0000ff !important;}

.broadcast-scrollable__timeline::before {
    padding-top:1px !important;
    background-color: transparent !important; }

.broadcast-scrollable__timeline {
    margin-top: 2px !important;margin-bottom: 2px !important;
    box-shadow: 0px -1px 0px rgba(253, 201, 51, 0.8), 0px 1px 0px rgba(150, 150, 150, 0.8)!important;
    border-right: rgba(100,100,100,.25) solid 1px !important;
    border-radius: 0px 5px 5px 0px !important; height: 4px !important;
    background: linear-gradient(to top,  rgba(253,181,31,1) 0%,rgba(253,181,31,.25) 100%)!important;}

.action-button--chevron  {
    color: #0000FF !important;}

.action-button--chevron:hover  {
    color: #FFF !important;}

.action-button--chevron{ 
    background: rgba(255,255,255,.7) !important;}

.tabbox__checkbox:checked ~ .tabbox__label {
    border-bottom-color: #0000FF;}

.tabbox__label{
    color: #191919;}

.tabbox__label:hover{
    color: #0000FF;}

span.icon.color-gentle-extreme.milli {color: transparent !important;}

.dropdown.dropdown--alternative_accent .dropdown__trigger  { 
    -moz-appearance:none !important; 
    border: transparent !important;
    height: 38px !important; 
    background:  url(data:image/png;base64,) no-repeat center !important; 
    background-size: 38px !important; 
    text-indent: -9999px !important;}

.dropdown.dropdown--alternative_accent .dropdown__trigger:active{
    border: 2px solid;
    -moz-border-top-colors:  rgba(255,255,255,.65) rgba(0,0,0,1)!important;
    -moz-border-right-colors: rgba(255,255,255,.7) rgba(0,0,0,.6)!important;
    -moz-border-left-colors:  rgba(255,255,255,.65) rgba(0,0,0,.8)!important;
    -moz-border-bottom-colors:  rgba(255,255,255,.7) rgba(0,0,0,.6)!important;
    background: none, url(data:image/png;base64,) no-repeat center !important; 
    background-size: 36px !important;}

.search__label, .circle {color: blue !important;
    border-radius: 2px !important;}

.search__input, .search__input:focus {
    border-radius: 1px !important;}

.grid--rev > .grid__item {
       text-align: right !important;}

.text-center.color-brand-secondary.text-op5.milli.content-show--b.hoverable.hoverable--semi-gentle.lineheight-date {background: rgba(0,0,0,.02)!important; border: 1px solid;
    -moz-border-top-colors:  rgba(255,255,255,.65) rgba(0,0,0,.1)!important;
    -moz-border-right-colors: rgba(255,255,255,.7) !important;
    -moz-border-left-colors:  rgba(255,255,255,.8) !important;
    -moz-border-bottom-colors:  rgba(255,255,255,.7) rgba(0,0,0,.3)!important;}

.pack__item.one-fifth.palm-one-whole.text-center {border: 1px solid;
    -moz-border-top-colors:  rgba(155,155,155,.65) rgba(0,0,0,1)!important;
    -moz-border-right-colors: rgba(155,155,155,.35) rgba(255,255,255,.65) !important;
    -moz-border-left-colors:  rgba(155,155,155,.35) rgba(255,255,255,.65)!important;
    -moz-border-bottom-colors:  rgba(0,0,0,.4) rgba(55,55,55,.7) !important;
    background: rgba(250, 250, 250,1) !important; border-radius:1px !important;}

p.indent-bottom--medium .gamma {
    display: none !important;}

.subnav, a.subnav__item {
    background:none !important;
    border-bottom: 1px solid transparent;
    color: white !important; 
    font-weight: bold!important;}

a.subnav__item:hover, .nav__body *:hover {
    color: #0000FF !important;}

.three-quarters{
    width: 87% !important;}

@-moz-document domain("blogg.tv.nu") {

#nav a, .nav__item, .nav__item--active {
    color: white !important; 
    font-weight: bold!important;}

#wrapper a:hover {
    color: blue !important; 
    font-weight: normal !important;}

span {
    text-decoration: none !important;}

}
