css:
@-moz-document domain("twitch.tv") {
#left_close, #right_close {
    border: 1px solid rgba(33, 33, 33, 0.25)!important;
    box-shadow: 1px 1px 1px rgb(33,33, 33), 1px 1px 1px rgb(33, 33, 33) inset !important;
    background: url("http://herpderp.com/twi/collapse1.png") no-repeat scroll 0px 0px transparent !important; 
}

#left_close:hover, #right_close:hover {
    border: 1px solid rgba(33, 33, 33, 0.25)!important;
    box-shadow: 2px 2px 2px rgb(33,33, 33), 2px 2px 2px rgb(33, 33, 33) inset !important;
    background: url("http://herpderp.com/twi/collapse2.png") no-repeat scroll 0px 0px transparent !important;
}


.segmented_tabs li a.selected, .segmented_tabs li a:active {
    box-shadow: 0px 1px 0px rgba(33, 33, 33, 0.65), 0px 1px rgba(0, 0, 0, 0.05) inset !important;
    background: -moz-linear-gradient(center top , rgb(44, 44, 44), rgb(22, 22, 22)) repeat scroll 0% 0% rgb(22, 22, 22) !important;
    background: -webkit-gradient(linear,left top,left bottom,from(rgb(44,44,44)),to(rgb(22, 22, 22))) !important;
}



#channel_panels_contain.masonry .panel {
    margin: 5px 5px 30px !important;
    background: -o-linear-gradient(top, #323232, #282828) #303030!important;
    background: -moz-linear-gradient(center top , rgb(50, 50, 50), rgb(40, 40, 40)) repeat scroll 0% 0% rgb(90, 90, 90) !important;
    background: -webkit-gradient(linear,left top,left bottom,from(rgb(50,50,50)),to(rgb(40, 40, 40))) !important;
    border-radius: 0.334em!important;
    padding:5px!important;
    color: #ccc!important;
}

#channel_panels_contain #channel_panels .panel h3 {
    color:#fefefe!important;
}



    A[class="mod_button ban"] {margin-right:2px!important;padding-top:2px!important;background-repeat:no-repeat!important;background-position:center center!important;background-image:url('http://www.herpderp.com/twitch/ban.png') !important;}
    IMG[src*="g18_ban-00000080"] {visibility:hidden!important;}
    A[class="mod_button unban"] {margin-right:2px!important;padding-top:2px!important;background-repeat:no-repeat!important;background-position:center center!important;background-image:url('http://www.herpderp.com/twitch/unban.png') !important;}
    IMG[src*="g18_unban-00000080"] {visibility:hidden!important;}
    A[class="mod_button timeout"] {padding-top:2px!important;background-repeat:no-repeat!important;background-position:center center!important;background-image:url('http://www.herpderp.com/twitch/clock.png') !important;}
    IMG[src*="g18_clock-00000080"] {visibility:hidden!important;}
    Span[class*="tag"] { vertical-align:middle!important; }
    Span[class*="tag broadcaster"] { vertical-align:middle!important; background-color:#1f1f1f!important;}



#player_col, #right_col{
    background-color:black!important;
    color:#ddd!important;
}


#chat_line_list > .line > p > [style*=black] {
    color: #777 !important;
}

#chat_line_list > .line > p > [style*="color:#000000"] {
    color: #777 !important;
}

#control_input textarea {
    background-color: #050505!important; 
    color: #c8c8c8!important; 
    box-shadow: 1px 1px 1px rgb(33,33, 33), 1px 1px 1px rgb(33, 33, 33) inset !important;
}

#right_col .content #chat_lines #chat_line_list{
    height: auto;
    background: none repeat scroll 0% 0% rgb(0, 0, 0)!important;
    color:#ccc!important;
}

#right_col .content{
    border-left:1px solid #151515!important;
}


#controls {
    background: none repeat scroll 0% 0% rgb(0, 0, 0)!important;
    border-top: 1px solid rgba(0, 0, 0, 0.65)!important;
    padding: 10px 10px 0px;
}

#chat_lines li:nth-child(even) { 
  	background-color: rgba(255,255,255,.25);
}


#twitch_chat{
    background: none repeat scroll 0% 0% rgb(0, 0, 0)!important;
    color:#ccc!important;
}


.scroll-scrollbar .drag-handle {
    background: none repeat scroll 0% 0% rgb(90, 90, 90)!important;
}


#right_col .segmented_tabs li .tab:not(.selected) { 
    box-shadow: 0px 1px 0px rgba(33, 33, 33, 0.65), 0px 1px rgba(0, 0, 0, 0.05) inset !important;
    background: -o-linear-gradient(top, #424242, #212121) #222222!important;
    background: -moz-linear-gradient(center top , rgb(66, 66, 66), rgb(33, 33, 33)) repeat scroll 0% 0% rgb(33, 33, 33) !important;
    background: -webkit-gradient(linear,left top,left bottom,from(rgb(66,66,66)),to(rgb(33, 33, 33))) !important;
    color: #fff!important;
    text-shadow: 0px 1px 0px rgba(0, 0, 0, 0.35)!important;
}


div.title_over {
    margin: 0px!important;
    background: -o-linear-gradient(top, #424242, #212121) #222222!important;
    background: -moz-linear-gradient(center top , rgb(50, 50, 50), rgb(40, 40, 40)) repeat scroll 0% 0% rgb(90, 90, 90) !important;
    background: -webkit-gradient(linear,left top,left bottom,from(rgb(50,50,50)),to(rgb(40, 40, 40))) !important;
    border-radius: 0.334em!important;
    padding:5px!important;
    color: #fff!important;
}



#right_col .content #archives .video a .title {
    color: #c8c8c8!important;
}

#archives .scroll-content-contain, #more_videos_spinner {
	background-color: rgb(16,16,16) !important;
 }

 .thumb:hover, .js-more_archives:hover {
	background-color: rgb(35, 35, 35) !important;
 }

 #archives .video a {
	border-bottom: 1px solid rgba(25, 25, 25, 0.75) !important;
	border-top: 1px solid rgba(25, 25, 25, 0.75) !important;
}

#archives .more_videos .js-more_archives, #more_videos_spinner {
	border-top: 1px solid rgb(25, 25, 25) !important;
    box-shadow: 1px 1px 1px rgba(33, 33, 33, 0.65), 1px 1px 1px rgba(0, 0, 0, 0.05) inset !important;
    background: -moz-linear-gradient(center top , rgb(50, 50, 50), rgb(40, 40, 40)) repeat scroll 0% 0% rgb(90, 90, 90) !important;
    background: -webkit-gradient(linear,left top,left bottom,from(rgb(50,50,50)),to(rgb(40, 40, 40))) !important;
}

#archives .more_videos .js-more_archives:hover, #more_videos_spinner:hover {
	border-top: 1px solid rgb(25, 25, 25) !important;
    box-shadow: 1px 1px 1px rgba(33, 33, 33, 0.65), 1px 1px 1px rgba(0, 0, 0, 0.05) inset !important;
    background: -moz-linear-gradient(center top , rgb(80, 80, 80), rgb(60, 60, 60)) repeat scroll 0% 0% rgb(90, 90, 90) !important;
    background: -webkit-gradient(linear,left top,left bottom,from(rgb(50,50,50)),to(rgb(40, 40, 40))) !important;
    color:#8663C7!important;
}



#right_col .content .top #right_nav {
    background: none repeat scroll 0% 0% rgb(16, 16, 16)!important;
}

.channel-main {
    background: none repeat scroll 0% 0% rgb(0, 0, 0)!important;

}


.real_title{
    color:white!important;
}


.betabar {
    display:none!important;
}
.channel-main {
    position: absolute;
    top: 0px!important;
}

#player_col .content #custom_bg {
    visibility:hidden;
}

#custom_bg {
    visibility:hidden;
}


.turbo a {
    background-color: #4060A5!important;
}

#chat_line_list {
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif !important;
    font-size:1.0em !important;
    line-height:1.50 !important;
}
}
