css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.last.fm/music") {
/* Hide */
#hero-video.hero-video,
div.g4.artist-action-area,
div.album-details.g3,
section.artist-events,
div.g4.g--padded.artist-bio-sidebar,
div.g8.artist-listening-trend,
div.artist-listening-trend.g12,
section.artist-top-albums,
li#originalsNav.navItem,
a.btn.btn--brand.on-tour,
section.ecommerce-actions.remove-bottom-margin,
div.actions-bar,
p.ringtone,
div.user-actions.with-love-button,
section.share-wrapper.remove-bottom-margin,
aside.g4 /* track-page element */
{display: none !important;}

/* Header */
#fauxHeader, 
#lastfmLogo, 
#primaryNav .nav-link:hover, 
#profileLinks .divider a, 
#loginLink .profile-link, 
#loginLink .profile-link strong, 
#idBadgerUser, #idBadgerDropper, 
#siteSearchBox, 
#siteSearchSubmit, 
#headerLangToggle img.globe_icon 
{
    background-image: none !important;
}


/* Dark BG */
article.content.artist-overview,
article.content.track-overview,
div.artist-social.r.r--full-bleed,
div.track-social.r.r--full-bleed
{
    background: #222 !important;
    color: #666 !important;
}
/* Black BG */
div.catalogue-scrobble-graph.needs-js,
.hero-missing--upload,
section.listener-details.listener-details--listening-now.remove-bottom-margin,
section.listener-details.remove-bottom-margin,
div.page-head-bar.page-head-bar--did-you-mean,
p.memo,
div.memo,
section.memo
{
    background: #000 !important;
    color: #666 !important;
}
/* Red BG */
section.listener-details.listener-details--in-library.remove-bottom-margin
    {
        background: #000 !important;
        color: red !important;
    }

/* No BG-color */
.tags a,
.tag a
    {background-color:#222 !important;}

/* Borders */
article.content.artist-overview,
article.content.track-overview
    {border:1px solid #111 !important;}

    /* Border-top black */
    .page-head .crumb-wrapper h1
        {border-top:1px solid #111 !important;}

/* 100% width */
div #header.clearit.responsive-container,
div#headerWrapper,
div.g8
{width: 100% !important;}
    /* Elements */
        .g8
        {width: 1200px !important;}

/* Max-width */
.content /* track-page */
{max-width:1600px !important;}

    /* Other version? */
    .responsive-container
        {max-width:1600px !important;}

/* Friends Who Listen to-pictures kleiner */
    .user-listen-counts .userImage img:first-child
    {
        width:30px !important;
        height:30px !important;
    }
    .user-listen-counts li
    {
        margin:0px 0px 0px 70px !important;
        min-height: 30px !important;
    }

}
