css:
/* @name allfreesoft.blog fc2 オールフリーソフト更新情報 ja jp 2013/02 */
@-moz-document domain("all-freesoft.net"){
/*== all-freesoft.net ==*/
/*- unwanted contents -*/
body>div>br,
table[summary="スポンサード リンク"],
body>div>iframe,
#b-box>div:nth-child(2),
#d-box>div[style="text-align:left;"]
 {display: none !important;}
/*- fit to window -*/
table,
#site-box, #a-box, #d-box {
 width: 100% !important; min-width: 784px;
 margin: 0 !important; padding: 0 !important;
 border: none !important;
 }
#b-box, #c-box {width: 50% !important; min-width: 392px;}
#c-box>div>form#cse-search-box>div>input {max-width: 386px !important;}
}

@-moz-document domain("allfreesoft.blog75.fc2.com"){
/*- unwanted contents -*/
*[id^="sh_fc2"], * [class^="sh_fc2"], *[id^="ad"],
div.page_navi+div.content /* sponsored link */
 {display: none !important;}

/*- page default -*/
html {top: 0 !important;}
body {font-family : inherit !important; font-size: inherit !important;}
/*- 3colomns relayout -*/
div#container {width: inherit !important; min-width: 800px;}
div#header {
 height: auto !important;
 margin: 0 !important; padding: 0 !important; border: none !important;
 }
div#wrap { /*- wrap= center+left -*/
 width: 75% !important; min-width: 600px; margin: 0 !important; padding: 0 !important;
 }
div#main { /*- center (in wrap) -*/
 width: 65% !important; min-width: 390px;
 padding: 0 !important;
 border: none !important;
 }
div#sidemenu2 { /*- right -*/
 width: 25% !important; min-width: 200px;
 margin: 0 !important; padding: 0 !important;
 border: none !important;
 }
div#sidemenu1 { /*- left (in wrap) -*/
 width: 35% !important; min-width: 210px;
 padding: 0 !important;
 border: none !important;
 }
div#footer {height: auto !important; padding: 0 !important;}
/*- article header fix -*/
h2.entry_header, h3.entry_header {background-repeat: repeat !important;}
/*- right side bar fix 2013/02/17 -*/
p.plugin-search>br {display: none !important;} /* search box */
.twtr-doc {width: 100% !important;} /* twitter widget */
}
