css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')  

 {

ol#posts li.post.new_post {background: #ffffff url('http://i.imgur.com/DWLJ8nl.png') center center no-repeat;}

img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{width:0; height:70px; padding-right:250px; opacity: 0;}







}



@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://www.tumblr.com') {



img[src*="http://assets.tumblr.com/images/post_icons.png"],

.new_post_label_icon {

    opacity: 0 !important;

}



.new_post_label {

  opacity: 1 !important;

}



ol#posts .post.new_post .new_post_label {

    display: block !important;

}



.new_post_label{



font-size:0px !important;

padding-top:70px !important;

font-family:georgia !important;

font-style:bold !important;

text-transform: uppercase !important;

color:#fff !important;

letter-spacing:1px !important

}





}
