body.page, body.popup, .titelg {
    background-image:url("http://static.allmystery.de/upics/9428de_fa39c8_pb.JPG");
    background-size: 100% 100% !important;
    background-attachment:fixed;
    }

.header {
    background: url(http://static.allmystery.de/upics/643e26_f3ec3c_002.JPG) no-repeat;
    background-size: 100% 100%;
    border-radius: 10px 10px; 
    border: 0px solid transparent;
    box-shadow: 0px 0px 40px #A80000;
    }

.tabnav a {
    background-color: transparent;
    border-bottom: 1px solid transparent;
    border-top: 1px solid transparent;
    color: #ffffff;
    }

.tabnav a:hover, a.tabnav_selected, a.tabnav_js_selected {
    background-color: #000000;
    border-radius: 4px 4px; 
    border: 0px solid transparent;
    box-shadow: 0px 0px 40px #A80000;
    color: #ffffff;
    }

.mcell {
    background: url(http://static.allmystery.de/upics/a07624_ddc94a_001.JPG) no-repeat;
    background-size: 100% 100%;
    border-radius: 5px 5px; 
    border: 0px solid transparent;
    box-shadow: 0px 0px 15px #A80000;
    }


.mcell_inhalt, .mcell2, .mcell_left, .mcell_inhalt2, .mcell_inhalt3, .mcell_inhalt4  {
    background-color: #000000;
    border-radius: 5px 5px; 
    border: 0px solid transparent;
    box-shadow: 0px 0px 15px #A80000;
    }
  
a.at {
    color: #ffffff;
    }

a.at:hover, a.at_bold:hover {
    color: #ff0000;
    }

.sufu {
    border: 1px solid transparent;
    border-radius: 7px 7px; 
    box-shadow: 0px 0px 15px #A80000;
    }

body {
    color: #ffffff;
    }

a {
    color: #ffffff;
    }

.at_in {
    color: #ffffff;
    }

.sub_info_p, .sub_info_p_check {
