    border-radius: 10px 10px; 
    border: 0px solid transparent;
    box-shadow: 0px 0px 15px #A80000;
    }

.finfo {
    border-right: solid 1px transparent;
    }

.fbeitrag {
    border-left: solid 1px transparent;
    }

.header_info {
    background-color: #000000;
    border-radius: 10px 10px; 
    border: 0px solid transparent;
