#chat_messages {
    background-color: #000000;
    border-radius: 10px 10px; 
    border: 0px solid transparent;
    box-shadow: 0px 0px 15px #A80000;
    }

div.post.warn {
    background-color: rgba(152,0,0,0.8) !important;
    box-shadow:inset 50px 20px 80px #ff0000 !important;
    }

