css:
@-moz-document url-prefix("http://friendfeed.com") {
@font-face {
	font-family:"BBCNassim";
	src: url( "http://lab.3hh.ir/negro/wp-content/themes/negroid/fonts/BBCNassim.eot" );
	src: local( "â˜º" ),
	url( "http://lab.3hh.ir/negro/wp-content/themes/negroid/fonts/BBCNassim.woff" )
	format( "woff" ),
	url( "http://lab.3hh.ir/negro/wp-content/themes/negroid/fonts/fonts/BBCNassim.ttf" )
	format( "truetype" );
}
body, .box .box-bar .box-bar-text, .box .box-bar .box-corner, #sidebar .box .box-body {font-family: 'BBCNassim';background:black;}
.box .box-bar, .box .box-bar .box-corner, .box .box-bar.friends, .box .box-bar.friends .box-corner, .box .box-bar.groups, .box .box-bar.groups .box-corner, .box .box-bar.search, .box .box-bar.search .box-corner, .box .box-top, .box .box-bottom, .box .box-corner, .box .box-bar, .box .box-bar .box-corner, #body .box .box-top, #body .box .box-bottom, #body .box .box-corner {background-image:none !important;}
td.logo {background: url('http://dl.dropbox.com/u/49769829/nano-logo.png') no-repeat;}
.subscribebar {border-color:black; background:black;}
.logo img {opacity:0;}
.logo {background: url('http://dl.dropbox.com/u/49769829/nano-logo.png') no-repeat; width:227px;height:50px;}
img.picture.medium {border-radius: .3em;}
.sharebox .post input[type='submit'], .buttons input[type='submit'] {background:none; color:white; border:none;}
.profile .header h1 a.name, .subscribebar, .box .box-bar .box-bar-text a, #sidebar a {color:white;}
a, .profile .subscribe, .entry .info a, .entry .likes a, div.photo, .entry .comment a, .entry .expandcomment a, .profile .header .description, .feedformats, #sidebar .box .box-body a, .subscribebar a, .subscribebar .g, a  {color:#c4c4c4;}
span.searchbold {color:#b10000;}
.sharebox .title textarea, div.textbox, textarea, input.textbox, ul.l_tolist, .l_toinput, .l_toinput, .entry .commentform textarea {background:#ebebeb; font-family:'BBCNassim'; border-radius:.5em; font-size:18px;}
.entry {border-bottom: 1px solid #626262;}
.box .box-bottom .box-spacer, .box .box-top .box-spacer, #body .white .box-body, #sidebar .box .box-body, #sidebar .box .box-bottom, #sidebar .box .box-bottom .box-corner {background:black;}
.box.white .box-corner {background:none;}
.box.white .box-body {background: rgb(0, 0, 0);}
.subbar {background-color: #272727 !important; color:white;}
.subbar a {color:#c4c4c4; text-shadow:none;}
.ebody, .entry.private .info,
.entry .info, .entry .likes,
.entry .comment, .entry .commentform,
.entry .expandcomment, .entry .clusterlink,
.pager.bottom, .textbox {direction:rtl;}
.entry .body {color: rgb(255,255,255);}
.entry .profile {float: right;padding-left:5px;}
.entry.private .info {background-position: right center;padding-right:20px;}
.entry .likes {background: url("http://friendfeed.com/static/images/smile.png?v=4b9a") no-repeat top right;padding-right: 21px;}
.entry .comment, .entry .commentform {background: url("http://friendfeed.com/static/images/comment-unknown.png?v=8733") no-repeat top right;padding-right:21px;}
.date {float:right;margin-left:2px;}
.popupmenu {border: 1px solid gray;background-color: #4e4e4e;}
.entry .file a, .sharebox .file a {direction:ltr;}
}
