css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bash.org.ru") {

body {width: 740px; margin: .2cm auto !important; background-color: silver !important; color: black !important; font-family: 'Lucida Grande', 'Tahoma', sans-serif !important;}

#page { margin: auto !important; width: auto !important; text-align: center !important; background-color: white !important; border: 2px black solid !important;}

.header { font-family: 'Times', 'Times New Roman', serif !important; font-size: 30px !important; font-weight: bold; text-align: left !important; background-color: black; color: white; padding: 5px; margin: 0px; height: 50px; margin-bottom: 0px; padding-bottom: 10px}
.menu { margin: 0 !important; background-color: #f5f5f5; color: #000; padding: 5px; font-family: Arial, sans-serif; text-align: left !important; }

div#quotes + div.menu { border-bottom: 0 !important; background-color: white; text-align: left !important;  margin-bottom: 35px !important; }
table { width: auto !important;}
a, a:visited { color: #0000C4 !important; font-size: 100% !important;}

div#quotes div.q { font-family: 'Courier New', monospace !important; font-size: 150% !important;}
div#quotes div.q div, div.stats { background: white !important; padding: 0 !important; border: none !important; }
div#quotes div.q div.vote { font-family: inherit !important; }
div.q div.vote a:first-child { font-weight: bold; }
div.q div.vote a:first-child:before { content: '#'; }
div.news { font-family: 'Arial', 'Tahoma', sans-serif !important; }
.b b { font-weight:  normal; }
small { font-family: 'Times New Roman', serif; }
div.header img, div#bar { display: none; }
div.vote {margin-bottom: 10px !important}
input {width: auto !important}
div.q {border: 2px silver dotted !important; margin-bottom: 25px !important; padding: 10px !important; background-color: white !important}

/* "Ð° Ð´Ð°Ð»ÑŒÑˆÐµ?" Ð² Ð±ÐµÐ·Ð´Ð½Ðµ*/
div.aby a {background-color: white !important; color: black !important; text-decoration: none !important; border: 1px solid black !important;}
div.aby a:hover {background-color: black !important; color: white !important; text-decoration: none !important;}

#cm_navi {background-color: white !important;}
#cm_navi td.navi a, #cm_navi td.navi a:hover {background-color: white !important; font-size: 150% !important;}
#cm_navi td {margin-top: 10px; margin-right: 0pt; margin-bottom: 10px; margin-left: 0pt; font-size: x-small; text-align: center; color: black !important;}
#cm_navi a:hover img { border: 2px solid blue !important; }
#cm_navi td.active {background-color: black !important; color: white !important;}
#cm_navi td.active img { border: 2px solid black !important; }

}
