@-moz-document domain("apps.facebook.com"), regexp("https?://www\\.facebook\\.com/(?!plugins/).*")
{
	._kso
	{
		background-color: #0B93F6;
		background-image: none;
		color: white;
		font-family: "Helvetica Neue", Arial;
		font-size: 12px;
		text-shadow: none;
	}
	._50kd ._kso, ._50kd ._kso a
	{
		color: black;
	}
	._50kd ._kso, ._50kd ._kso:before
	{
		background-color: #E5E5EA;
		background-image: none;
		color: black;
		font-family: "Helvetica Neue", Arial;
		font-size: 12px;
	}
	._kso a
	{
		color: white;
	}
	._kso
	{
		border: 1px solid #dfdfdf;
		border: none;
		border-bottom-color: none;
		border-radius: 10px;
		margin: 5px 5px 5px 0;
		min-height: 14px;
		padding: 4px 5px 3px 6px;
		position: relative;
		text-shadow: none;
		webkit-border-radius: 10px;
		webkit-box-shadow: none;
	}
	._50dw .profileLink:after
	{
		background-image: none;
	}
	._50kd ._kso:before
	{
		display: none;
		left: auto;
		right: 100%;
	}
	.fbChatConvItem .profileLink .profilePhoto
	{
		border-radius: 300px !important;
	}
	.fbChatConvItem .profileLink:before
	{
		background: transparent;
		border-radius: 300px !important;
		box-shadow: 0 1px 4px -1px #000, 0 2px 4px -2px #000, inset 0 0px 0px 1px rgba(255, 255, 255, 0.25), inset 0 4px 5px -1px rgba(255, 255, 255, 0.25);
		content: "";
		display: block;
		height: 32px;
		position: absolute;
		width: 32px;
	}
	._kso:before
	{
		background-color: none;
		background-image: none;
	}
	._kso:before, ._kso:after
	{
		background-image: none;
		background-position: -218px -433px;
		background-repeat: no-repeat;
		background-size: auto;
		height: 13px;
		top: 5px;
	}
	._kso:before
	{
		background-color: none;
		background-image: none;
	}
	.fbDockChatTabFlyout .fbNubFlyoutBody
	{
		background-color: white;
	}
	.fbChatConvItem:hover .metaInfoContainer
	{
		visibility: hidden;
	}
	._5w-6
	{
		background-color: white;
	}
	._50kd ._kso:after
	{
		background-image: none;
	}
	._kso:before, ._kso:after
	{
		background-color: white;
	}
}
