@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);



/* Hide options and any separator above: */
#menu_preferences,menuitem + menuseparator, #menu_editprefs
{
  display: none !important;
}


/* Disable right clicking on toolbar, navigation bar, and NO Context Menu on web pages: */
popup:not(#autoscroller)
{
  display:none !important;
}
