css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.reddit.com/") {
body {
    background: #fff;
    color: #333;
    font: 13px/17px "helvetica neue", helvetica, arial, sans-serif;
}
#sr-header-area {
    background-color: #f3f3f3;
    border-bottom: 0;
    border-bottom: 1px solid #B9CBDF;
    font-size: 12px;
    height: 25px;
    line-height: 25px;
    text-transform: lowercase;
}
#sr-header-area .width-clip {
    left: 10px
}
#sr-header-area .selected {
    margin-bottom: -1px
}
#sr-header-area .selected a,
.tabmenu li.selected a {
    background: #CEE2F9;
    border-bottom: 1px solid #CEE2F9;
    border-left: 1px solid #B9CBDF;
    border-right: 1px solid #B9CBDF;
    color: #535B65;
    display: inline-block;
    font-weight: normal;
}
.tabmenu li a {
    background: none;
    font-weight: normal;
    height: 25px;
    line-height: 25px;
    margin-bottom: 0;
}
a,
.thing .title,
.thing .title.loggedin,
.sr-bar a:hover,
.tabmenu li a:hover,
.thing .title.loggedin.click {
    color: #08c;
    text-decoration: none;
}
a:hover,
.thing .title.loggedin.click:hover,
.thing .title:hover {
    color: purple
}
.tabmenu li.selected a {
    background: #fff;
    border-bottom-color: #fff;
    border-top: 1px solid #B9CBDF;
    margin-bottom: -1px;
}
.sr-bar .separator {
    color: #777;
    display: none;
}
.sr-bar a,
.tabmenu li a {
    color: #777;
    height: 25px;
    line-height: 25px;
    padding-left: 8px;
    padding-right: 8px;
}
.thing .title:visited,
a:visited {
    color: #aaa
}
#header {
    background: #CEE2F9;
    border-bottom: 1px solid #B9CBDF;
}
#header-img.default-header {
    margin: 8px 0
}
#header-bottom-left {
    font-size: 12px
}
#header-bottom-right {
    background: none;
    border: none;
    border-radius: 0;
    border-right: 0;
    font-size: 12px;
    height: 25px;
    line-height: 25px;
    padding: 0 5px;
    top: 41px;
}
#header-bottom-right a {
  color:#67717C;
  }

.user a:hover {
      text-decoration: underline; }
.pref-lang {
    font-weight: normal
}
#sr-more-link {
    color: #aaa;
    font-weight: normal;
}
ul.flat-vert li {
    margin: 5px 0
}
.debuginfo {
    display: none
}
.footer {
    display: block;
    margin: 0;
    padding: 10px 0;
}
.footer .col {
    border: none;
    height: auto;
    padding: 1% 2%;
    width: 21%;
}
.footer-parent {
    background: #f3f3f3;
    border-top: 1px solid #ddd;
    margin-top: 0px;
    padding-bottom: 20px;
    padding-top: 0;
}
.sitetable {
    margin-bottom: 20px;
    overflow: hidden;
}
.nextprev {
    border-top: 1px solid #ddd;
    color: #777;
    font-size: 14px;
    margin: 0;
    padding: 10px;
}
.content .spacer {
    margin-bottom: 0
}
.nextprev a,
.nextprev a:hover {
    background: none;
    border: none;
}
.side {
    border-left: 1px solid #ddd;
    font-size: 12px;
    margin-left: 20px;
    margin-top: 20px;
    padding-left: 10px;
}
.link {
    margin-bottom: 20px;
    margin-top: 20px;
}
.tagline {
    color: #bbb
}
.link .title {
    font-size: 16px;
    line-height: 22px;
}
.entry .buttons li a {
    font-size: 12px;
    font-weight: normal;
}
.thing .title:visited {
    color: #aaa !important
}
.morelink .nub {
    display: none
}
.morelink,
.morelink:hover {
    background: #08c;
    border: none;
    color: #fff;
    font-size: 14px;
    letter-spacing: normal;
}
.morelink a {
    color: #fff;
    font-weight: normal;
}
.side #search {
    background: #08c;
    margin-bottom: 10px;
    padding: 5px;
}
.side #search input[type="text"] {
    border-color: #ccc;
    box-shadow: inset 0 1px 1px rgba(0,0,0,.1);
    font-size: 14px;
    padding: 8px 2%;
    width: 95%;
}
.sidecontentbox .title h1 {
    text-transform: lowercase
}
.sidecontentbox .content {
    border: none
}
.gadget .reddit-link {
    margin: 10px 0
}
.user { color: rgba(0,0,0,.4) }
}

.searchpane {
margin: 5px 331px 5px 0px;
background-color:#eee;
}
