css:
@-moz-document url-prefix("http://www.youtube.com"), url-prefix("https://www.youtube.com") {

#player-api,#watch-video7-container.watch-wide, #watch7-video {
width: 100% !important;
height:auto !important;

}

#player,#watch7-video-container {
padding-left: 0 !important;
}

#player embed{
	height:90% !important;
}

#player,#watch7-player {
width: 100% !important;

margin: auto !important;
}
#page.watch{
padding-top:0px !important;
}
#page.watch #alerts{
display:none !important;
}

#page.watch #guide-container{
padding:14px 0 0 5px !important;
background-color:#f1f1f1 !important;
left:900px !important;
}

#page.watch #guide{
position:absolute !important;
top:0 !important;
z-index:5 !important; 
  
}

#page.watch #guide #guide-container{
height:36px !important;
}

#page.watch #guide #guide-container #guide-main,#page.watch #guide #guide-container #watch-context-container{
background-color:#f1f1f1 !important;
padding:0 5px 0 5px !important;
}

#page.watch #guide #guide-container #watch-context-container{
top: -38px !important;
}

.guide-collapsed #page.watch #guide #guide-container #watch-context-container{
left:178px !important;
}

.guide-expanded #page.watch #guide #guide-container #watch-context-container{
padding-bottom:5px !important;
}

#page.watch{
position:static !important;
}


#watch7-playlist-data,.watch7-playlist-bar{
padding-left:0 !important;
width:100% !important;
}


.watch7-playlist{
padding-top: 0px !important;
}
.watch-playlist #player{
	padding-top:0px; 
}
  
#watch7-video-container{
padding-top:0px !important;
}
.watch-playlist #watch7-video-container{
padding-top:50px !important;
}

#watch7-playlist-bar-toggle-button{
display:inline !important;
}

#watch7-sidebar, .watch-playlist.watch-autohide #watch7-sidebar{
margin-top:0 !important;
}

#watch7-sidebar{
padding-top:15px !important;
}
}
