css:
@-moz-document url(http://www.jam-graffiti.com/non-pic-css/download/css-party.html) {
* {
line-height: 1.618;
}
body {
margin: 0;
padding: 0 12px;
background-color: #FFF;
color: #000;
}
h6, h5 {
font-weight: normal;
}
h6 {
color: rgb(180, 45, 135);
text-shadow: 1px 0px 0px #999;
letter-spacing: 1px;
font-size: 112.5%;
margin: 16px 0;
}
h5 {
font-size: 125%;
color: rgb(220, 55, 165);
text-shadow: 1px 0px 0px #777;
letter-spacing: 1px;
margin: 16px 0 16px -3px;
}
h4 {
font-size: 137.5%;
color: rgb(240, 60, 180);
text-shadow: 1px 1px 1px #555;
margin: 16px 0 16px -6px;
letter-spacing: 1px;
}
h3 {
font-size: 162.5%;
margin: 16px 0 16px -9px;
color: rgb(240, 60, 180);
text-shadow: 2px 1px 2px #333;
letter-spacing: 2px;
}
h2 {
font-size: 200%;
margin: 16px 0 16px -9px;
color: rgb(255, 64, 192);
text-shadow: 3px 1px 2px #111;
letter-spacing: 3px;
}
h1 {
border-radius: 24px;
padding: 12px;
color: rgb(255, 64, 192);
text-shadow: 3px 1px 2px #111;
letter-spacing: 3px;
background: #E8E8E8;
font-size: 250%;
margin: 16px 0;
}
hr {
display: none;
}
p:before {
content: "§";
color: #bbb;
background: #ddd;
border-radius: 4px;
margin-right: .3em;
padding: 2px;
font-weight: bold;
font-family: monospace;
vertical-align: top;
position: relative;
top: 2px;
}
dt {
border: 2px solid #999;
display: inline-block;
padding: 0 .5em;
border-radius: 1em;
background-color: #CCC;
min-width: 6em;
}
dd {
border-left: 8px double #999;
padding-left: .3em;
padding-bottom: .3em;
margin-bottom: .3em;
margin-left: 1.5em;
border-radius: 0 0 0 .9em;
}
em {
text-shadow: 1px 0px 0px #BBB;
}
a:link {
color: #00F;
text-shadow: 0 1px 1px #9CF;
}
a:visited {
color: #B37;
}
div.section {
background-color: #F4F4F4;
border: none;
border-radius: 24px;
margin: 24px 0;
padding: 1px 12px;
}
div.section div.section {
background-color: #EBEBEB;
width: 48%;
float: left;
border: none;
padding: 1px 0;
margin: 24px 1%;
}
div.section:after {
content: "";
display: block;
clear: left;
}
}
