css:
@-moz-document domain("userstyles.org") {
@font-face {
    font-family: "wf_SegoeUILight";
    font-style: normal;
    font-weight: normal;
    src: url("http://i.s-microsoft.com/fonts/Segoe-UI/East-European/Light/latest.eot?#iefix") format("embedded-opentype"), url("http://i.s-microsoft.com/fonts/Segoe-UI/East-European/Light/latest.woff") format("woff"), url("http://i.s-microsoft.com/fonts/Segoe-UI/East-European/Light/latest.ttf") format("truetype"), url("http://i.s-microsoft.com/fonts/Segoe-UI/East-European/Light/latest.svg#web") format("svg");
}
@font-face {
    font-family: "MSHPIconsRegular";
    font-style: normal;
    font-weight: normal;
    src: url("http://i.s-microsoft.com/fonts/homepage/icons/mshpicons-regular-webfont.eot?#iefix") format("embedded-opentype"), url("http://i.s-microsoft.com/fonts/homepage/icons/mshpicons-regular-webfont.woff") format("woff"), url("http://i.s-microsoft.com/fonts/homepage/icons/mshpicons-regular-webfont.ttf") format("truetype"), url("http://i.s-microsoft.com/fonts/homepage/icons/mshpicons-regular-webfont.svg#web") format("svg");
}
@font-face {
    font-family: "wf_SegoeUI";
    font-style: normal;
    font-weight: normal;
    src: url("http://i.s-microsoft.com/fonts/Segoe-UI/East-European/Normal/latest.eot?#iefix") format("embedded-opentype"), url("http://i.s-microsoft.com/fonts/Segoe-UI/East-European/Normal/latest.woff") format("woff"), url("http://i.s-microsoft.com/fonts/Segoe-UI/East-European/Normal/latest.ttf") format("truetype"), url("http://i.s-microsoft.com/fonts/Segoe-UI/East-European/Normal/latest.svg#web") format("svg");
}


body
{
  width: 960px;
  margin: auto;
  font-size: 14px;
  line-height: 18px;
  color: #666;
  font-family: "wf_SegoeUI",​"Tahoma",​"...a",​"Arial",​"sans-serif";
  font-size: 100%;
  font-weight: normal;
  line-height: 1.5;
  line-height: 18px;
}

.ad
{
  display: none;
}

footer
{
  text-align: right;
  padding-top: 40px;
}

footer a
{
  font-size: 12px;
}

#main-header
{
  margin: 20px 0 0 0;
  padding: 0;
  background-color: inherit;
  display: block;
  height: 100px;
}

#main-header h1 a, #main-header h1 a:visited
{
  text-decoration: none;
  color: #1a1a1a;
}


#main-header h1 
{
  color: #1A1A1A;
  font-size: 38px;
  font-family: 	"wf_SegoeUILight",​"wf_S...a",​"Arial",​"sans-serif";
  font-weight: 300;
  font-style: normal;
  margin-bottom: 19px;
  height: 47px;
  line-height: 47px;
}

#left-sidebar nav, #left-sidebar form
{
  background-color: inherit;
  margin: 0;
  padding: 0;
}

#left-sidebar nav ul li
{
  display: block;
  width: 170px;
  height: 30px;
  background-color: #0044CC;
  color: #fff;
  padding-top: 80px;
  padding-left: 10px;
  position: relative;
}

#left-sidebar nav ul li a
{
  display: block;
  width: 170px;
  height: 30px;
  background-color: #EB3C00;
  color: #fff;
  padding-top: 80px;
  padding-left: 10px;
  text-decoration: none;
  vertical-align:	baseline;
  position: absolute;
  top: 0;
  left: 0;
  font-size: 16px;
}

#left-sidebar nav ul li a:hover
{
  background-color: #0044CC;
}

#left-sidebar form
{
  margin: 30px 0;
  width: 180px;
}

#search-terms
{
  display: block;
  float: left;
  width: 145px;
}

#search-submit
{
  display: block;
  float: right;
}

#show-search-options
{
  display: block;
  text-align: right;
}

#related
{
  background-color: inherit;
  border-radius: 0;
  margin: 0;
  padding: 0;
  text-align: left;
  
}

#related h2
{
   display: block;
  color: #1A1A1A;
  font-size: 26px;
  font-family: 	"wf_SegoeUILight",​"wf_S...a",​"Arial",​"sans-serif";
  font-weight: 300;
  font-style: normal;
  margin-bottom: 14.3px;
  height: 32.5px;
  line-height: 32.5px;
}

#related ul li
{
  line-height: 1.0;
}

#related ul li a
{
  font-size: 12px;
}

p
{
  margin: 0;
  margin-bottom: 10px;
  font-size: 14px;
}

a, a:visited
{
  color: #1570A6;
  text-decoration: none;
}

a:hover, a:visited:hover
{
  color: #083047;
  text-decoration: underline;
}

h2
{
  display: block;
  color: #1A1A1A;
  font-size: 26px;
  font-family: 	"wf_SegoeUILight",​"wf_S...a",​"Arial",​"sans-serif";
  font-weight: 300;
  font-style: normal;
  margin-bottom: 14.3px;
  height: 32.5px;
  line-height: 32.5px;
}

h3
{
  display: block;
  color: #1A1A1A;
  font-size: 26px;
  font-family: 	"wf_SegoeUILight",​"wf_S...a",​"Arial",​"sans-serif";
  font-weight: 300;
  font-style: normal;
  margin-bottom: 12px;
  height: 20px;
  line-height: 20px;
}

ul
{
  margin: 0;
  padding: 0;
}

ul li, ol li
{
  list-style-type: none;
  font-size: 14px;
}

dt, dd, th
{
  font-size: 14px;
}

table
{
  border-collapse: collapse;
}

th
{
  border-left:1px solid #ddd;
  border-right:1px solid #ddd;
  padding: 7px;
  font-family: 	"wf_SegoeUILight",​"wf_S...a",​"Arial",​"sans-serif";
  font-weight: 300;
}

th:first-child, td:first-child
{
  border-left: 0;
}

th:last-child, td:last-child
{
  border-right: 0;
}

td
{
  font-size: 11px;
  border:1px solid #ddd;
  padding: 7px;
}


#main-article {
   margin-left: 200px;
}

#front-page-best
{
  width: 50%;
}

#search
{
  width: 110px;
}
}

@-moz-document url("http://userstyles.org/help") {
p
{
  margin-top: 20px;
}
}

@-moz-document url-prefix("http://userstyles.org/users") {
#left-sidebar nav ul li:nth-child(2) a
{
  background-color: #0044cc;
}

dt, dd
{
  display: block; 
}

dt
{
  width: 80px;
  float: left;
}
}

@-moz-document url-prefix("http://userstyles.org/styles") {
td, th
{
  border: 0;
  padding: 2px;
}

#more-screenshots span
{
  display: none;
}

#more-screenshots ul
{
  padding: 0;
  margin: 12px 0 0 0;
}

#screenshots
{
  border-radius: 0;
}

#long-description, #additional-info
{
  font-size: 14px;
}

#stylish-installed-style-not-installed-chrome, #stylish-installed-style-installed-chrome, #show-button
{
  background-color: #1570A6;
  color: #fff;
  background-image: none;
  border-radius: 0;
  border: 0;
}

#show-button
{
  padding: 7px;
}

#more-info, #discussions-area
{
  padding: 50px 0 0 0;
}

#more-info > h2, #discussions-area > h2
{
  margin-left: 0;
  font-style: normal;
}

#license
{
  margin-top: 20px;
}

h3
{
  margin-top: 50px;
}

#discussions
{
  padding-left: 26px;
}

#post-install-ad
{
  display: none;
}
}
