css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fansubber.com.br") {
* {
    border: none !important;
    font-size: 12px !important;
    font-family: Terminus !important;
    text-decoration: none !important;
    font-weight: normal !important;
}
.block-head-title{
    background-image: none !important;
    background-color: #3F4345 !important;
    border: none !important;
    border-radius: 0px !important;
}
#body-bg, html, body  {
    background-image:none !important;
    background-color: #141414
}

.header {
    box-shadow: none !important;
    background-image: none !important;
    background-color: #3F4345 !important;
    color: #EDEDED !important;
    }
td.lista {
	   box-shadow: none !important;
	   background-color: #2D2F30 !important;
	   background-image: none !important;
	   color: #ECECEC !important;
	   border-radius: 1px !important;
	   border-color: #fff !important
}
.b-content {
    background-image:none !important;
    background-color: #232526 !important;
    border: none !important;
    border-radius: 0px !important;
    }
tr td.yellow,  font[color="orange"] {
    color: #FDFE71 !important
}
tr td.red {
    color: #FE7186 !important
}
tr td.green {
    color: #81FE71 !important
}
a[target="_blank"], a[class="menu"], .lista a font, .lista b .forum, a[class="user"], td[class="blocklist"], a[class="lasttor"], a[class="toptor"], .lista a b, a[class="mainuser"], #header tr td a, #mcol tr .lista a, #mcol .b-content a {
color: #6FF7B7 !important;
}
#mcol .lista span, font[color="red"]{
color: #FE7186!important;
}
#mcol .header .pager {
border: none !important;
border-radius: 0px !important;
color: #72FF8D !important;
-moz-transition: all 0.3s ease;
-webkit-transition: all 0.3s ease;
-o-transition: all 0.3s ease;
box-shadow: 0px 0px 3px rgba(0,0,0,.8) !important;
}
#mcol .header .pager:hover {
border: none !important;
border-radius: 0px !important;
color: #aeffbd !important;
-moz-transition: all 0.3s ease;
-webkit-transition: all 0.3s ease;
-o-transition: all 0.3s ease;
box-shadow: 0px 0px 10px rgba(0,0,0,.8) !important;
}
select[onchange="location=document.jump.style.options[document.jump.style.selectedIndex].value"], select[onchange="location=document.jump1.style.options[document.jump1.style.selectedIndex].value"] {
border: none !important;
border-radius: 0px !important;
background-color: #000 !important;
-webkit-appearance: none;
-moz-appearance: none;
-o-appearance: none;
color: #b3b3b3 !important
}
select{
border: none !important;
border-radius: 0px !important;
background-color: #EDEDED !important;
-webkit-appearance: none;
-moz-appearance: none;
-o-appearance: none;
color: #b3b3b3 !important
}
div[id="menu-under"]{
background-color:  #333638 !important;
background-image: none !important;
height: 4px !important;
margin-top: -5px !important;
}
div[id="menu-bg"]{
display:none
}
div[id="menu-under"] {
margin-top: 18px !important
}
.logo1{
position: absolute !important;
top: 23px !important;
background-image: url('http://i.imgur.com/E6TAPD3.png') !important
} 
#header div .b-content{
position: absolute !important;
padding: 2px !important;
width: 100% !important;
left:0px !important;
top:-66px !important;
box-shadow: 0px 0px 10px rgba(0,0,0,.8) !important;
-moz-box-sizing: border-box !important;
-moz-transition: top .3s ease-in-out 3s, opacity .3s ease-in-out 3s;
-webkit-box-sizing: border-box !important;
-webkit-transition: top .3s ease-in-out 3s, opacity .3s ease-in-out 3s;
-o-box-sizing: border-box !important;
-o-transition: top .3s ease-in-out 3s, opacity .3s ease-in-out 3s;
}
#header div .b-content:hover{
top:-3px !important;
box-shadow: 0px 0px 10px rgba(0,0,0,1) !important;
-moz-transition: top .3s ease-in-out, opacity .3s ease-in-out;
-webkit-transition: top .3s ease-in-out 3s, opacity .3s ease-in-out 3s;
-o-transition: top .3s ease-in-out 3s, opacity .3s ease-in-out 3s;
}


td[class="post"]:hover{
	   background-color: #4c4c4c !important;
	   box-shadow: none !important;
	   background-image: none !important;
	   }
/*alinhamento*/

#lcol {
position: absolute !important;
left: 1px !important;
}
#mcol{
position: absolute !important;
left: 185px !important;
right:185px !important;

}
#rcol{
    position: absolute !important;
    right: 0px !important;
   
}
#fcol, tbody .footbg{
    display:none!important
}
#header .block-head-title{
    background-image: url('http://i.imgur.com/p6ay0df.png') !important;
     border-bottom:2px solid rgba(255,255,255,.2)!important;
    position: absolute !important;
    right: 0px !important;
    height: 26px !important;
    width: 100% !important
    
}
#mcol .block {
margin-bottom: -8px !important
}
#mcol .block-head{
margin-bottom: -5px !important
}
/*forum fixes*/
td[class="post"], td[class="block"] {
	   box-shadow: none !important;
	   background-color: #1f2021 !important;
	   background-image: none !important;
	   border-left:2px #FE7186 solid !important;
}
td[class="post"]:hover{
       background-color: #1f2021 !important;
}
table[class="quote"]{
    background-color: #333637 !important;
    border-bottom:1px #FE7186 solid !important;
}
.post img{
box-shadow: 0px 0px 20px rgba(0,0,0,.7) !important;
}
.post p{
color: #808080 !important;
}
.quote td {
color: #fff !important
}
#mcol .b-content {
padding: 1px !important
}
/*fix do paginador, portado do outro script*/
form[name="change_page"]{
position: fixed !important;
bottom: 0px !important;
left: 0px !important;
background-color: #232526 !important;
padding: 3px !important;
box-shadow: 0px 0px 10px rgba(0,0,0,.8) !important;
height: 30px !important
}

span[class="pagercurrent"]{
background-color: #b2b2b2 !important;
color: #000 !important
}
span[class="pager"]{
background-color: #787878 !important;
border-color: #787878 !important;
max-width: 100% !important;
}

select[onchange="location=document.change_page.pages.options[document.change_page.pages.selectedIndex].value"]{
   border-radius: 0px !important;
   border:none !important;
   color: #fff !important;
   background-color: #000 !important;
   -webkit-appearance: none;
   -moz-appearance: none;
}
/*botões de resposta*/
input[class="btn"]{
    background-image:none !important;
    background-color: #EDEDED !important;
    border: none !important;
    border-radius: 0px !important;
    -webkit-appearance: none;
   -moz-appearance: none;
}
input[class="btn"]:hover{
    color: #000 !important;
    background-color: #d0d0d0 !important;
    }
}
