css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vampirefreaks.com") {


.maintop td[align] img{
display: none !important;
}

.mainheading td[width], .side_td[align=right]{
display: none !important;
}

.maintop td[align]{
height: 45px;
background-color: transparent!important;
background-image: url('http://i26.tinypic.com/oiuki8.jpg');
background-attachment: scroll;
background-repeat: no-repeat;
background-position: center center;
}

body, html{
background-color: #ffffff !important;
color: #040404 !important;
font: 10px verdana !important;
}

table{ 
font: 10px verdana !important; 
}

a:link, a:hover, a:visited, a:active {
font: 11px verdana !important;
font-weight: normal !important;
color: #0fad22!important;
background-color: transparent!important;
}

input, select, textarea{
font: 11px verdana!important;
color: #0fad22!important;
background-color: #eeeeee !important;
border:1px solid #d6d6d6 !important; 
}

input[type="submit"]{
color: #040404!important;
}

.leftnav, .topbar, .comments, .mainupdates{
border-width:1px !important;
border-style: solid !important;
border-color: #d6d6d6 !important;
background: #eeeeee !important;
}

.darkbox, .darkbox td{
border: 1px solid #d6d6d6 !important;
background-color: #eeeeee !important; 
}

.darkbox a{
color: #979897!important;
}

.tdbordered{
border: none!important;
}

.notify{
color: #0fad22 !important;
}

.leftnav a, .mainheading a {
display: block !important;
font-weight: normal !important;
color: #0fad22 !important;
}

.leftnav a:hover, .mainheading a:hover {
background: #e7e6e6 !important;
color: #077d15 !important;
font-weight: normal !important;
}

.menu, a.menu, a.menu:visited{
background-color: #e7e6e6 !important;
color: #077d15 !important;
font: 10px Verdana !important;
font-weight: normal !important;
text-decoration: none !important;
}

.menu2, a.menu2, a.menu2:visited{ 
background-color: #eeeeee !important;
color: #0fad22 !important;
font: 10px Verdana !important;
font-weight: normal !important;
text-decoration: none !important;
border: 0px !important;
}

.heading{
background-image: url('http://i30.tinypic.com/2hwfsd4.jpg') !important;
background-repeat: repeat-x !important;
background-color: #eeeeee !important;
}

.side_td[align="right"]{
display: none;
}

tr[bgcolor="#000000"]{
background-color: transparent!important;
}

.darkrow img, .lightrow img{
display: none;
}

.darkrow {
  background-color: #e7e6e6 !important;
}

.lightrow {
  background-color: #eeeeee !important;
}

.forum_comment td{
background-image: url('http://i30.tinypic.com/2hwfsd4.jpg') !important;
background-repeat: repeat-x !important;
background-color: #eeeeee !important;
}

hr {
  display: none !important; 
}

.icon {
border: 1px solid #0fad22 !important;
}

.profileurl, .earnpoints {
  color: #069517 !important;
}

.profileurl {
  color: #979897;
}

.titletext {
  color: #979897 !important;
  font-weight: normal !important;
}

.latestupdates {
 border: 0px !important;
} 

.tdnoborders {
  border: 0px !important; }
}
