css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {

.post_form_wrapper.detached {left: 35% !important;}
#post_controls {position: fixed !important; top: 20% !important; right: 25%;}
#post_controls button.close {margin-left:10px;}
.tag_editor {
    background: #FFF;
    z-index: 1000 !important;
    position: fixed !important;
    top: 29% !important;
    right: 20%;
    width: 500px !important;
    opacity:0.4;
    -webkit-transition: all 0.2s ease;
    -moz-transition: all 0.2s ease;
    -o-transition: all 0.2s ease;
    transition: all  0.2s ease;
    }
#create_post{box-shadow:3px 3px 5px rgba(0, 0, 0, 0.35);}
#tinymce {padding-bottom: 0 !important;}
.inner_content {
    z-index: 1000 !important;
    box-shadow:3px 3px 5px rgba(0, 0, 0, 0.35);
    position: fixed !important;
    top: 35% !important;
    right: 20%;
    width: 500px;
    opacity:0.4;
    -webkit-transition: all 0.2s ease;
    -moz-transition: all 0.2s ease;
    -o-transition: all 0.2s ease;
    transition: all  0.2s ease;
    }
.tag_editor:hover , .inner_content:hover {opacity:1;}
#tinymce {max-height: 400px !important; overflow-y: auto !important; }
#post_two_ifr {max-height: 415px !important;}

}
