css:
@-moz-document domain("e.mail.ru") {
#slot-container_2, #slot-container_2_separator, #AdvertisingTopLine, #AdLeftInformer, #portal__banner, #b-slot_left_banner, #b-slot_left_direct, .dummy-dline, .leftcol__banner__direct, .leftcol__sep-wrapper, .b-datalist__head, .b-letter__head__rb, .rb_banner, .message-sent__text, .js-banner, .b-datalist__head__item *, #LEGO #leftcol-banners, #LEGO .direct_top, .slider { display: none !important; visibility: hidden !important; }
.leftcol, .announcements, .b-nav, .b-datalist { border-radius: 5px !important; }
.toolbar_top, .toolbar_compose-top, .toolbar_corner-top, .toolbar_readmessage-top, .b-letter__head, .b-datalist__item:first-child { border-top-left-radius: 5px !important; border-top-right-radius: 5px !important; }
.toolbar_bottom, .toolbar_compose-bottom, .toolbar_readmessage-bottom, .mr_read mr_toolbar__y, .b-letter__foot, .b-datalist__item:last-child /*.b-datalist__item.b-datalist__item_last*/ { border-bottom-left-radius: 5px !important; border-bottom-right-radius: 5px !important; }
#ReadMsgBottom { border-radius: 8px !important; }
/*.b-datalist__item.b-datalist__item_last > .js-item-cbx.b-datalist__item__cbx { border-bottom-left-radius: 40px; }*/
.b-datalist__item:first-child > .js-item-cbx.b-datalist__item__cbx { border-top-left-radius: 5px; }
.b-datalist__item:last-child > .js-item-cbx.b-datalist__item__cbx { border-bottom-left-radius: 5px; }
}
