css:
/* name: sourceforge.jp magazine ja jp 2013/02 */
@-moz-document url-prefix("http://sourceforge.jp/magazine/"){
/*== sourceforge.jp magazine ==*/
/*- unwanted contents -*/
div[class^="ads-"],
div[id^="ad-"],
div[id^="ad_"],
div[id*="-ad-"],
#adbar,
#gpt-sf_mag_rec
 {display: none !important;}

/*- fit to window -*/
body {min-width: inherit !important; padding: 0 !important;}
#magazine-topnav {min-width: inherit !important; margin: 0 !important;}
#magazine-topnav li a,
.topnav .firstwave li a
 {line-height: inherit !important;}
.magazine {
 font-size: inherit !important; line-height: inherit !important;
 margin: 0 !important;
 }
#recent_tweet {width: 95%; margin: 0 auto !important;}
}
