css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp("http://forums.shoryuken.com/(?!dashboard/).*") {

/* site background */
html, body {
background-color: #000000 !important;
background-repeat: no-repeat-y !important;
}

/* white logo */
.Logo {
background: url(http://srktt.com/skin/srk_logo.png) !important;
text-indent: -9999px !important;
}

/* background colors */
div * { background-color: transparent !important; }
.Announcement { background-color: #1a1a1a !important; }
.ButtonBar, .cleditorToolbar, .cleditorMain { background-color: #ffffff !important; }
.Breadcrumbs, .UserQuote, .UserSpoiler, .HasNew, pre { background-color: #333333 !important; }
/* .Menu .Row { background: url(http://srktt.com/skin/black_twotone.png) !important; } */
.Row, .TextBox, .InputBox, .PopList, .Dropdown, .Flyout, .tipsy { background-color: #2a2a2a !important; }

/* smaller fonts */
body, div, h3, .Item, .Title, .Number, .TextBox, .InputBox, .NewCommentCount, .Pager .Highlight {
font-family: "arial", "helvetica", "clean", sans-serif !important;
font-size: 10pt !important;
color: #ffffff !important;
}

/* link colors */
a:link, a:hover, .MenuItems a:link { color: #b5d1d6 !important; }
a:visited, .MenuItems a:visited, a.Button, a.NavButton { color: #699cb7 !important; }

/* misc fixes */
.Breadcrumbs span a:after, .Breadcrumbs span a:hover:after { border-left-color: #333333 !important; }
.PostEdited { padding: 0px 10px !important; }
.Promoted { box-shadow: none !important; }
.Signature, .PostEdited { color: #cfd0be !important; }
.TextBox, .InputBox { border-color: #eeeecc !important; }
.UserSignature { border-top: 1px solid rgba(255, 255, 255, 0.102) !important; }
}
