css:
html{overflow-x:hidden;}

.fbx #globalContainer{margin-top:47px;}

.sidebarMode #fbProfileCover {margin-top:50px;}
.fbTimelineScrubber {top:100px;}

.profilePic{
  -webkit-border-radius:50px;
  -moz-border-radius:50px;
  
  -webkit-transition:all .7s ease;
  -moz-transition:all .7s ease;
}
.profilePic:hover{
  -webkit-border-radius:0px;
  -moz-border-radius:0px;
}


#pagelet_bluebar{
position:fixed;
top:0;
height:46px;
width:100%;


z-index:9000;
}

#blueBarHolder #blueBar {
height:47px;
}

.fbx #pageHead, #blueBar #pageHead{
width:100%;
margin:0 20px;
padding-top:10px;

}

#navSearch .uiTypeahead {
width: 465px;
height: 18px;
background: #2A4675;
border: 0;
border-radius: 3px;
padding:5px;
margin-left: 10px;
box-shadow: inset 2px 2px 2px rgba(0, 0, 0, .15);

transition: all .7s;
-webkit-transition: all .7s;
}

#navSearch:hover .uiTypeahead {background:#fff;}

.uiTypeahead input{color:white;} 
.uiTypeahead input:hover{color:#6a83b5;}

#navSearch .uiTypeahead *{background:none; border:none;}


.fbTimelineStickyHeader {
left:0;
top:48px;
}

.fbTimelineStickyHeaderHidden{display:inherit!important;}
.tinyViewport .fixed_elem {position:fixed!important;}

.fbTimelineStickyHeader .name .profileThumb .img{
position:absolute;
width:48px; 
height:48px;
margin-top:-8px;
margin-left:-19px;
}

.stickyHeaderWrap .uiButtonGroup {margin-left:40px; background:#333;}
.uiButtonText, .uiButton input{color:#aaa;}

.fbTimelineStickyHeader .back{
background:#1d1e23;
border: 1px solid #1d1e23;
width:3000px;

-webkit-box-shadow: 0px 0px 15px 0 rgba(0, 0, 0, 0.9), inset 0px -40px 10px 0 rgba(100, 100, 100, 0.1);
}

.stickyHeaderWrap .actions {
opacity:0.3;
float:left;
transition: all .7s;
-webkit-transition: all .7s;
}

.stickyHeaderWrap:hover .actions {opacity:1; color:#fff;}
.stickyHeaderWrap .actions > div > form ul > li:hover { background:#2b2c33;}
.stickyHeaderWrap .actions ul .uiIconText:hover{ background:none;}

.stickyHeaderWrap div.actions ul li {
border-color: #2f3038;
border-style: solid;

transition: all .7s;
-webkit-transition: all .7s;
}

.stickyHeaderWrap div.actions ul li a{color:#818289;}

.uiButtonGroup span .uiButtonOverlay, .uiButtonGroup span .uiButtonOverlay:hover{
background-position: right -294px;
}



.fbChatSidebar{z-index:299; top:47px;}

.fbChatSidebar{right:-160px;}
.fbChatSidebar .status.icon.img {margin-left:-195px;}
.fbChatSidebar .pic_container{margin-left:8px;}
.fbChatSidebar{
  -webkit-transition:all .7s ease;
  -moz-transition:all .7s ease;
}
  

.fbChatSidebar:hover{right:0px;}
.fbChatSidebar:hover .status.icon.img {margin-left:-0px; margin-top:0px;}








#pagelet_side_ads, .ego_column {display:none;}
