css:
@-moz-document url-prefix(http://sweclockers.com/forum), url-prefix(http://www.sweclockers.com/forum)
{
  body
  {
    background-color: #FFF !important;
  }

  #header
  {
    height: 20px !important;
  }

  #fluidwrapper
  {
    float: none !important;
  }

  #fluidarea
  {
    width: auto !important;
    min-height: 0 !important;
    margin: 0 10px !important;
    padding: 0 !important;
  }

  table.vbforum
  {
    width: 100%;
  }

  table.forumnews, #fixedarea, #footer
  {
    display: none !important;
  }

  #login, #logout
  {
     top: 96px !important;
     color: #FFF !important;
     z-index: 1;
  }

  a#settings, a#pm
  {
    color: #FFF !important;
  }

  tbody tr:first-child
  {
    display: none !important;
  }
}
